import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

// Optimized service card with continuous shimmer effect
const ServiceCard = ({ children, onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.div
      className="relative h-full w-full rounded-2xl overflow-hidden"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      onClick={onClick}
      whileHover={{ 
        y: -6,
        transition: { duration: 0.5, ease: "easeOut" }
      }}
    >
      {children}

      {/* Shimmer effect only visible on hover - continuous animation */}
      {isHovered && (
        <motion.div 
          className="absolute inset-0 pointer-events-none"
          initial={{ opacity: 0 }}
          animate={{ 
            opacity: 1,
            backgroundPosition: ["200% 0%", "-200% 0%"]
          }}
          style={{
            background: "linear-gradient(to right, transparent, rgba(255, 255, 255, 0.1), transparent)",
            backgroundSize: "400% 100%",
          }}
          transition={{
            backgroundPosition: {
              duration: 3.5,
              repeat: Infinity,
              ease: "linear",
              repeatType: "loop"
            }
          }}
        />
      )}
    </motion.div>
  );
};

const Services = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [clickedIndex, setClickedIndex] = useState(null);

  // Enhanced staggered entrance animation
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.15,
        delayChildren: 0.2,
      } 
    },
  };

  // Entrance animation for title and description
  const headerVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.7,
        ease: [0.22, 1, 0.36, 1]
      } 
    }
  };

  // More dynamic item animation
  const itemVariants = {
    hidden: { opacity: 0, y: 40, scale: 0.9 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { 
        duration: 0.7,
        type: "spring",
        stiffness: 80,
        damping: 20
      } 
    },
    tap: {
      scale: 0.97,
      transition: { duration: 0.1 }
    }
  };

  const handleServiceClick = (index) => {
    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  const services = currentLanguage.services.items;

  return (
    <motion.section
      id="services"
      className="min-h-screen py-24 px-8 flex flex-col justify-center relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        {/* Entrance animation for section title */}
        <motion.div className="overflow-hidden mb-2">
          <motion.h2
            className={`text-3xl font-bold text-center
              ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.title}
          </motion.h2>
        </motion.div>

        {/* Entrance animation for description */}
        <motion.div className="overflow-hidden mb-12">
          <motion.p
            className={`text-base text-center mb-2 max-w-3xl mx-auto
              ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.description}
          </motion.p>
        </motion.div>

        {/* Service Cards with staggered animation */}
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
          variants={containerVariants}
        >
          {services.map((service, index) => (
            <motion.div
              key={index}
              custom={index}
              variants={itemVariants}
              whileTap="tap"
              className={`overflow-hidden rounded-2xl h-full transform-gpu
                ${theme === "light" 
                  ? "bg-gradient-to-br from-white to-rose-50/50 shadow-[0_8px_20px_rgba(0,0,0,0.06)]" 
                  : "bg-gradient-to-br from-gray-800/80 to-slate-900/80 shadow-[0_8px_20px_rgba(0,0,0,0.12)]"
                } backdrop-blur-sm border border-opacity-20
                ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
            >
              <ServiceCard onClick={() => handleServiceClick(index)}>
                <div className="p-6 relative z-10">
                  <div className="relative mb-4">
                    <div className={`absolute -left-3 top-0 h-full w-1 rounded-full 
                      ${theme === "light" 
                        ? "bg-gradient-to-b from-rose-500 to-pink-600" 
                        : "bg-gradient-to-b from-indigo-500 to-violet-600"
                      }`} 
                    />
                    <h3
                      className={`text-xl font-semibold ml-1
                        ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}
                    >
                      {service.name}
                    </h3>
                  </div>
                  <p
                    className={`text-sm leading-relaxed
                      ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}
                  >
                    {service.description}
                  </p>
                </div>
              </ServiceCard>

              {/* Highlight effect on click */}
              <motion.div 
                className={`absolute inset-0 rounded-xl opacity-0 z-0
                  ${theme === "dark" 
                    ? "bg-gradient-to-br from-indigo-500/5 to-violet-500/5" 
                    : "bg-gradient-to-br from-rose-500/5 to-pink-500/5"
                  }`}
                animate={{
                  opacity: clickedIndex === index ? 1 : 0
                }}
                transition={{ duration: 0.3 }}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Services;
