import { createContext, useState, useContext } from "react";
import { languages } from "../languages";

const LanguageContext = createContext();

// Define available languages with their metadata
const availableLanguages = [
  { code: "en", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
  { code: "jp", name: "æ—¥æœ¬èªž", flag: "ðŸ‡¯ðŸ‡µ" },
  { code: "ko", name: "í•œêµ­ì–´", flag: "ðŸ‡°ðŸ‡·" }
];

export const LanguageProvider = ({ children }) => {
  const [language, setLanguage] = useState("en");

  const toggleLanguage = () => {
    // Cycle through available languages: en -> jp -> ko -> en
    const currentIndex = availableLanguages.findIndex(lang => lang.code === language);
    const nextIndex = (currentIndex + 1) % availableLanguages.length;
    setLanguage(availableLanguages[nextIndex].code);
  };

  return (
    <LanguageContext.Provider 
      value={{ 
        language, 
        text: languages[language], 
        toggleLanguage, 
        setLanguage,
        availableLanguages
      }}
    >
      {children}
    </LanguageContext.Provider>
  );
};

export const useLanguage = () => useContext(LanguageContext);
