import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";

const Services = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();

  const [hoveredCard, setHoveredCard] = useState(null);
  const [activeModal, setActiveModal] = useState(null);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3,
      },
    },
  };

  // Header animation
  const headerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6,
        ease: "easeOut"
      } 
    }
  };

  // Service card animations
  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.5,
        ease: "easeOut" 
      }
    },
    hover: {
      y: -10,
      transition: { 
        duration: 0.3,
        ease: "easeOut" 
      }
    }
  };

  // Modal animation variants
  const modalVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { 
      opacity: 1, 
      scale: 1,
      transition: { 
        type: "spring",
        stiffness: 300,
        damping: 25
      }
    },
    exit: {
      opacity: 0,
      scale: 0.8,
      transition: {
        duration: 0.2
      }
    }
  };

  // Service icons
  const serviceIcons = {
    tutoring: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
      </svg>
    ),
    translation: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5h12M9 3v2m1.048 9.5A18.022 18.022 0 016.412 9m6.088 9h7M11 21l5-10 5 10M12.751 5C11.783 10.77 8.07 15.61 3 18.129" />
      </svg>
    ),
    trumpet: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19V6l12-3v13M9 19c0 1.105-1.343 2-3 2s-3-.895-3-2 1.343-2 3-2 3 .895 3 2zm12-3c0 1.105-1.343 2-3 2s-3-.895-3-2 1.343-2 3-2 3 .895 3 2zM9 10l12-3" />
      </svg>
    ),
  };

  const openServiceModal = (index) => {
    setActiveModal(index);
  };

  const closeModal = () => {
    setActiveModal(null);
  };

  // Ensure services exists with a fallback empty array
  const services = text?.services?.items || [];

  // Map service name to icon key for details lookup
  const getServiceIconKey = (serviceName) => {
    if (!serviceName) return "tutoring"; // Default fallback

    if (serviceName.includes("Tutoring") || serviceName.includes("個別指導") || serviceName.includes("과외")) {
      return "tutoring";
    } else if (serviceName.includes("Translation") || serviceName.includes("翻訳") || serviceName.includes("번역")) {
      return "translation";
    } else if (serviceName.includes("Trumpet") || serviceName.includes("トランペット") || serviceName.includes("트럼펫")) {
      return "trumpet";
    }
    return "tutoring"; // default fallback
  };

  // Get service icon
  const getServiceIcon = (serviceName) => {
    const key = getServiceIconKey(serviceName);
    return serviceIcons[key] || serviceIcons.tutoring;
  };

  return (
    <motion.section
      id="services"
      className="py-24 px-4 sm:px-6 lg:px-8 relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.1 }}
      variants={containerVariants}
    >
      {/* Background decorations */}
      <div className="absolute top-0 right-0 -mr-40 -mt-40 w-96 h-96 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-br from-indigo-600 to-violet-600" />
      <div className="absolute bottom-0 left-0 -ml-40 -mb-40 w-96 h-96 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-tr from-violet-600 to-indigo-600" />

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section Header */}
        <motion.div className="text-center mb-16" variants={headerVariants}>
          <span className={`inline-block px-3 py-1 text-sm font-medium rounded-full mb-4 
            ${theme === "light" 
              ? "bg-indigo-100 text-indigo-800" 
              : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
            }`}>
            {text?.services?.title}
          </span>
          <h2 className={`text-3xl sm:text-4xl font-bold mb-4 ${
            theme === "light" ? "text-slate-900" : "text-white"
          }`}>
            Professional <span className={theme === "light" ? "text-indigo-600" : "text-indigo-400"}>
              Services
            </span>
          </h2>
          <motion.p 
            className={`text-lg max-w-2xl mx-auto
              ${theme === "light" ? "text-slate-600" : "text-slate-300"}`}
            variants={headerVariants}
          >
            {text?.services?.description}
          </motion.p>
        </motion.div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div 
              key={index}
              className={`rounded-xl overflow-hidden ${
                theme === "light" 
                  ? "bg-white border border-slate-100 shadow-lg" 
                  : "bg-slate-800 border border-slate-700/50 shadow-lg"
              } relative`}
              variants={cardVariants}
              whileHover="hover"
              onHoverStart={() => setHoveredCard(index)}
              onHoverEnd={() => setHoveredCard(null)}
            >
              {/* Service color band at top */}
              <div className={`h-2 w-full ${
                theme === "light" 
                ? "bg-gradient-to-r from-indigo-500 to-violet-500" 
                : "bg-gradient-to-r from-indigo-600 to-violet-600"
              }`} />

              <div className="p-6">
                {/* Service icon */}
                <div className={`mb-5 p-3 inline-flex rounded-lg ${
                  theme === "light" 
                    ? "bg-indigo-100 text-indigo-600" 
                    : "bg-indigo-900/30 text-indigo-400"
                }`}>
                  {getServiceIcon(service.name)}
                </div>

                {/* Service title */}
                <h3 className={`text-xl font-bold mb-3 ${
                  theme === "light" ? "text-slate-800" : "text-white"
                }`}>
                  {service.name}
                </h3>

                {/* Service description */}
                <p className={`${theme === "light" ? "text-slate-600" : "text-slate-300"} mb-6`}>
                  {service.description}
                </p>

                {/* Divider */}
                <div className={`w-full h-px my-4 ${
                  theme === "light" ? "bg-slate-200" : "bg-slate-700"
                }`} />

                {/* Learn more button */}
                <motion.button 
                  className={`mt-2 inline-flex items-center text-sm font-medium cursor-pointer
                    ${theme === "light" ? "text-indigo-600 hover:text-indigo-700" : "text-indigo-400 hover:text-indigo-300"}`}
                  initial={{ opacity: 0.9 }}
                  animate={{ opacity: hoveredCard === index ? 1 : 0.9 }}
                  whileHover={{ x: 4, transition: { duration: 0.2 } }}
                  onClick={() => openServiceModal(index)}
                >
                  {text?.services?.learnMore}
                  <svg className="w-4 h-4 ml-1.5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L12.586 11H5a1 1 0 110-2h7.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                  </svg>
                </motion.button>
              </div>

              {/* Hover effect at bottom */}
              <motion.div 
                className={`absolute bottom-0 left-0 right-0 h-0.5 ${
                  theme === "light" 
                    ? "bg-gradient-to-r from-indigo-500 to-violet-500" 
                    : "bg-gradient-to-r from-indigo-500 to-violet-500"
                }`}
                initial={{ scaleX: 0 }}
                animate={{ scaleX: hoveredCard === index ? 1 : 0 }}
                transition={{ duration: 0.3 }}
              />
            </motion.div>
          ))}
        </div>

        {/* Service Detail Modals */}
        <AnimatePresence>
          {activeModal !== null && services[activeModal] && (
            <motion.div
              className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/60 backdrop-blur-sm"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={closeModal}
            >
              <motion.div 
                className={`relative max-w-2xl w-full rounded-xl p-0 mx-auto overflow-hidden ${
                  theme === "light" 
                    ? "bg-white shadow-xl" 
                    : "bg-slate-800 shadow-xl"
                }`}
                variants={modalVariants}
                initial="hidden"
                animate="visible"
                exit="exit"
                onClick={(e) => e.stopPropagation()}
              >
                {/* Modal header with gradient */}
                <div className={`p-6 ${
                  theme === "light" 
                    ? "bg-gradient-to-r from-indigo-500 to-violet-500 text-white" 
                    : "bg-gradient-to-r from-indigo-600 to-violet-600 text-white"
                }`}>
                  <div className="flex items-center">
                    <div className="p-3 bg-white/20 rounded-lg mr-4">
                      {getServiceIcon(services[activeModal].name)}
                    </div>
                    <h3 className="text-2xl font-bold">
                      {services[activeModal].name}
                    </h3>
                  </div>

                  {/* Close button */}
                  <button 
                    className="absolute top-4 right-4 p-2 rounded-full text-white/80 hover:text-white hover:bg-white/10 transition-colors"
                    onClick={closeModal}
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>

                {/* Modal content */}
                <div className="p-6">
                  {/* Service full description */}
                  <p className={`mb-6 ${
                    theme === "light" ? "text-slate-600" : "text-slate-300"
                  }`}>
                    {services[activeModal].description}
                  </p>

                  {/* Service features */}
                  <div className="mb-6">
                    <h4 className={`text-lg font-semibold mb-4 ${
                      theme === "light" ? "text-slate-800" : "text-white"
                    }`}>
                      {text?.services?.whatIncluded}
                    </h4>
                    <div className="space-y-3">
                      {text?.services?.details[getServiceIconKey(services[activeModal].name)] && (
                        <>
                          <div className={`flex items-start p-3 rounded-lg ${
                            theme === "light" ? "bg-slate-50" : "bg-slate-700/30"
                          }`}>
                            <span className={`mr-3 ${
                              theme === "light" ? "text-indigo-600" : "text-indigo-400"
                            }`}>
                              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                              </svg>
                            </span>
                            <span className={theme === "light" ? "text-slate-700" : "text-slate-200"}>
                              {text?.services?.details[getServiceIconKey(services[activeModal].name)].point1}
                            </span>
                          </div>

                          <div className={`flex items-start p-3 rounded-lg ${
                            theme === "light" ? "bg-slate-50" : "bg-slate-700/30"
                          }`}>
                            <span className={`mr-3 ${
                              theme === "light" ? "text-indigo-600" : "text-indigo-400"
                            }`}>
                              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                              </svg>
                            </span>
                            <span className={theme === "light" ? "text-slate-700" : "text-slate-200"}>
                              {text?.services?.details[getServiceIconKey(services[activeModal].name)].point2}
                            </span>
                          </div>

                          <div className={`flex items-start p-3 rounded-lg ${
                            theme === "light" ? "bg-slate-50" : "bg-slate-700/30"
                          }`}>
                            <span className={`mr-3 ${
                              theme === "light" ? "text-indigo-600" : "text-indigo-400"
                            }`}>
                              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                              </svg>
                            </span>
                            <span className={theme === "light" ? "text-slate-700" : "text-slate-200"}>
                              {text?.services?.details[getServiceIconKey(services[activeModal].name)].point3}
                            </span>
                          </div>
                        </>
                      )}
                    </div>
                  </div>

                  {/* Service details */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
                    {/* Pricing */}
                    <div className={`p-4 rounded-lg ${
                      theme === "light" ? "bg-indigo-50" : "bg-indigo-900/20"
                    }`}>
                      <h4 className={`text-lg font-semibold mb-2 flex items-center ${
                        theme === "light" ? "text-slate-800" : "text-white"
                      }`}>
                        <svg className="w-5 h-5 mr-2 text-indigo-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        {text?.services?.pricing}
                      </h4>
                      <p className={theme === "light" ? "text-slate-600" : "text-slate-300"}>
                        {text?.services?.details[getServiceIconKey(services[activeModal].name)]?.pricing}
                      </p>
                    </div>

                    {/* Availability */}
                    <div className={`p-4 rounded-lg ${
                      theme === "light" ? "bg-violet-50" : "bg-violet-900/20"
                    }`}>
                      <h4 className={`text-lg font-semibold mb-2 flex items-center ${
                        theme === "light" ? "text-slate-800" : "text-white"
                      }`}>
                        <svg className="w-5 h-5 mr-2 text-violet-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        {text?.services?.availability}
                      </h4>
                      <p className={theme === "light" ? "text-slate-600" : "text-slate-300"}>
                        {text?.services?.details[getServiceIconKey(services[activeModal].name)]?.availability}
                      </p>
                    </div>
                  </div>

                  {/* CTA Button */}
                  <motion.a 
                    href="#contact"
                    onClick={closeModal}
                    className={`inline-block w-full py-3.5 px-6 text-center rounded-lg font-medium 
                      ${theme === "light" 
                        ? "bg-indigo-600 text-white hover:bg-indigo-700" 
                        : "bg-indigo-500 text-white hover:bg-indigo-600"
                      } transition-colors duration-200 shadow-md`}
                    whileHover={{ scale: 1.02, y: -2 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    {text?.services?.getStarted}
                  </motion.a>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Call to action */}
        <motion.div 
          className="text-center mt-16"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <motion.a
            href="#contact"
            className={`inline-flex items-center px-6 py-3 rounded-full text-base font-medium 
              ${theme === "light" 
                ? "bg-indigo-600 text-white hover:bg-indigo-700" 
                : "bg-indigo-500 text-white hover:bg-indigo-600"
              } transition-all duration-300 shadow-md hover:shadow-lg`}
            whileHover={{ y: -5 }}
            whileTap={{ scale: 0.95 }}
          >
            {text?.services?.contactButton}
            <svg className="w-5 h-5 ml-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L12.586 11H5a1 1 0 110-2h7.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
            </svg>
          </motion.a>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Services;
