import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";

const ResumeButton = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();

  return (
    <motion.a
      href="https://docs.google.com/document/d/11GSYyV8riF_J5v3ewUiMieyqCJpkYWa6/edit?usp=sharing&ouid=100239401016507998095&rtpof=true&sd=true" 
      target="_blank"
      rel="noopener noreferrer"
      className={`px-4 py-2 rounded-lg text-sm font-medium inline-flex items-center transition-all ${
        theme === "dark"
          ? "bg-indigo-600 text-white hover:bg-indigo-700 shadow-md hover:shadow-indigo-500/20"
          : "bg-indigo-600 text-white hover:bg-indigo-700 shadow-md hover:shadow-indigo-500/20"
      }`}
      whileHover={{ y: -2 }}
      whileTap={{ scale: 0.97 }}
    >
      <svg 
        className="w-4 h-4 mr-2" 
        fill="none" 
        stroke="currentColor" 
        viewBox="0 0 24 24" 
        xmlns="http://www.w3.org/2000/svg"
      >
        <path 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          strokeWidth={2} 
          d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" 
        />
      </svg>
      {text.header.resume}    
    </motion.a>
  );
};

export default ResumeButton;
