import React, { useState } from "react";
import { motion } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages.js";

const Contact = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [formStatus, setFormStatus] = useState({
    submitting: false,
    submitted: false,
    error: false,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormStatus({ submitting: true, submitted: false, error: false });

    // Simulate form submission
    setTimeout(() => {
      setFormStatus({ submitting: false, submitted: true, error: false });
      setFormData({ name: "", email: "", message: "" });

      // Reset form status after 5 seconds
      setTimeout(() => {
        setFormStatus({ submitting: false, submitted: false, error: false });
      }, 5000);
    }, 1500);
  };

  return (
    <section id="contact" className="py-24 relative overflow-hidden">
      {/* Background Grid */}
      <div 
        className={`absolute inset-0 ${
          theme === "light" ? "opacity-[0.015]" : "opacity-[0.03]"
        }`}
        style={{
          backgroundImage: `
            linear-gradient(to right, ${theme === "light" ? "black" : "white"} 1px, transparent 1px),
            linear-gradient(to bottom, ${theme === "light" ? "black" : "white"} 1px, transparent 1px)
          `,
          backgroundSize: '5rem 5rem'
        }}
      />

      {/* Background Shapes */}
      {[1, 2, 3].map((i) => (
        <motion.div
          key={i}
          className={`absolute pointer-events-none ${
            theme === "light" 
              ? i % 2 === 0 ? "bg-rose-500/10" : "bg-gray-800/5" 
              : i % 2 === 0 ? "bg-indigo-500/10" : "bg-gray-200/5"
          } ${i === 3 ? 'rounded-none' : 'rounded-full'}`}
          style={{
            width: 200 + i * 100,
            height: 200 + i * 100,
            bottom: `${i * 15}%`,
            right: i % 2 === 0 ? `${80 + i * 5}%` : `-${i * 5}%`,
            zIndex: 0
          }}
          animate={{
            x: [0, i * 20, -i * 30, i * 10, 0],
            y: [0, -i * 20, i * 30, -i * 10, 0],
            rotate: [0, i * 10, i * 20, i * 10, 0]
          }}
          transition={{
            duration: 20 + i * 5,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}

      <div className="container mx-auto px-4 relative z-10">
        {/* Section Header */}
        <div className="mb-16 relative">
          <motion.div
            initial={{ x: -30, opacity: 0 }}
            whileInView={{ x: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
            className={`absolute -left-3 top-0 h-16 w-3 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
          />
          <motion.h2 
            className={`text-5xl md:text-6xl font-bold mb-6 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}
            initial={{ y: 30, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
          >
            {currentLanguage.contact.title}
          </motion.h2>
          <motion.p
            className={`text-xl max-w-3xl ${
              theme === "light" ? "text-gray-700" : "text-gray-300"
            }`}
            initial={{ y: 20, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            {currentLanguage.contact.description}
          </motion.p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
          {/* Contact Info */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
          >
            <h3 className={`text-2xl font-bold mb-8 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}>
              {currentLanguage.contact.getInTouch}
            </h3>

            {/* Contact Cards */}
            <div className="space-y-6">
              {/* Email */}
              <motion.div 
                className={`p-6 border-2 ${
                  theme === "light" 
                    ? "border-gray-800 bg-white" 
                    : "border-gray-100 bg-gray-900"
                }`}
                whileHover={{ y: -5 }}
                transition={{ duration: 0.2 }}
              >
                <div className="flex items-start">
                  <div className={`mr-4 p-3 ${
                    theme === "light" ? "bg-gray-100" : "bg-gray-800"
                  }`}>
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                  </div>
                  <div>
                    <h4 className={`font-bold mb-1 ${
                      theme === "light" ? "text-gray-900" : "text-white"
                    }`}>
                      {currentLanguage.contact.emailTitle}
                    </h4>
                    <a 
                      href="mailto:yamaguchi@example.com" 
                      className={`${
                        theme === "light" ? "text-rose-600 hover:text-rose-700" : "text-indigo-400 hover:text-indigo-300"
                      }`}
                    >
                      yamaguchi@example.com
                    </a>
                  </div>
                </div>
              </motion.div>

              {/* Location */}
              <motion.div 
                className={`p-6 border-2 ${
                  theme === "light" 
                    ? "border-gray-800 bg-white" 
                    : "border-gray-100 bg-gray-900"
                }`}
                whileHover={{ y: -5 }}
                transition={{ duration: 0.2 }}
              >
                <div className="flex items-start">
                  <div className={`mr-4 p-3 ${
                    theme === "light" ? "bg-gray-100" : "bg-gray-800"
                  }`}>
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>
                  </div>
                  <div>
                    <h4 className={`font-bold mb-1 ${
                      theme === "light" ? "text-gray-900" : "text-white"
                    }`}>
                      {currentLanguage.contact.locationTitle}
                    </h4>
                    <p className={`${
                      theme === "light" ? "text-gray-700" : "text-gray-300"
                    }`}>
                      {currentLanguage.contact.location}
                    </p>
                  </div>
                </div>
              </motion.div>

              {/* Follow Me */}
              <div className="mt-12">
                <h4 className={`font-bold mb-4 ${
                  theme === "light" ? "text-gray-900" : "text-white"
                }`}>
                  {currentLanguage.contact.followMe}
                </h4>
                <div className="flex space-x-4">
                  <motion.a
                    href="https://linkedin.com" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className={`p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-800 text-gray-800 hover:bg-gray-800 hover:text-white" 
                        : "border-gray-100 text-white hover:bg-gray-100 hover:text-gray-900"
                    }`}
                    whileHover={{ y: -5 }}
                    transition={{ duration: 0.2 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M19 0h-14c-2.761 0-5 2.239-5 5v14c0 2.761 2.239 5 5 5h14c2.762 0 5-2.239 5-5v-14c0-2.761-2.238-5-5-5zm-11 19h-3v-11h3v11zm-1.5-12.268c-.966 0-1.75-.79-1.75-1.764s.784-1.764 1.75-1.764 1.75.79 1.75 1.764-.783 1.764-1.75 1.764zm13.5 12.268h-3v-5.604c0-.88-.018-2.013-1.227-2.013-1.227 0-1.415.957-1.415 1.947v5.67h-3v-11h2.85v1.634h.042c.494-.936 1.699-1.924 3.5-1.924 3.742 0 4.4 2.464 4.4 5.667v6.623z"/>
                    </svg>
                  </motion.a>
                  <motion.a
                    href="https://github.com" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className={`p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-800 text-gray-800 hover:bg-gray-800 hover:text-white" 
                        : "border-gray-100 text-white hover:bg-gray-100 hover:text-gray-900"
                    }`}
                    whileHover={{ y: -5 }}
                    transition={{ duration: 0.2 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                    </svg>
                  </motion.a>
                  <motion.a
                    href="https://twitter.com" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className={`p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-800 text-gray-800 hover:bg-gray-800 hover:text-white" 
                        : "border-gray-100 text-white hover:bg-gray-100 hover:text-gray-900"
                    }`}
                    whileHover={{ y: -5 }}
                    transition={{ duration: 0.2 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M24 4.557c-.883.392-1.832.656-2.828.775 1.017-.609 1.798-1.574 2.165-2.724-.951.564-2.005.974-3.127 1.195-.897-.957-2.178-1.555-3.594-1.555-3.179 0-5.515 2.966-4.797 6.045-4.091-.205-7.719-2.165-10.148-5.144-1.29 2.213-.669 5.108 1.523 6.574-.806-.026-1.566-.247-2.229-.616-.054 2.281 1.581 4.415 3.949 4.89-.693.188-1.452.232-2.224.084.626 1.956 2.444 3.379 4.6 3.419-2.07 1.623-4.678 2.348-7.29 2.04 2.179 1.397 4.768 2.212 7.548 2.212 9.142 0 14.307-7.721 13.995-14.646.962-.695 1.797-1.562 2.457-2.549z"/>
                    </svg>
                  </motion.a>
                </div>
              </div>
            </div>
          </motion.div>

          {/* Contact Form */}
          <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            <h3 className={`text-2xl font-bold mb-8 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}>
              {currentLanguage.contact.sendMessage}
            </h3>

            <form 
              onSubmit={handleSubmit}
              className={`border-2 ${
                theme === "light" 
                  ? "border-gray-800" 
                  : "border-gray-100"
              }`}
            >
              <div className="p-6 space-y-6">
                {/* Form fields */}
                <div>
                  <label 
                    htmlFor="name" 
                    className={`block mb-2 font-bold ${
                      theme === "light" ? "text-gray-900" : "text-white"
                    }`}
                  >
                    {currentLanguage.contact.form.name}
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                    className={`w-full p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-300 focus:border-rose-500 text-gray-900 bg-white" 
                        : "border-gray-700 focus:border-indigo-500 text-white bg-gray-800"
                    }`}
                  />
                </div>

                <div>
                  <label 
                    htmlFor="email" 
                    className={`block mb-2 font-bold ${
                      theme === "light" ? "text-gray-900" : "text-white"
                    }`}
                  >
                    {currentLanguage.contact.form.email}
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    className={`w-full p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-300 focus:border-rose-500 text-gray-900 bg-white" 
                        : "border-gray-700 focus:border-indigo-500 text-white bg-gray-800"
                    }`}
                  />
                </div>

                <div>
                  <label 
                    htmlFor="message" 
                    className={`block mb-2 font-bold ${
                      theme === "light" ? "text-gray-900" : "text-white"
                    }`}
                  >
                    {currentLanguage.contact.form.message}
                  </label>
                  <textarea
                    id="message"
                    name="message"
                    rows="5"
                    value={formData.message}
                    onChange={handleChange}
                    required
                    className={`w-full p-3 border-2 ${
                      theme === "light" 
                        ? "border-gray-300 focus:border-rose-500 text-gray-900 bg-white" 
                        : "border-gray-700 focus:border-indigo-500 text-white bg-gray-800"
                    }`}
                  ></textarea>
                </div>
              </div>

              {/* Form footer */}
              <div className={`p-6 ${
                theme === "light" ? "bg-gray-100" : "bg-gray-800"
              }`}>
                <div className="flex items-center justify-between">
                  <div>
                    {formStatus.submitted && (
                      <span className={`text-sm font-medium ${
                        theme === "light" ? "text-green-600" : "text-green-400"
                      }`}>
                        {currentLanguage.contact.form.success}
                      </span>
                    )}
                    {formStatus.error && (
                      <span className={`text-sm font-medium ${
                        theme === "light" ? "text-red-600" : "text-red-400"
                      }`}>
                        {currentLanguage.contact.form.error}
                      </span>
                    )}
                  </div>

                  <motion.button
                    type="submit"
                    disabled={formStatus.submitting}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className={`px-6 py-3 font-bold ${
                      theme === "light"
                        ? "bg-rose-500 text-white hover:bg-rose-600"
                        : "bg-indigo-500 text-white hover:bg-indigo-600"
                    } transition-colors`}
                  >
                    {formStatus.submitting 
                      ? currentLanguage.contact.form.sending 
                      : currentLanguage.contact.form.send
                    }
                  </motion.button>
                </div>
              </div>
            </form>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
