import React, { useRef, useState, useEffect } from "react";
import { motion } from "framer-motion";
import { useLanguage } from "../contexts/LanguageContext";
import { useTheme } from "../contexts/ThemeContext";

// ServiceCard component with subtle hover effect
const ServiceCard = ({ icon, title, description, theme }) => {
  const cardRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });

  // Handle mouse movement on the card
  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const rect = cardRef.current.getBoundingClientRect();
    const width = rect.width;
    const height = rect.height;

    // Calculate mouse position relative to the card center
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    // Convert to normalized position from -0.5 to 0.5
    const normalizedX = (mouseX / width - 0.5);
    const normalizedY = (mouseY / height - 0.5);

    // Calculate rotation angle (very subtle)
    setRotation({
      x: normalizedY * 2,
      y: normalizedX * -2
    });
  };

  // Reset rotation when mouse leaves
  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotation({ x: 0, y: 0 });
  };

  return (
    <motion.div
      ref={cardRef}
      className={`h-full p-6 rounded-xl ${
        theme === "light"
          ? "bg-gradient-to-br from-white to-rose-50 border border-rose-100 shadow-md shadow-rose-200/20"
          : "bg-gradient-to-br from-slate-800 to-slate-900 border border-indigo-500/10 shadow-lg shadow-indigo-500/5"
      }`}
      onMouseMove={handleMouseMove}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={handleMouseLeave}
      style={{
        transformStyle: "preserve-3d",
        transform: `perspective(1000px) rotateX(${rotation.x}deg) rotateY(${rotation.y}deg)`,
        transition: "transform 0.2s ease"
      }}
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
    >
      <div className="flex flex-col h-full">
        <div 
          className={`text-3xl p-3 rounded-lg inline-block mb-4 ${
            theme === "light" 
              ? "bg-rose-100 text-rose-600" 
              : "bg-indigo-500/20 text-indigo-400"
          }`}
        >
          {icon}
        </div>

        <h3 className="text-xl font-bold mb-3">{title}</h3>
        <p className={`mt-2 ${theme === "light" ? "text-gray-600" : "text-gray-400"}`}>
          {description}
        </p>

        {isHovered && (
          <motion.div 
            className="absolute bottom-0 right-0 w-32 h-32 -z-10 opacity-20 rounded-full blur-2xl"
            initial={{ opacity: 0 }}
            animate={{ opacity: 0.2 }}
            exit={{ opacity: 0 }}
            style={{
              background: theme === "light" 
                ? "radial-gradient(circle, rgba(244,114,182,0.6) 0%, rgba(255,255,255,0) 70%)" 
                : "radial-gradient(circle, rgba(99,102,241,0.6) 0%, rgba(0,0,0,0) 70%)"
            }}
          />
        )}
      </div>
    </motion.div>
  );
};

const Services = () => {
  const { language } = useLanguage();
  const { theme } = useTheme();
  const [currentLanguage, setCurrentLanguage] = useState(null);

  useEffect(() => {
    // Dynamic import to avoid circular dependencies
    import('../languages.js').then(module => {
      setCurrentLanguage(module.languages[language]);
    });
  }, [language]);

  if (!currentLanguage) return null;

  // Animation variants for staggered animations
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.4,
        ease: "easeOut"
      }
    }
  };

  // Services data with icons
  const services = [
    {
      icon: "üíª",
      title: currentLanguage.services.web_dev.title,
      description: currentLanguage.services.web_dev.description
    },
    {
      icon: "üì±",
      title: currentLanguage.services.mobile_dev.title,
      description: currentLanguage.services.mobile_dev.description
    },
    {
      icon: "üé®",
      title: currentLanguage.services.ui_design.title,
      description: currentLanguage.services.ui_design.description
    },
    {
      icon: "üñ•Ô∏è",
      title: currentLanguage.services.backend.title,
      description: currentLanguage.services.backend.description
    }
  ];

  return (
    <div id="services" className="py-16 lg:py-24">
      <div className="container mx-auto px-4">
        <motion.div
          className="text-center mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true, margin: "-100px" }}
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            {currentLanguage.services.title}
          </h2>
          <p className="max-w-2xl mx-auto text-lg opacity-80">
            {currentLanguage.services.subtitle}
          </p>
        </motion.div>

        <motion.div
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-100px" }}
        >
          {services.map((service, index) => (
            <motion.div key={index} variants={itemVariants}>
              <ServiceCard 
                icon={service.icon}
                title={service.title}
                description={service.description}
                theme={theme}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </div>
  );
};

export default Services;
