import React, { useState, useEffect } from "react";
import { Typewriter } from "react-simple-typewriter";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";

const Particle = ({ x, y, size, speed, color }) => {
  const [position, setPosition] = useState({ x, y });

  useEffect(() => {
    const animate = () => {
      setPosition(prev => ({
        x: prev.x + (Math.random() - 0.5) * speed,
        y: prev.y + (Math.random() - 0.5) * speed
      }));
    };

    const interval = setInterval(animate, 50);
    return () => clearInterval(interval);
  }, [speed]);

  return (
    <motion.div
      className="absolute rounded-full"
      style={{
        width: size,
        height: size,
        background: color,
        opacity: 0.3,
        x: position.x,
        y: position.y,
      }}
    />
  );
};

const LoadingScreen = ({ onComplete }) => {
  const { setTheme } = useTheme();
  const { text, toggleLanguage } = useLanguage();
  const [showLanguagePrompt, setShowLanguagePrompt] = useState(true);
  const [showMoodPrompt, setShowMoodPrompt] = useState(false);
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    // Generate particles
    const newParticles = Array.from({ length: 50 }, (_, i) => ({
      id: i,
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 4 + 2,
      speed: Math.random() * 0.5 + 0.2,
      color: `rgba(${Math.random() * 100 + 155}, ${Math.random() * 100 + 155}, ${Math.random() * 100 + 155}, 0.3)`
    }));
    setParticles(newParticles);
  }, []);

  const languagePrompts = [
    "Select A Language",
    "言語を選択してください",
    "언어를 선택하세요",
  ];

  const handleLanguageSelection = (language) => {
    toggleLanguage(language);
    setShowLanguagePrompt(false);
    setShowMoodPrompt(true);
  };

  const handleMoodSelection = (mood) => {
    setTheme(mood);
    setTimeout(onComplete, 1000);
  };

  return (
    <div className="w-full h-screen flex flex-col justify-center items-center bg-gradient-to-br from-gray-900 to-black text-white relative overflow-hidden">
      {/* Particles */}
      {particles.map(particle => (
        <Particle key={particle.id} {...particle} />
      ))}

      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 to-purple-500/10 animate-pulse"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-blue-500/5 via-transparent to-transparent"></div>
      </div>

      <AnimatePresence mode="wait">
        {showLanguagePrompt ? (
          <motion.div 
            key="language"
            className="text-center z-10"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20, transition: { duration: 0.3 } }}
            transition={{ duration: 0.5 }}
          >
            {/* Language Selection */}
            <div className="text-4xl font-bold mb-12 h-16 flex items-center justify-center">
              <Typewriter
                words={languagePrompts}
                loop={true}
                cursor
                cursorStyle="_"
                typeSpeed={70}
                deleteSpeed={50}
                delaySpeed={1500}
              />
            </div>
            <div className="flex space-x-6 mt-8">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleLanguageSelection("en")}
                className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/0 to-purple-500/0 group-hover:from-blue-500/10 group-hover:to-purple-500/10 rounded-2xl transition-all duration-300"></div>
                <div className="relative flex items-center space-x-3">
                  <img
                    src="https://flagsapi.com/US/flat/32.png"
                    alt="US Flag"
                    className="w-8 h-8"
                  />
                  <span className="text-lg font-medium">English</span>
                </div>
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleLanguageSelection("jp")}
                className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/0 to-purple-500/0 group-hover:from-blue-500/10 group-hover:to-purple-500/10 rounded-2xl transition-all duration-300"></div>
                <div className="relative flex items-center space-x-3">
                  <img
                    src="https://flagsapi.com/JP/flat/32.png"
                    alt="Japanese Flag"
                    className="w-8 h-8"
                  />
                  <span className="text-lg font-medium">日本語</span>
                </div>
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleLanguageSelection("ko")}
                className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/0 to-purple-500/0 group-hover:from-blue-500/10 group-hover:to-purple-500/10 rounded-2xl transition-all duration-300"></div>
                <div className="relative flex items-center space-x-3">
                  <img
                    src="https://flagsapi.com/KR/flat/32.png"
                    alt="Korean Flag"
                    className="w-8 h-8"
                  />
                  <span className="text-lg font-medium">한국어</span>
                </div>
              </motion.button>
            </div>
          </motion.div>
        ) : (
          <motion.div 
            key="theme"
            className="text-center z-10"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20, transition: { duration: 0.3 } }}
            transition={{ duration: 0.5 }}
          >
            {/* Theme Selection */}
            <h1 className="text-4xl font-bold mb-12">{text.loadingScreen.moodPrompt}</h1>
            <div className="flex space-x-6">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleMoodSelection("light")}
                className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-yellow-500/0 to-orange-500/0 group-hover:from-yellow-500/10 group-hover:to-orange-500/10 rounded-2xl transition-all duration-300"></div>
                <div className="relative flex items-center space-x-3">
                  <span className="text-2xl">☀️</span>
                  <span className="text-lg font-medium">{text.loadingScreen.moodBright}</span>
                </div>
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleMoodSelection("dark")}
                className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/0 to-purple-500/0 group-hover:from-blue-500/10 group-hover:to-purple-500/10 rounded-2xl transition-all duration-300"></div>
                <div className="relative flex items-center space-x-3">
                  <span className="text-2xl">🌙</span>
                  <span className="text-lg font-medium">{text.loadingScreen.moodDark}</span>
                </div>
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default LoadingScreen;
