import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

// Service card with flip effect to reveal contact info
const ServiceCard = ({ service, onClick }) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  return (
    <div 
      className="relative h-full w-full perspective-1000 cursor-pointer"
      onMouseEnter={() => setIsFlipped(true)}
      onMouseLeave={() => setIsFlipped(false)}
      onClick={onClick}
    >
      <motion.div 
        className="relative w-full h-full transform-gpu preserve-3d duration-500"
        animate={{ 
          rotateY: isFlipped ? 180 : 0
        }}
        transition={{ duration: 0.6, ease: [0.23, 1, 0.32, 1] }}
      >
        {/* Front of card (Service info) */}
        <div className={`absolute inset-0 p-6 rounded-2xl backface-hidden
          ${theme === "light" 
            ? "bg-gradient-to-br from-white to-rose-50/50" 
            : "bg-gradient-to-br from-gray-800/80 to-slate-900/80"
          } backdrop-blur-sm border border-opacity-20
          ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
        >
          <div className="relative mb-4">
            <div className={`absolute -left-3 top-0 h-full w-1 rounded-full 
              ${theme === "light" 
                ? "bg-gradient-to-b from-rose-500 to-pink-600" 
                : "bg-gradient-to-b from-indigo-500 to-violet-600"
              }`} 
            />
            <h3
              className={`text-xl font-semibold ml-1
                ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}
            >
              {service.name}
            </h3>
          </div>
          <p
            className={`text-sm leading-relaxed
              ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}
          >
            {service.description}
          </p>
        </div>

        {/* Back of card (Contact info) */}
        <div className={`absolute inset-0 p-6 rounded-2xl backface-hidden rotateY-180
          ${theme === "light" 
            ? "bg-gradient-to-br from-rose-50 to-white" 
            : "bg-gradient-to-br from-slate-900/80 to-gray-800/80"
          } backdrop-blur-sm border border-opacity-20
          ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
        >
          <div className="flex flex-col items-center justify-center h-full space-y-4">
            <div className={`w-12 h-12 rounded-full flex items-center justify-center mb-2
              ${theme === "light"
                ? "bg-rose-100 text-rose-600"
                : "bg-indigo-900/30 text-indigo-400"
              }`}
            >
              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                <path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z" />
                <path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z" />
              </svg>
            </div>
            <h4 className={`text-lg font-medium
              ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}>
              {currentLanguage.services.contactInfo.title}
            </h4>
            <p className={`text-sm text-center
              ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}>
              {currentLanguage.services.contactInfo.message}
            </p>
            <motion.button
              className={`mt-2 px-4 py-2 rounded-xl text-sm font-medium
                ${theme === "light"
                  ? "bg-gradient-to-r from-rose-500 to-pink-600 text-white"
                  : "bg-gradient-to-r from-indigo-500 to-violet-600 text-white"
                }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {currentLanguage.services.contactInfo.button}
            </motion.button>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

const Services = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [clickedIndex, setClickedIndex] = useState(null);

  // Enhanced staggered entrance animation
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.15,
        delayChildren: 0.2,
      } 
    },
  };

  // Entrance animation for title and description
  const headerVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.7,
        ease: [0.22, 1, 0.36, 1]
      } 
    }
  };

  // More dynamic item animation
  const itemVariants = {
    hidden: { opacity: 0, y: 40, scale: 0.9 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { 
        duration: 0.7,
        type: "spring",
        stiffness: 80,
        damping: 20
      } 
    },
    tap: {
      scale: 0.97,
      transition: { duration: 0.1 }
    }
  };

  const handleServiceClick = (index) => {
    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  const services = currentLanguage.services.items;

  return (
    <motion.section
      id="services"
      className="min-h-screen py-24 px-8 flex flex-col justify-center relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        {/* Entrance animation for section title */}
        <motion.div className="overflow-hidden mb-2">
          <motion.h2
            className={`text-3xl font-bold text-center
              ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.title}
          </motion.h2>
        </motion.div>

        {/* Entrance animation for description */}
        <motion.div className="overflow-hidden mb-12">
          <motion.p
            className={`text-base text-center mb-2 max-w-3xl mx-auto
              ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.description}
          </motion.p>
        </motion.div>

        {/* Service Cards with staggered animation */}
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
          variants={containerVariants}
        >
          {services.map((service, index) => (
            <motion.div
              key={index}
              custom={index}
              variants={itemVariants}
              whileTap="tap"
              className="h-[220px] transform-gpu shadow-[0_8px_20px_rgba(0,0,0,0.06)]"
            >
              <ServiceCard 
                service={service} 
                onClick={() => handleServiceClick(index)} 
              />

              {/* Highlight effect on click */}
              <motion.div 
                className={`absolute inset-0 rounded-xl opacity-0 z-0
                  ${theme === "dark" 
                    ? "bg-gradient-to-br from-indigo-500/5 to-violet-500/5" 
                    : "bg-gradient-to-br from-rose-500/5 to-pink-500/5"
                  }`}
                animate={{
                  opacity: clickedIndex === index ? 1 : 0
                }}
                transition={{ duration: 0.3 }}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Services;

/* Add these custom CSS classes to your stylesheets or append them for 3D transforms */
/* If you don't have these classes already, add to global CSS */
/*
.perspective-1000 {
  perspective: 1000px;
}

.preserve-3d {
  transform-style: preserve-3d;
}

.backface-hidden {
  backface-visibility: hidden;
}

.rotateY-180 {
  transform: rotateY(180deg);
}
*/
