import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";

const Footer = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const currentYear = new Date().getFullYear();

  // Use text from language context with fallbacks
  const footerText = {
    description: text?.footer?.description || "Multilingual full-stack developer specializing in creating responsive web applications with a focus on user experience and cross-cultural communication.",
    quickLinks: text?.footer?.quickLinks || "Quick Links",
    contact: text?.footer?.contact || "Contact",
    rights: text?.footer?.rights || "All rights reserved.",
    designed: text?.footer?.designed || "Built with",
    resume: text?.footer?.resume || "View Resume"
  };

  // Animations for staggered appearance
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.1,
        delayChildren: 0.1,
      } 
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  const socialLinks = [
    {
      name: "LinkedIn",
      href: "https://www.linkedin.com/in/yutaka-yamaguchi",
      icon: (
        <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
          <path d="M19 0h-14c-2.761 0-5 2.239-5 5v14c0 2.761 2.239 5 5 5h14c2.762 0 5-2.239 5-5v-14c0-2.761-2.238-5-5-5zm-11 19h-3v-11h3v11zm-1.5-12.268c-.966 0-1.75-.79-1.75-1.764s.784-1.764 1.75-1.764 1.75.79 1.75 1.764-.783 1.764-1.75 1.764zm13.5 12.268h-3v-5.604c0-.88-.018-2.013-1.227-2.013-1.227 0-1.415.957-1.415 1.947v5.67h-3v-11h2.85v1.634h.042c.494-.936 1.699-1.924 3.5-1.924 3.742 0 4.4 2.464 4.4 5.667v6.623z"/>
        </svg>
      ),
    },
    {
      name: "GitHub",
      href: "https://github.com/yyamaguchi0102?tab=repositories",
      icon: (
        <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
          <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
        </svg>
      ),
    },
    {
      name: "Email",
      href: "mailto:contact@yutaka-yamaguchi.com",
      icon: (
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
        </svg>
      ),
    },
    {
      name: "Twitter",
      href: "https://twitter.com",
      icon: (
        <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
          <path d="M23.953 4.57a10 10 0 01-2.825.775 4.958 4.958 0 002.163-2.723c-.951.555-2.005.959-3.127 1.184a4.92 4.92 0 00-8.384 4.482C7.69 8.095 4.067 6.13 1.64 3.162a4.822 4.822 0 00-.666 2.475c0 1.71.87 3.213 2.188 4.096a4.904 4.904 0 01-2.228-.616v.06a4.923 4.923 0 003.946 4.827 4.996 4.996 0 01-2.212.085 4.936 4.936 0 004.604 3.417 9.867 9.867 0 01-6.102 2.105c-.39 0-.779-.023-1.17-.067a13.995 13.995 0 007.557 2.209c9.053 0 13.998-7.496 13.998-13.985 0-.21 0-.42-.015-.63A9.935 9.935 0 0024 4.59z"/>
        </svg>
      ),
    },
  ];

  // Derive navigation items from text context with fallbacks
  const quickLinks = [
    { name: text?.header?.home || "Home", href: "#home" },
    { name: text?.header?.skills || "Skills", href: "#skills" },
    { name: text?.header?.projects || "Projects", href: "#projects" },
    { name: text?.header?.services || "Services", href: "#services" },
    { name: text?.header?.contact || "Contact", href: "#contact" },
  ];

  return (
    <motion.footer
      className={`relative mt-20 overflow-hidden ${
        theme === "light" 
          ? "bg-slate-50 text-slate-700 border-t border-slate-200" 
          : "bg-slate-900 text-slate-300 border-t border-slate-800/50"
      }`}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      {/* Background decorative elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div 
          className={`absolute -top-24 -right-24 w-72 h-72 rounded-full blur-3xl opacity-10 ${
            theme === "light" 
              ? "bg-indigo-300" 
              : "bg-indigo-800"
          }`}
        />
        <div 
          className={`absolute -bottom-24 -left-24 w-72 h-72 rounded-full blur-3xl opacity-10 ${
            theme === "light" 
              ? "bg-violet-300" 
              : "bg-violet-800"
          }`}
        />
      </div>

      {/* Main footer content */}
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        {/* Logo and information section */}
        <div className="pt-16 pb-12 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-12">
          {/* About section */}
          <motion.div className="col-span-1 md:col-span-2 lg:col-span-2" variants={itemVariants}>
            <div className="flex items-center gap-2 mb-6">
              <div className={`flex items-center justify-center w-9 h-9 rounded-lg bg-gradient-to-br ${
                theme === "light"
                  ? "from-indigo-500 to-violet-500" 
                  : "from-indigo-600 to-violet-600"
              }`}>
                <span className="text-white font-bold text-lg">Y</span>
              </div>
              <span className={`text-lg font-bold ${
                theme === "light" ? "text-slate-800" : "text-white"
              }`}>
                Yutaka Yamaguchi
              </span>
            </div>

            <p className={`mb-6 max-w-md ${
              theme === "light" ? "text-slate-600" : "text-slate-400"
            }`}>
              {footerText.description}
            </p>

            {/* Social links */}
            <div className="flex gap-3 mt-6">
              {socialLinks.map((link) => (
                <motion.a
                  key={link.name}
                  href={link.href}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`p-2.5 rounded-full transition-all duration-300 ${
                    theme === "light"
                      ? "bg-slate-100 text-slate-700 hover:bg-slate-200 hover:text-indigo-600"
                      : "bg-slate-800 text-slate-300 hover:bg-slate-700 hover:text-indigo-400"
                  }`}
                  whileHover={{ y: -3 }}
                  whileTap={{ scale: 0.95 }}
                  aria-label={link.name}
                >
                  {link.icon}
                </motion.a>
              ))}
            </div>
          </motion.div>

          {/* Quick links */}
          <motion.div className="col-span-1" variants={itemVariants}>
            <h3 className={`text-lg font-semibold mb-5 ${
              theme === "light" ? "text-slate-900" : "text-white"
            }`}>
              {footerText.quickLinks}
            </h3>
            <ul className="space-y-3">
              {quickLinks.map((link) => (
                <li key={link.name}>
                  <motion.a
                    href={link.href}
                    className={`group transition-all duration-300 block py-1 ${
                      theme === "light" 
                        ? "text-slate-600 hover:text-indigo-600" 
                        : "text-slate-400 hover:text-indigo-400"
                    }`}
                    whileHover={{ x: 3 }}
                  >
                    <span className="inline-flex items-center">
                      <span className={`mr-2 text-xs opacity-0 group-hover:opacity-100 transition-opacity ${
                        theme === "light" ? "text-indigo-600" : "text-indigo-400"
                      }`}>→</span>
                      {link.name}
                    </span>
                  </motion.a>
                </li>
              ))}
            </ul>
          </motion.div>

          {/* Contact info */}
          <motion.div className="col-span-1" variants={itemVariants}>
            <h3 className={`text-lg font-semibold mb-5 ${
              theme === "light" ? "text-slate-900" : "text-white"
            }`}>
              {footerText.contact}
            </h3>
            <div className="space-y-3">
              <p className={`flex items-center ${
                theme === "light" ? "text-slate-600" : "text-slate-400"
              }`}>
                <svg className="w-5 h-5 mr-3 opacity-70" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
                Tokyo, Japan
              </p>
              <p className={`flex items-center ${
                theme === "light" ? "text-slate-600" : "text-slate-400"
              }`}>
                <svg className="w-5 h-5 mr-3 opacity-70" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                </svg>
                contact@yutaka-yamaguchi.com
              </p>
            </div>

            {/* Resume link */}
            <motion.a
              href="https://docs.google.com/document/d/11GSYyV8riF_J5v3ewUiMieyqCJpkYWa6/edit?usp=sharing"
              target="_blank"
              rel="noopener noreferrer"
              className={`mt-6 inline-flex items-center px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 ${
                theme === "light"
                  ? "bg-indigo-600 text-white hover:bg-indigo-700 shadow-sm hover:shadow"
                  : "bg-indigo-500 text-white hover:bg-indigo-600 shadow-sm hover:shadow"
              }`}
              whileHover={{ y: -2 }}
              whileTap={{ scale: 0.98 }}
            >
              <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
              {footerText.resume}
            </motion.a>
          </motion.div>
        </div>

        {/* Bottom section with copyright */}
        <motion.div 
          className={`py-6 border-t ${
            theme === "light" ? "border-slate-200" : "border-slate-800"
          }`}
          variants={itemVariants}
        >
          <div className="flex flex-col md:flex-row justify-between items-center">
            <p className={`text-sm ${theme === "light" ? "text-slate-500" : "text-slate-500"}`}>
              © {currentYear} Yutaka Yamaguchi. {footerText.rights}
            </p>
            <p className={`text-sm mt-2 md:mt-0 flex items-center ${theme === "light" ? "text-slate-500" : "text-slate-500"}`}>
              {footerText.designed} 
              <span className={`inline-flex items-center ml-1.5 ${
                theme === "light" ? "text-indigo-600" : "text-indigo-400"
