import React, { useState } from "react";
import { useLanguage } from "../contexts/LanguageContext";
import { useTheme } from "../contexts/ThemeContext";
import { Typewriter } from "react-simple-typewriter";
import { motion } from "framer-motion";

const Home = () => {
  const { text } = useLanguage();
  const { theme } = useTheme();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <section
      id="home"
      className={`relative min-h-screen flex items-center justify-center overflow-hidden ${
        theme === "light"
          ? "bg-gradient-to-br from-light-background via-white to-orange-50"
          : "bg-gradient-to-br from-dark-background via-gray-900 to-blue-900"
      }`}
    >
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className={`absolute w-[500px] h-[500px] rounded-full blur-3xl opacity-20
          ${theme === "light" ? "bg-orange-400" : "bg-blue-500"}
          -top-32 -right-32 animate-pulse`}
        />
        <div className={`absolute w-[500px] h-[500px] rounded-full blur-3xl opacity-20
          ${theme === "light" ? "bg-yellow-400" : "bg-purple-500"}
          -bottom-32 -left-32 animate-pulse delay-1000`}
        />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
          {/* Left Column - Text Content */}
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="text-left"
          >
            <motion.h1 
              className={`text-6xl font-bold mb-6 leading-tight
                ${theme === "light" ? "text-gray-900" : "text-white"}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
            >
              {text.home.name}
            </motion.h1>

            <motion.h2 
              className={`text-3xl font-medium mb-6
                ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4 }}
            >
              {text.home.staticPhrase}{" "}
              <span className={`font-bold ${
                theme === "light" ? "text-light-accent" : "text-dark-accent"
              }`}>
                <Typewriter
                  words={text.home.typewriter}
                  loop={true}
                  cursor
                  cursorStyle="|"
                  typeSpeed={70}
                  deleteSpeed={50}
                  delaySpeed={1500}
                />
              </span>
            </motion.h2>

            <motion.p 
              className={`text-lg mb-8 leading-relaxed
                ${theme === "light" ? "text-gray-600" : "text-gray-400"}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6 }}
            >
              {text.home.intro}
            </motion.p>

            <motion.div 
              className="flex flex-wrap gap-4"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8 }}
            >
              <motion.a
                href="https://www.linkedin.com/in/yutaka-yamaguchi" 
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`group relative px-8 py-4 rounded-xl font-semibold overflow-hidden
                  ${theme === "light"
                    ? "bg-gradient-to-r from-light-accent to-orange-500 text-white"
                    : "bg-gradient-to-r from-dark-accent to-blue-500 text-white"
                  }`}
              >
                <div className="absolute inset-0 bg-white/0 group-hover:bg-white/10 transition-all duration-300" />
                <span className="relative flex items-center space-x-2">
                  <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M19 0h-14c-2.761 0-5 2.239-5 5v14c0 2.761 2.239 5 5 5h14c2.762 0 5-2.239 5-5v-14c0-2.761-2.238-5-5-5zm-11 19h-3v-11h3v11zm-1.5-12.268c-.966 0-1.75-.79-1.75-1.764s.784-1.764 1.75-1.764 1.75.79 1.75 1.764-.783 1.764-1.75 1.764zm13.5 12.268h-3v-5.604c0-.88-.018-2.013-1.227-2.013-1.227 0-1.415.957-1.415 1.947v5.67h-3v-11h2.85v1.634h.042c.494-.936 1.699-1.924 3.5-1.924 3.742 0 4.4 2.464 4.4 5.667v6.623z"/>
                  </svg>
                  {text.home.buttons.linkedin}
                </span>
              </motion.a>

              <motion.a
                href="https://github.com/yyamaguchi0102?tab=repositories"
                target="_blank"
                rel="noopener noreferrer"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`group relative px-8 py-4 rounded-xl font-semibold overflow-hidden
                  ${theme === "light"
                    ? "bg-gradient-to-r from-gray-700 to-gray-800 text-white"
                    : "bg-gradient-to-r from-gray-800 to-gray-900 text-white"
                  }`}
              >
                <div className="absolute inset-0 bg-white/0 group-hover:bg-white/10 transition-all duration-300" />
                <span className="relative flex items-center space-x-2">
                  <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                  </svg>
                  {text.home.buttons.github}
                </span>
              </motion.a>

              <motion.button
                onClick={toggleModal}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`group relative px-8 py-4 rounded-xl font-semibold overflow-hidden
                  ${theme === "light"
                    ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white"
                    : "bg-gradient-to-r from-blue-400 to-blue-500 text-white"
                  }`}
              >
                <div className="absolute inset-0 bg-white/0 group-hover:bg-white/10 transition-all duration-300" />
                <span className="relative flex items-center space-x-2">
                  <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 2c5.514 0 10 4.486 10 10s-4.486 10-10 10-10-4.486-10-10 4.486-10 10-10zm0-2c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm-1 6h2v8h-2v-8zm0 2h2v6h-2v-6z"/>
                  </svg>
                  {text.home.buttons.aboutMe}
                </span>
              </motion.button>
            </motion.div>
          </motion.div>

          {/* Right Column - Visual Element */}
          {/* Removed the visual element for a cleaner design */}
          {/* <motion.div
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            className="relative hidden lg:block"
          >
            <div className={`w-full h-[600px] rounded-3xl overflow-hidden
              ${theme === "light" ? "bg-white/50" : "bg-gray-800/50"}
              backdrop-blur-sm border border-white/20 shadow-2xl`}
            >
              <div className="absolute inset-0 flex items-center justify-center">
                <span className="text-9xl">👨‍💻</span>
              </div>
            </div>
          </motion.div> */}
        </div>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 flex items-center justify-center bg-black/50 backdrop-blur-sm z-50"
          onClick={toggleModal}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            className={`p-8 rounded-2xl shadow-2xl max-w-2xl w-full mx-4
              ${theme === "light" ? "bg-white" : "bg-gray-800"}
              border border-white/20`}
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className={`text-3xl font-bold mb-6
              ${theme === "light" ? "text-gray-900" : "text-white"}`}>
              {text.home.about.title}
            </h2>
            <p className={`leading-relaxed mb-6
              ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}>
              {text.home.about.content}
            </p>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={toggleModal}
              className={`px-6 py-3 rounded-xl font-medium
                ${theme === "light"
                  ? "bg-light-accent text-white hover:bg-orange-600"
                  : "bg-dark-accent text-black hover:bg-blue-700"
                }`}
            >
              {text.home.about.close}
            </motion.button>
          </motion.div>
        </motion.div>
      )}
    </section>
  );
};

export default Home;
