import { motion, AnimatePresence, useInView } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { useState, useRef, useEffect } from "react";
import { languages } from "../languages.js";

const TiltCard = ({ children, speed = 25, theme }) => {
  const cardRef = useRef(null);
  const [tiltStyle, setTiltStyle] = useState({
    transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
    transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
  });
  const [glarePosition, setGlarePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const cardRect = card.getBoundingClientRect();
    const cardCenterX = cardRect.left + cardRect.width / 2;
    const cardCenterY = cardRect.top + cardRect.height / 2;

    // Calculate cursor position relative to card center
    const cursorX = e.clientX;
    const cursorY = e.clientY;

    // Calculate rotation values - reduced intensity
    const rotateY = ((cursorX - cardCenterX) / (cardRect.width / 2)) * (speed / 10);
    const rotateX = -((cursorY - cardCenterY) / (cardRect.height / 2)) * (speed / 10);

    // Update glare position based on cursor
    const glareX = ((cursorX - cardRect.left) / cardRect.width) * 100;
    const glareY = ((cursorY - cardRect.top) / cardRect.height) * 100;

    setGlarePosition({ x: glareX, y: glareY });

    // Apply transformation - smaller scale effect
    setTiltStyle({
      transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale3d(1.01, 1.01, 1.01)`,
      transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setTiltStyle({
      transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
      transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  return (
    <div
      ref={cardRef}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={tiltStyle}
      className="relative h-full w-full"
    >
      {children}
      {isHovered && (
        <div
          className="absolute inset-0 pointer-events-none overflow-hidden rounded-xl opacity-20"
          style={{
            background: `radial-gradient(circle at ${glarePosition.x}% ${glarePosition.y}%, rgba(255, 255, 255, 0.8) 0%, transparent 50%)`,
          }}
        />
      )}
    </div>
  );
};

const ProficiencyBar = ({ level, theme }) => {
  // Convert proficiency level to percentage
  const getPercentage = (level) => {
    switch (level.toLowerCase()) {
      case 'native': return 100;
      case 'fluent': return 85;
      case 'advanced': return 75;
      case 'intermediate': return 60;
      case 'beginner': return 40;
      default: return 50;
    }
  };

  const percentage = getPercentage(level);

  return (
    <div className="w-full h-1.5 bg-gray-200/30 rounded-full mt-1 overflow-hidden">
      <motion.div 
        className={`h-full rounded-full ${
          theme === "light" 
            ? "bg-gradient-to-r from-rose-400 to-pink-500" 
            : "bg-gradient-to-r from-indigo-400 to-violet-500"
        }`}
        initial={{ width: 0 }}
        animate={{ width: `${percentage}%` }}
        transition={{ duration: 1, delay: 0.2 }}
      />
    </div>
  );
};

const Skills = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [clickedLanguageIndex, setClickedLanguageIndex] = useState(null);
  const [clickedProgrammingIndex, setClickedProgrammingIndex] = useState(null);
  const [clickedDatabaseIndex, setClickedDatabaseIndex] = useState(null);

  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  const containerVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        duration: 0.8, 
        staggerChildren: 0.3,
        delayChildren: 0.2,
        ease: "easeOut" 
      } 
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30, scale: 0.95 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { 
        duration: 0.7,
        type: "spring",
        stiffness: 50,
        damping: 15
      } 
    },
    hover: {
      scale: 1.02,
      y: -5,
      transition: { duration: 0.3, ease: "easeOut" }
    },
    tap: {
      scale: 0.98,
      transition: { duration: 0.1 }
    }
  };

  const spokenLanguages = [
    { name: currentLanguage.skills.languages.japanese, level: currentLanguage.skills.proficiency.native, icon: "https://img.icons8.com/color/48/japan.png" },
    { name: currentLanguage.skills.languages.english, level: currentLanguage.skills.proficiency.native, icon: "https://img.icons8.com/color/48/usa.png" },
    { name: currentLanguage.skills.languages.korean, level: currentLanguage.skills.proficiency.fluent, icon: "https://img.icons8.com/color/48/south-korea.png" },
  ];

  const programmingLanguages = [
    { name: "Java", icon: "https://img.icons8.com/color/48/java-coffee-cup-logo.png", tools: ["SpringBoot", "Maven"] },
    { name: "Python", icon: "https://img.icons8.com/color/48/python.png", tools: ["Pandas", "NumPy", "TensorFlow"] },
    { name: "JavaScript", icon: "https://img.icons8.com/color/48/javascript.png", tools: ["React", "Node.js"] },
    { name: "TypeScript", icon: "https://img.icons8.com/color/48/typescript.png", tools: ["Angular"] },
  ];

  const databases = [
    { name: "MySQL", icon: "https://img.icons8.com/color/48/mysql-logo.png", tools: ["Relational", "ACID"] },
    { name: "MongoDB", icon: "https://img.icons8.com/color/48/mongodb.png", tools: ["NoSQL", "Document"] },
    { name: "PostgreSQL", icon: "https://img.icons8.com/color/48/postgreesql.png", tools: ["Relational", "Advanced"] },
    { name: "Redis", icon: "https://img.icons8.com/color/48/redis.png", tools: ["In-Memory", "Caching"] },
  ];

  const handleLanguageTileClick = (index) => {
    setClickedLanguageIndex(index);
    setTimeout(() => setClickedLanguageIndex(null), 600);
  };

  const handleProgrammingTileClick = (index) => {
    setClickedProgrammingIndex(index);
    setTimeout(() => setClickedProgrammingIndex(null), 600);
  };

  const handleDatabaseTileClick = (index) => {
    setClickedDatabaseIndex(index);
    setTimeout(() => setClickedDatabaseIndex(null), 600);
  };

  const SkillTile = ({ item, index, type = "language" }) => {
    // Determine which click handler to use based on type
    const handleClick = () => {
      if (type === "language") handleLanguageTileClick(index);
      else if (type === "programming") handleProgrammingTileClick(index);
      else if (type === "database") handleDatabaseTileClick(index);
    };

    // Determine if this tile is currently clicked
    const isClicked = () => {
      if (type === "language") return clickedLanguageIndex === index;
      else if (type === "programming") return clickedProgrammingIndex === index;
      else if (type === "database") return clickedDatabaseIndex === index;
      return false;
    };

    return (
      <motion.div
        className={`relative rounded-xl cursor-pointer overflow-hidden h-full
          ${theme === "light" 
            ? "bg-white/80 shadow-[0_10px_25px_rgba(0,0,0,0.07)]" 
            : "bg-dark-card/80 shadow-[0_10px_25px_rgba(0,0,0,0.15)]"
          } backdrop-blur-sm border border-opacity-20
          ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
        variants={itemVariants}
        whileHover="hover"
        whileTap="tap"
        onClick={handleClick}
      >
        <TiltCard theme={theme}>
          <div className="p-4 relative z-10">
            <div className="flex items-center">
              <div className={`w-10 h-10 rounded-full p-2 flex-shrink-0
                ${theme === "light" 
                  ? "bg-gradient-to-br from-gray-50 to-white shadow-inner" 
                  : "bg-gradient-to-br from-dark-card to-dark-background shadow-inner"
                }`}>
                <img 
                  src={item.icon} 
                  alt={`${item.name} icon`} 
                  className={`w-full h-full object-contain transition-transform duration-300
                    ${isClicked() ? "scale-105" : ""}`}
                />
              </div>
              <div className="ml-3 flex-1">
                <h4 className={`text-sm font-semibold
                  ${theme === "dark" ? "text-dark-text" : "text-gray-800"}`}>
                  {item.name}
                </h4>
                {type === "language" ? (
                  <>
                    <p className={`text-xs font-medium
                      ${theme === "dark" ? "text-dark-muted" : "text-gray-600"}`}>
                      {item.level}
                    </p>
                    <ProficiencyBar level={item.level} theme={theme} />
                  </>
                ) : (
                  item.tools && (
                    <div className="flex flex-wrap gap-1 mt-1">
                      {item.tools.slice(0, 2).map((tool, idx) => (
                        <span
                          key={idx}
                          className={`px-1.5 py-0.5 text-xs rounded-full font-medium
                            ${theme === "dark" 
                              ? "bg-dark-border/50 text-dark-text" 
                              : "bg-gray-100 text-gray-700"
                            }`}
                        >
                          {tool}
                        </span>
                      ))}
                    </div>
                  )
                )}
              </div>
            </div>
          </div>
        </TiltCard>
        <motion.div 
          className={`absolute inset-0 rounded-xl opacity-0 transition-all duration-300
            ${theme === "dark" 
              ? "bg-gradient-to-br from-indigo-500/5 to-violet-500/5" 
              : "bg-gradient-to-br from-rose-500/5 to-pink-500/5"
            }`}
          animate={{
            opacity: isClicked() ? 1 : 0
          }}
        />
      </motion.div>
    );
  };

  return (
    <motion.section
      ref={ref}
      className="scroll-offset py-20 pt-16 px-6 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }} 
      animate={isInView ? "visible" : "hidden"}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        <motion.div
          className="text-center mb-2"
          variants={itemVariants}
        >
          <motion.h2 
            className={`text-3xl font-bold inline ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
            initial={{ opacity: 0, filter: "blur(8px)" }}
            animate={isInView ? { opacity: 1, filter: "blur(0px)" } : {}}
            transition={{ duration: 1, delay: 0.5 }}
          >
            {currentLanguage.skills.title}
          </motion.h2>
        </motion.div>
        <motion.p
          className="text-base text-center mb-6 max-w-3xl mx-auto"
          variants={itemVariants}
        >
          {currentLanguage.skills.description}
        </motion.p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Spoken Languages */}
          <motion.div
            variants={containerVariants}
          >
            <motion.h3
              className={`text-xl font-semibold mb-3 ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
              variants={itemVariants}
            >
              {currentLanguage.skills.sections.spokenLanguages}
            </motion.h3>
            <motion.div
              className="grid grid-cols-1 gap-3"
              variants={containerVariants}
            >
              {spokenLanguages.map((lang, index) => (
                <motion.div
                  key={index}
                  custom={index}
                  variants={{
                    hidden: { 
                      opacity: 0, 
                      y: 20,
                      scale: 0.9
                    },
                    visible: i => ({ 
                      opacity: 1, 
                      y: 0,
                      scale: 1,
                      transition: { 
                        duration: 0.6, 
                        delay: i * 0.1,
                        type: "spring", 
                        damping: 12
                      } 
                    })
                  }}
                >
                  <SkillTile item={lang} index={index} type="language" />
                </motion.div>
              ))}
            </motion.div>
          </motion.div>

          {/* Programming Languages */}
          <motion.div variants={containerVariants}>
            <motion.h3
              className={`text-xl font-semibold mb-3 ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
              variants={itemVariants}
            >
              {currentLanguage.skills.sections.programmingStack}
            </motion.h3>
            <motion.div
              className="grid grid-cols-1 gap-3"
              variants={containerVariants}
            >
              {programmingLanguages.map((lang, index) => (
                <motion.div
                  key={index}
                  custom={index}
                  variants={{
                    hidden: { 
                      opacity: 0, 
                      y: 20,
                      scale: 0.9
                    },
                    visible: i => ({ 
                      opacity: 1, 
                      y: 0,
                      scale: 1,
                      transition: { 
                        duration: 0.6, 
                        delay: i * 0.1,
                        type: "spring", 
                        damping: 12
                      } 
                    })
                  }}
                >
                  <SkillTile item={lang} index={index} type="programming" />
                </motion.div>
              ))}
            </motion.div>
          </motion.div>

          {/* Databases */}
          <motion.div variants={containerVariants}>
            <motion.h3
              className={`text-xl font-semibold mb-3 ${theme === "dark" ? "text-indigo-400" : "text-rose-500"}`}
              variants={itemVariants}
            >
              Databases
            </motion.h3>
            <motion.div
              className="grid grid-cols-1 gap-3"
              variants={containerVariants}
            >
              {databases.map((db, index) => (
                <motion.div
                  key={index}
                  custom={index}
                  variants={{
                    hidden: { 
                      opacity: 0, 
                      y: 20,
                      scale: 0.9
                    },
                    visible: i => ({ 
                      opacity: 1, 
                      y: 0,
                      scale: 1,
                      transition: { 
                        duration: 0.6, 
                        delay: i * 0.1,
                        type: "spring", 
                        damping: 12
                      } 
                    })
                  }}
                >
                  <SkillTile item={db} index={index} type="database" />
                </motion.div>
              ))}
            </motion.div>
          </motion.div>
        </div>
      </div>
    </motion.section>
  );
};

export default Skills;
