import { motion, AnimatePresence, useInView } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { useState, useRef, useEffect } from "react";
import { languages } from "../languages.js";

const TiltCard = ({ children, speed = 25, theme }) => {
  const cardRef = useRef(null);
  const [tiltStyle, setTiltStyle] = useState({
    transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
    transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
  });
  const [glarePosition, setGlarePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const cardRect = card.getBoundingClientRect();
    const cardCenterX = cardRect.left + cardRect.width / 2;
    const cardCenterY = cardRect.top + cardRect.height / 2;

    // Calculate cursor position relative to card center
    const cursorX = e.clientX;
    const cursorY = e.clientY;

    // Calculate rotation values - reduced intensity
    const rotateY = ((cursorX - cardCenterX) / (cardRect.width / 2)) * (speed / 10);
    const rotateX = -((cursorY - cardCenterY) / (cardRect.height / 2)) * (speed / 10);

    // Update glare position based on cursor
    const glareX = ((cursorX - cardRect.left) / cardRect.width) * 100;
    const glareY = ((cursorY - cardRect.top) / cardRect.height) * 100;

    setGlarePosition({ x: glareX, y: glareY });

    // Apply transformation - smaller scale effect
    setTiltStyle({
      transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale3d(1.02, 1.02, 1.02)`,
      transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setTiltStyle({
      transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
      transition: 'all 0.3s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  return (
    <div
      ref={cardRef}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={tiltStyle}
      className="relative h-full w-full"
    >
      {children}
      {isHovered && (
        <div
          className="absolute inset-0 pointer-events-none overflow-hidden rounded-xl opacity-25"
          style={{
            background: `radial-gradient(circle at ${glarePosition.x}% ${glarePosition.y}%, ${theme === "light" ? "rgba(255, 255, 255, 0.9)" : "rgba(255, 255, 255, 0.5)"} 0%, transparent 60%)`,
          }}
        />
      )}
    </div>
  );
};

// Progress bar component for language proficiency
const ProficiencyBar = ({ level, theme }) => {
  // Map proficiency level to percentage
  const getPercentage = (level) => {
    switch(level) {
      case "Native":
      case "ネイティブ":
      case "원어민":
        return "95%";
      case "Fluent":
      case "流暢":
      case "유창함":
        return "80%";
      case "Intermediate":
      case "中級":
      case "중급":
        return "60%";
      case "Beginner":
      case "初級":
      case "초급":
        return "40%";
      default:
        return "50%";
    }
  };

  return (
    <div className="w-full h-1.5 bg-gray-200 dark:bg-gray-700 rounded-full overflow-hidden mt-2">
      <motion.div
        initial={{ width: 0 }}
        animate={{ width: getPercentage(level) }}
        transition={{ duration: 1, delay: 0.2, ease: "easeOut" }}
        className={`h-full rounded-full ${
          theme === "light" 
            ? "bg-gradient-to-r from-indigo-500 to-violet-500" 
            : "bg-gradient-to-r from-indigo-400 to-violet-400"
        }`}
      />
    </div>
  );
};

const Skills = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [clickedLanguageIndex, setClickedLanguageIndex] = useState(null);
  const [clickedProgrammingIndex, setClickedProgrammingIndex] = useState(null);

  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  const containerVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        duration: 0.8, 
        staggerChildren: 0.3,
        delayChildren: 0.2,
        ease: "easeOut" 
      } 
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30, scale: 0.95 },
    visible: { 
      opacity: 1, 
      y: 0, 
      scale: 1,
      transition: { 
        duration: 0.7,
        type: "spring",
        stiffness: 50,
        damping: 15
      } 
    },
    hover: {
      scale: 1.03,
      y: -5,
      transition: { duration: 0.3, ease: "easeOut" }
    },
    tap: {
      scale: 0.98,
      transition: { duration: 0.1 }
    }
  };

  const spokenLanguages = [
    { name: currentLanguage.skills.languages.japanese, level: currentLanguage.skills.proficiency.native, icon: "https://img.icons8.com/color/48/japan.png" },
    { name: currentLanguage.skills.languages.english, level: currentLanguage.skills.proficiency.native, icon: "https://img.icons8.com/color/48/usa.png" },
    { name: currentLanguage.skills.languages.korean, level: currentLanguage.skills.proficiency.fluent, icon: "https://img.icons8.com/color/48/south-korea.png" },
  ];

  const programmingLanguages = [
    { name: "Java", icon: "https://img.icons8.com/color/48/java-coffee-cup-logo.png", tools: ["SpringBoot", "Maven"], level: 90 },
    { name: "Python", icon: "https://img.icons8.com/color/48/python.png", tools: ["Pandas", "NumPy", "MongoDB", "TensorFlow", "PyCharm"], level: 85 },
    { name: "Kotlin", icon: "https://img.icons8.com/color/48/kotlin.png", tools: ["Ktor"], level: 80 },
    { name: "SQL", icon: "https://img.icons8.com/color/48/sql.png", tools: ["MySQL", "NoSQL"], level: 85 },
    { name: "JavaScript", icon: "https://img.icons8.com/color/48/javascript.png", tools: ["React.js", "Node.js"], level: 90 },
    { name: "TypeScript", icon: "https://img.icons8.com/color/48/typescript.png", tools: ["Angular"], level: 85 },
  ];

  const handleLanguageTileClick = (index) => {
    setClickedLanguageIndex(index);
    setTimeout(() => setClickedLanguageIndex(null), 600);
  };

  const handleProgrammingTileClick = (index) => {
    setClickedProgrammingIndex(index);
    setTimeout(() => setClickedProgrammingIndex(null), 600);
  };

  const LanguageTile = ({ item, index }) => (
    <motion.div
      className={`relative rounded-xl overflow-hidden h-full cursor-pointer
        ${theme === "light" 
          ? "bg-white shadow-lg border border-gray-100" 
          : "bg-slate-800 shadow-lg border border-slate-700/50"
        }`}
      variants={itemVariants}
      whileHover="hover"
      whileTap="tap"
      onClick={() => handleLanguageTileClick(index)}
    >
      <TiltCard theme={theme}>
        <div className="p-5 relative z-10">
          <div className="flex items-center mb-3">
            <div className={`w-12 h-12 rounded-full p-2 mr-4 flex-shrink-0
              ${theme === "light" 
                ? "bg-gradient-to-br from-gray-50 to-gray-100 shadow-sm" 
                : "bg-gradient-to-br from-slate-700 to-slate-900 shadow-sm"
              }`}>
              <img 
                src={item.icon} 
                alt={`${item.name} icon`} 
                className={`w-full h-full object-contain transition-transform duration-300
                  ${clickedLanguageIndex === index ? "scale-105" : ""}`}
              />
            </div>
            <div>
              <h4 className={`text-lg font-semibold
                ${theme === "light" ? "text-slate-800" : "text-white"}`}>
                {item.name}
              </h4>
              <p className={`text-sm font-medium 
                ${theme === "light" ? "text-slate-500" : "text-slate-400"}`}>
                {item.level}
              </p>
            </div>
          </div>

          <ProficiencyBar level={item.level} theme={theme} />
        </div>
      </TiltCard>

      {/* Highlight effect on click */}
      <motion.div 
        className={`absolute inset-0 opacity-0 transition-all duration-300
          ${theme === "light" 
            ? "bg-gradient-to-br from-indigo-500/10 to-violet-500/10" 
            : "bg-gradient-to-br from-indigo-500/15 to-violet-500/15"
          }`}
        animate={{
          opacity: clickedLanguageIndex === index ? 1 : 0
        }}
      />
    </motion.div>
  );

  const ProgrammingTile = ({ item, index }) => (
    <motion.div
      className={`relative rounded-xl overflow-hidden h-full cursor-pointer
        ${theme === "light" 
          ? "bg-white shadow-lg border border-gray-100" 
          : "bg-slate-800 shadow-lg border border-slate-700/50"
        }`}
      variants={itemVariants}
      whileHover="hover"
      whileTap="tap"
      onClick={() => handleProgrammingTileClick(index)}
    >
      <TiltCard theme={theme}>
        <div className="p-5 relative z-10">
          <div className="flex items-center mb-3">
            <div className={`w-12 h-12 rounded-full p-2 mr-3 flex-shrink-0
              ${theme === "light" 
                ? "bg-gradient-to-br from-gray-50 to-gray-100 shadow-sm" 
                : "bg-gradient-to-br from-slate-700 to-slate-900 shadow-sm"
              }`}>
              <img 
                src={item.icon} 
                alt={`${item.name} icon`} 
                className={`w-full h-full object-contain transition-transform duration-300
                  ${clickedProgrammingIndex === index ? "scale-105" : ""}`}
              />
            </div>
            <div>
              <h4 className={`text-lg font-semibold
                ${theme === "light" ? "text-slate-800" : "text-white"}`}>
                {item.name}
              </h4>
            </div>
          </div>

          {/* Skill level bar */}
          <div className="mb-3">
            <div className="flex justify-between items-center mb-1">
              <span className={`text-xs font-medium ${theme === "light" ? "text-slate-500" : "text-slate-400"}`}>
                Proficiency
              </span>
              <span className={`text-xs font-medium ${theme === "light" ? "text-indigo-600" : "text-indigo-400"}`}>
                {item.level}%
              </span>
            </div>
            <div className="w-full h-1.5 bg-gray-200 dark:bg-slate-700 rounded-full overflow-hidden">
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${item.level}%` }}
                transition={{ duration: 1, delay: 0.2, ease: "easeOut" }}
                className={`h-full rounded-full ${
                  theme === "light" 
                    ? "bg-gradient-to-r from-indigo-500 to-violet-500" 
                    : "bg-gradient-to-r from-indigo-400 to-violet-400"
                }`}
              />
            </div>
          </div>

          {/* Tools & frameworks */}
          {item.tools && (
            <div className="flex flex-wrap gap-1.5 mt-3">
              {item.tools.map((tool, idx) => (
                <span
                  key={idx}
                  className={`px-2 py-0.5 text-xs rounded-full font-medium
                    ${theme === "light" 
                      ? "bg-slate-100 text-slate-700 border border-slate-200" 
                      : "bg-slate-700 text-slate-300 border border-slate-600"
                    }`}
                >
                  {tool}
                </span>
              ))}
            </div>
          )}
        </div>
      </TiltCard>

      {/* Highlight effect on click */}
      <motion.div 
        className={`absolute inset-0 opacity-0 transition-all duration-300
          ${theme === "light" 
            ? "bg-gradient-to-br from-indigo-500/10 to-violet-500/10" 
            : "bg-gradient-to-br from-indigo-500/15 to-violet-500/15"
          }`}
        animate={{
          opacity: clickedProgrammingIndex === index ? 1 : 0
        }}
      />
    </motion.div>
  );

  return (
    <motion.section
      id="skills"
      ref={ref}
      className="py-24 px-4 sm:px-6 lg:px-8 relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }} 
      animate={isInView ? "visible" : "hidden"}
      variants={containerVariants}
    >
      {/* Background decorations */}
      <div className="absolute top-0 right-0 -mr-20 -mt-20 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-br from-indigo-600 to-violet-600" />
      <div className="absolute bottom-0 left-0 -ml-20 -mb-20 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-tr from-violet-600 to-indigo-600" />

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section header */}
        <motion.div
          className="text-center mb-12"
          variants={itemVariants}
        >
          <span className={`inline-block px-3 py-1 text-sm font-medium rounded-full mb-4 
            ${theme === "light" 
              ? "bg-indigo-100 text-indigo-800" 
              : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
            }`}>
            {currentLanguage.skills.title}
          </span>
          <motion.h2 
            className={`text-3xl sm:text-4xl font-bold mb-4 inline-block ${
              theme === "light" ? "text-slate-900" : "text-white"
            }`}
            initial={{ opacity: 0, filter: "blur(8px)" }}
            animate={isInView ? { opacity: 1, filter: "blur(0px)" } : {}}
            transition={{ duration: 1, delay: 0.5 }}
          >
            <span className={theme === "light" ? "text-indigo-600" : "text-indigo-400"}>
              Multi-
            </span>
            lingual Developer
          </motion.h2>
          <motion.p
            className={`text-lg max-w-2xl mx-auto ${
              theme === "light" ? "text-slate-600" : "text-slate-300"
            }`}
            variants={itemVariants}
          >
            {currentLanguage.skills.description}
          </motion.p>
        </motion.div>

        {/* Spoken Languages */}
        <motion.div
          className="mb-16"
          variants={containerVariants}
        >
          <motion.div className="flex items-center mb-6" variants={itemVariants}>
            <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-3 ${
              theme === "light" 
                ? "bg-indigo-100 text-indigo-600" 
                : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
            }`}>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5h12M9 3v2m1.048 9.5A18.022 18.022 0 016.412 9m6.088 9h7M11 21l5-10 5 10M12.751 5C11.783 10.77 8.07 15.61 3 18.129" />
              </svg>
            </div>
            <h3
              className={`text-2xl font-bold ${
                theme === "light" ? "text-slate-900" : "text-white"
              }`}
            >
              {currentLanguage.skills.sections.spokenLanguages}
            </h3>
          </motion.div>

          <motion.div
            className="grid grid-cols-1 md:grid-cols-3 gap-5"
            variants={containerVariants}
          >
            {spokenLanguages.map((lang, index) => (
              <motion.div
                key={index}
                custom={index}
                variants={{
                  hidden: { 
                    opacity: 0, 
                    y: 50,
                    scale: 0.9
                  },
                  visible: i => ({ 
                    opacity: 1, 
                    y: 0,
                    scale: 1,
                    transition: { 
                      duration: 0.8, 
                      delay: i * 0.2,
                      type: "spring", 
                      damping: 12
                    } 
                  })
                }}
              >
                <LanguageTile item={lang} index={index} />
              </motion.div>
            ))}
          </motion.div>
        </motion.div>

        {/* Programming Stack */}
        <motion.div variants={containerVariants}>
          <motion.div className="flex items-center mb-6" variants={itemVariants}>
            <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-3 ${
              theme === "light" 
                ? "bg-indigo-100 text-indigo-600" 
                : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
            }`}>
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
              </svg>
            </div>
            <h3
              className={`text-2xl font-bold ${
                theme === "light" ? "text-slate-900" : "text-white"
              }`}
            >
              {currentLanguage.skills.sections.programmingStack}
            </h3>
          </motion.div>

          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5"
            variants={containerVariants}
          >
            {programmingLanguages.map((lang, index) => (
              <motion.div
                key={index}
                custom={index}
                variants={{
                  hidden: { 
                    opacity: 0, 
                    y: 50,
                    scale: 0.9
                  },
                  visible: i => ({ 
                    opacity: 1, 
                    y: 0,
                    scale: 1,
                    transition: { 
                      duration: 0.8, 
                      delay: i * 0.1,
                      type: "spring", 
                      damping: 12
                    } 
                  })
                }}
              >
                <ProgrammingTile item={lang} index={index} />
              </motion.div>
            ))}
          </motion.div>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Skills;
