import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";

const Projects = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        staggerChildren: 0.3,
        duration: 0.8,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { duration: 1 },
    },
    hover: {
      scale: 1.03,
      y: -8,
      transition: { duration: 0.3, ease: "easeOut" }
    },
    tap: {
      scale: 0.97,
      transition: { duration: 0.1 }
    }
  };

  const handleDemoClick = (project) => {
    if (!project.demoUrl || project.demoUrl === "#") {
      setAlertMessage("Live demo coming soon!");
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 2500);
      return;
    }
    window.open(project.demoUrl, '_blank');
  };

  return (
    <motion.section
      id="projects"
      className="min-h-screen py-24 px-8 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      {/* Alert Notification */}
      <AnimatePresence>
        {showAlert && (
          <motion.div 
            className={`fixed z-50 top-24 left-1/2 transform -translate-x-1/2 py-3 px-6 rounded-xl shadow-lg
              ${theme === "light" 
                ? "bg-white text-gray-800 border border-light-accent/20" 
                : "bg-gray-800 text-white border border-dark-accent/20"
              } backdrop-blur-md`}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
          >
            <div className="flex items-center space-x-3">
              <svg className={`w-5 h-5 ${theme === "light" ? "text-orange-500" : "text-blue-400"}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span className="font-medium">{alertMessage}</span>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      <div className="max-w-7xl mx-auto">
        <motion.h2
          className="text-3xl font-bold mb-6 text-center"
          variants={itemVariants}
        >
          {text.projects.title}
        </motion.h2>
        <motion.p className="text-center mb-10" variants={itemVariants}>
          {text.projects.description}
        </motion.p>
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          variants={containerVariants}
        >
          {text.projects.items.map((project, index) => (
            <motion.div
              key={index}
              className={`group relative overflow-hidden rounded-2xl 
                ${theme === "light" 
                  ? "bg-gradient-to-br from-white to-gray-50 shadow-[0_15px_35px_rgba(0,0,0,0.1)]" 
                  : "bg-gradient-to-br from-gray-800/90 to-gray-900/90 shadow-[0_15px_35px_rgba(0,0,0,0.3)]"
                } backdrop-blur-sm border border-opacity-20
                ${theme === "light" ? "border-gray-200" : "border-gray-700"}`}
              variants={itemVariants}
              whileHover="hover"
              whileTap="tap"
            >
              {/* Card Content */}
              <div className="p-6 relative z-10">
                {/* Title with decorative element */}
                <div className="relative mb-5">
                  <div className={`absolute -left-3 top-0 h-full w-1 rounded-full 
                    ${theme === "light" 
                      ? "bg-gradient-to-b from-light-accent to-orange-400" 
                      : "bg-gradient-to-b from-dark-accent to-blue-500"
                    }`} 
                  />
                  <h3 className={`text-xl font-bold ml-1
                    ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}>
                    {project.name}
                  </h3>
                </div>

                {/* Description */}
                <p className={`mb-5 leading-relaxed text-sm
                  ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}>
                  {project.description}
                </p>

                {/* Technology tags */}
                <div className="mt-6">
                  <h4 className={`text-sm font-medium mb-3 flex items-center
                    ${theme === "light" ? "text-gray-700" : "text-gray-200"}`}>
                    <svg className="w-4 h-4 mr-2 opacity-70" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M2 5a2 2 0 012-2h12a2 2 0 012 2v10a2 2 0 01-2 2H4a2 2 0 01-2-2V5zm3.293 1.293a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 01-1.414-1.414L7.586 10 5.293 7.707a1 1 0 010-1.414zM11 12a1 1 0 100 2h3a1 1 0 100-2h-3z" clipRule="evenodd" />
                    </svg>
                    {text.projects.technologiesUsed}
                  </h4>
                  <div className="flex flex-wrap gap-2">
                    {(project.tech || []).map((tech, idx) => (
                      <span
                        key={idx}
                        className={`px-3 py-1 text-xs rounded-full font-medium inline-flex items-center
                          ${theme === "light" 
                            ? "bg-gray-100 text-gray-700" 
                            : "bg-dark-border/50 text-gray-200"
                          }`}
                      >
                        <span className={`w-1.5 h-1.5 rounded-full mr-1.5 
                          ${theme === "light" ? "bg-light-accent" : "bg-dark-accent"}`} 
                        />
                        {tech}
                      </span>
                    ))}
                  </div>
                </div>

                {/* Action buttons */}
                <div className="flex justify-between mt-8 pt-4 border-t border-gray-200/30">
                  <button
                    onClick={() => handleDemoClick(project)}
                    className={`px-4 py-2.5 rounded-xl font-medium transition-all duration-300 flex items-center space-x-2
                      ${theme === "light"
                        ? "bg-gradient-to-r from-light-accent to-orange-500 text-white hover:shadow-lg hover:shadow-orange-500/20"
                        : "bg-gradient-to-r from-dark-accent to-blue-500 text-white hover:shadow-lg hover:shadow-blue-500/20"
                      }`}
                  >
                    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                      <path d="M10 12a2 2 0 100-4 2 2 0 000 4z" />
                      <path fillRule="evenodd" d="M.458 10C1.732 5.943 5.522 3 10 3s8.268 2.943 9.542 7c-1.274 4.057-5.064 7-9.542 7S1.732 14.057.458 10zM14 10a4 4 0 11-8 0 4 4 0 018 0z" clipRule="evenodd" />
                    </svg>
                    <span>{project.demo}</span>
                  </button>
                  <a
                    href={project.repoUrl || "#"}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`px-4 py-2.5 rounded-xl font-medium transition-all duration-300 flex items-center space-x-2
                      ${theme === "light"
                        ? "bg-gradient-to-r from-gray-100 to-white text-gray-800 hover:shadow-lg hover:shadow-gray-200 border border-gray-200"
                        : "bg-gradient-to-r from-gray-800 to-gray-700 text-gray-200 hover:shadow-lg hover:shadow-gray-800/20 border border-gray-700"
                      }`}
                  >
                    <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M12.316 3.051a1 1 0 01.633 1.265l-4 12a1 1 0 11-1.898-.632l4-12a1 1 0 011.265-.633zM5.707 6.293a1 1 0 010 1.414L3.414 10l2.293 2.293a1 1 0 11-1.414 1.414l-3-3a1 1 0 010-1.414l3-3a1 1 0 011.414 0zm8.586 0a1 1 0 011.414 0l3 3a1 1 0 010 1.414l-3 3a1 1 0 11-1.414-1.414L16.586 10l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                    </svg>
                    <span>{project.repo}</span>
                  </a>
                </div>
              </div>

              {/* Animated Background Gradient on Hover */}
              <div 
                className={`absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-2xl
                  ${theme === "light" 
                    ? "bg-gradient-to-br from-orange-50/50 to-light-accent/5" 
                    : "bg-gradient-to-br from-dark-accent/5 to-blue-900/10"
                  }`}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Projects;
