import React, { useState, useEffect } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';

const PageTransition = ({ children }) => {
  const [isLoading, setIsLoading] = useState(true);
  const { theme } = useTheme();

  useEffect(() => {
    // Use a shorter loading time for better UX
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 800);

    return () => clearTimeout(timer);
  }, []);

  // Number of tiles for the grid transition
  const rows = 6;
  const columns = 6;

  // Create array for grid cells
  const gridCells = Array.from({ length: rows * columns }, (_, i) => i);

  return (
    <AnimatePresence mode="wait">
      {isLoading ? (
        <motion.div
          key="loader"
          className={`fixed inset-0 z-50 overflow-hidden
            ${theme === 'light' 
              ? 'bg-gradient-to-br from-rose-50 via-white to-amber-50' 
              : 'bg-gradient-to-br from-slate-900 via-gray-900 to-indigo-900'}`}
          exit={{ 
            opacity: 1,
            transition: { duration: 0.6 }
          }}
        >
          {/* Grid Transition */}
          <div className="absolute inset-0 grid" 
            style={{ 
              gridTemplateColumns: `repeat(${columns}, 1fr)`,
              gridTemplateRows: `repeat(${rows}, 1fr)`
            }}>
            {gridCells.map((cell, i) => (
              <motion.div
                key={i}
                className={`${theme === 'light' 
                  ? 'bg-rose-500' 
                  : 'bg-indigo-500'}`}
                initial={{ opacity: 0 }}
                exit={{
                  opacity: 1,
                  transition: {
                    duration: 0.4,
                    delay: 0.1 + (i % columns * 0.05) + (Math.floor(i / columns) * 0.05)
                  }
                }}
              />
            ))}
          </div>

          {/* Animated Text */}
          <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
            <motion.div
              className="relative"
              initial={{ opacity: 1 }}
              exit={{ 
                opacity: 0,
                transition: { duration: 0.2, delay: 0.3 }
              }}
            >
              <motion.div 
                className={`text-4xl font-bold ${theme === 'light' ? 'text-white' : 'text-white'}`}
                initial={{ filter: "blur(8px)", opacity: 0, scale: 1.5 }}
                animate={{ 
                  filter: "blur(0px)", 
                  opacity: 1, 
                  scale: 1,
                  transition: { duration: 0.5, ease: "easeOut" } 
                }}
                exit={{ 
                  filter: "blur(8px)", 
                  opacity: 0, 
                  scale: 0.8,
                  transition: { duration: 0.3 } 
                }}
              >
                {theme === 'light' ? 'Welcome' : 'Welcome'}
              </motion.div>

              {/* Animated particles that fly out */}
              <div className="absolute inset-0 flex items-center justify-center">
                {[...Array(12)].map((_, i) => (
                  <motion.div
                    key={i}
                    className={`absolute w-2 h-2 rounded-full ${
                      theme === 'light' ? 'bg-rose-300' : 'bg-indigo-300'
                    }`}
                    initial={{ x: 0, y: 0, opacity: 0 }}
                    animate={{ 
                      x: (Math.random() - 0.5) * 200, 
                      y: (Math.random() - 0.5) * 200,
                      opacity: [0, 1, 0],
                      scale: [0, 1.5, 0]
                    }}
                    transition={{ 
                      duration: 1.2,
                      delay: 0.1 + i * 0.05,
                      ease: "easeOut" 
                    }}
                  />
                ))}
              </div>
            </motion.div>
          </div>
        </motion.div>
      ) : (
        <motion.div
          key="content"
          className="w-full"
          initial={{ opacity: 0 }}
          animate={{ 
            opacity: 1,
            transition: {
              duration: 0.5,
              ease: "easeOut"
            }
          }}
        >
          {children}
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default PageTransition;
