import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

// Improved project card with 3D tilt effect
const ProjectCard = ({ children, onClick, theme }) => {
  const [isHovered, setIsHovered] = useState(false);
  const cardRef = useRef(null);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const rect = card.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    const rotateY = (x - centerX) / 20;
    const rotateX = (centerY - y) / 20;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotation({ x: 0, y: 0 });
  };

  return (
    <motion.div
      ref={cardRef}
      className="relative h-full w-full rounded-xl overflow-hidden cursor-pointer"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={handleMouseLeave}
      onMouseMove={handleMouseMove}
      onClick={onClick}
      style={{
        transformStyle: "preserve-3d",
        transform: `perspective(1000px) rotateX(${rotation.x}deg) rotateY(${rotation.y}deg)`,
        transition: "transform 0.2s ease-out"
      }}
      whileHover={{ 
        y: -6,
        transition: { duration: 0.3, ease: "easeOut" }
      }}
    >
      {children}

      {/* Shimmer effect only visible on hover */}
      {isHovered && (
        <motion.div 
          className="absolute inset-0 pointer-events-none"
          initial={{ opacity: 0 }}
          animate={{ 
            opacity: 1,
            backgroundPosition: ["200% 0%", "-200% 0%"]
          }}
          style={{
            background: `linear-gradient(to right, transparent, ${theme === "light" ? "rgba(255, 255, 255, 0.2)" : "rgba(255, 255, 255, 0.1)"}, transparent)`,
            backgroundSize: "400% 100%",
          }}
          transition={{
            backgroundPosition: {
              duration: 2.5,
              repeat: Infinity,
              ease: "linear",
              repeatType: "loop"
            }
          }}
        />
      )}
    </motion.div>
  );
};

const Projects = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [clickedIndex, setClickedIndex] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");
  const popupTimeoutRef = useRef(null);

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        staggerChildren: 0.3, // Faster staggering
        delayChildren: 0.1,
        duration: 0.6,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.98 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { 
        duration: 0.5,
        type: "spring",
        stiffness: 100,
        damping: 15
      },
    },
    tap: {
      scale: 0.97,
      transition: { duration: 0.1 }
    }
  };

  const handleDemoClick = (e) => {
    e.preventDefault();
    e.stopPropagation();

    // Clear any existing timeout
    if (popupTimeoutRef.current) {
      clearTimeout(popupTimeoutRef.current);
    }

    setPopupMessage("Live Demo Coming Soon!");
    setShowPopup(true);
    popupTimeoutRef.current = setTimeout(() => {
      setShowPopup(false);
      popupTimeoutRef.current = null;
    }, 2000);
  };

  const handleRepoClick = (project, e) => {
    e.preventDefault();
    e.stopPropagation();

    // Check if the project has a repo URL defined
    if (!project.repoUrl || project.repoUrl === "#" || project.repoUrl === "") {
      // Clear any existing timeout
      if (popupTimeoutRef.current) {
        clearTimeout(popupTimeoutRef.current);
      }

      setPopupMessage("Repository Coming Soon!");
      setShowPopup(true);
      popupTimeoutRef.current = setTimeout(() => {
        setShowPopup(false);
        popupTimeoutRef.current = null;
      }, 2000);
      return;
    }

    // Make sure URL has proper protocol
    let url = project.repoUrl;
    if (!url.startsWith('http://') && !url.startsWith('https://')) {
      url = 'https://' + url;
    }

    window.open(url, '_blank');
  };

  const handleCardClick = (index) => {
    if (clickedIndex === index) {
      setClickedIndex(null);
      return;
    }

    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  // Improved Popup component
  const Popup = () => (
    <motion.div 
      className="fixed top-0 left-0 right-0 flex justify-center items-start z-50 mt-24 px-4"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ type: "spring", stiffness: 300, damping: 25 }}
    >
      <motion.div 
        className={`px-6 py-3 rounded-lg shadow-xl backdrop-blur-sm ${
          theme === "light" 
            ? "bg-gradient-to-r from-indigo-500 to-violet-600 text-white" 
            : "bg-gradient-to-r from-indigo-600 to-violet-700 text-white"
        }`}
        initial={{ scale: 0.9 }}
        animate={{ scale: 1 }}
        exit={{ scale: 0.9 }}
      >
        <div className="flex items-center space-x-2">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
          </svg>
          <p className="font-medium">{popupMessage}</p>
        </div>
      </motion.div>
    </motion.div>
  );

  return (
    <motion.section
      id="projects"
      className="py-24 px-4 sm:px-6 lg:px-8 relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
      variants={containerVariants}
    >
      {/* Background decorations */}
      <div className="absolute -left-20 -bottom-20 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-tr from-indigo-600 to-violet-600" />
      <div className="absolute -right-20 top-1/2 -translate-y-1/2 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-br from-violet-600 to-indigo-600" />

      {/* Alert Notification */}
      <AnimatePresence>
        {showAlert && (
          <motion.div 
            className={`fixed z-50 top-24 left-1/2 -translate-x-1/2 py-3 px-6 rounded-xl shadow-lg
              ${theme === "light" 
                ? "bg-white/90 text-slate-800 border border-indigo-500/30" 
                : "bg-slate-800/90 text-white border border-indigo-500/30"
              } backdrop-blur-md flex items-center space-x-3 min-w-[220px] justify-center`}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5, type: 'spring', stiffness: 300 }}
          >
            <svg className={`w-5 h-5 ${theme === "light" ? "text-indigo-500" : "text-indigo-400"}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
            </svg>
            <span className="font-medium">{alertMessage}</span>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Popup for coming soon features */}
      <AnimatePresence>
        {showPopup && <Popup />}
      </AnimatePresence>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section header */}
        <motion.div className="text-center mb-12" variants={itemVariants}>
          <span className={`inline-block px-3 py-1 text-sm font-medium rounded-full mb-4 
            ${theme === "light" 
              ? "bg-indigo-100 text-indigo-800" 
              : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
            }`}>
            {currentLanguage.projects.title}
          </span>
          <h2 className={`text-3xl sm:text-4xl font-bold mb-4 ${
            theme === "light" ? "text-slate-900" : "text-white"
          }`}>
            My <span className={theme === "light" ? "text-indigo-600" : "text-indigo-400"}>
              Recent Work
            </span>
          </h2>
          <p className={`text-lg max-w-2xl mx-auto ${
            theme === "light" ? "text-slate-600" : "text-slate-300"
          }`}>
            {currentLanguage.projects.description}
          </p>
        </motion.div>

        <motion.div
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
          variants={containerVariants}
        >
          {currentLanguage.projects.items.map((project, index) => (
            <motion.div
              key={index}
              custom={index}
              variants={itemVariants}
              whileTap="tap"
              className={`h-full transform-gpu`}
            >
              <ProjectCard onClick={() => handleCardClick(index)} theme={theme}>
                {/* Card Content */}
                <div className={`h-full relative z-10 border rounded-xl overflow-hidden ${
                  theme === "light" 
                    ? "bg-white border-gray-100 shadow-lg" 
                    : "bg-slate-800 border-slate-700/50 shadow-lg"
                }`}>
                  {/* Project image or gradient background */}
                  <div className={`h-48 relative overflow-hidden ${
                    theme === "light" 
                      ? "bg-gradient-to-br from-indigo-100 to-violet-100" 
                      : "bg-gradient-to-br from-indigo-900/40 to-violet-900/40"
                  }`}>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <div className={`w-16 h-16 flex items-center justify-center rounded-full ${
                        theme === "light" 
                          ? "bg-white/90 text-indigo-600" 
                          : "bg-slate-800/90 text-indigo-400"
                      }`}>
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M5 8h14M5 8a2 2 0 110-4h14a2 2 0 110 4M5 8v10a2 2 0 002 2h10a2 2 0 002-2V8m-9 4h4" />
                        </svg>
                      </div>
                    </div>

                    {/* Project title badge */}
                    <div className="absolute bottom-4 left-4 right-4">
                      <div className={`py-2 px-4 rounded-lg shadow-md backdrop-blur-sm ${
                        theme === "light" 
                          ? "bg-white/90 text-slate-800" 
                          : "bg-slate-800/90 text-white"
                      }`}>
                        <h3 className="text-lg font-bold truncate">
                          {project.name}
                        </h3>
                      </div>
                    </div>
                  </div>

                  <div className="p-5">
                    {/* Description */}
                    <p className={`mb-6 leading-relaxed ${
                      theme === "light" ? "text-slate-600" : "text-slate-300"
                    }`}>
                      {project.description}
                    </p>

                    {/* Technology tags */}
                    <div className="mt-4">
                      <h4 className={`text-sm font-medium mb-3 flex items-center ${
                        theme === "light" ? "text-slate-700" : "text-slate-200"
                      }`}>
                        <svg className="w-4 h-4 mr-2 opacity-70" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
                        </svg>
                        {currentLanguage.projects.technologiesUsed}
                      </h4>
                      <div className="flex flex-wrap gap-2">
                        {(project.tech || []).map((tech, idx) => (
                          <span
                            key={idx}
                            className={`px-3 py-1 text-xs rounded-full font-medium inline-flex items-center
                              ${theme === "light" 
                                ? "bg-slate-100 text-slate-700 border border-slate-200" 
                                : "bg-slate-700 text-slate-300 border border-slate-600"
                              }`}
                          >
                            <span className={`w-1.5 h-1.5 rounded-full mr-1.5 ${
                              theme === "light" ? "bg-indigo-500" : "bg-indigo-400"
                            }`} />
                            {tech}
                          </span>
                        ))}
                      </div>
                    </div>

                    {/* Action buttons with updated hover colors */}
                    <div className="flex justify-between mt-8 pt-4 border-t border-slate-200/20">
                      <motion.button
                        onClick={handleDemoClick}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        className={`px-4 py-2 rounded-lg mr-2 transition-colors duration-300 ${
                          theme === "light"
                            ? "bg-indigo-600 text-white hover:bg-indigo-700"
                            : "bg-indigo-500 text-white hover:bg-indigo-600"
                        }`}
                      >
                        {currentLanguage.projects.demo}
                      </motion.button>
                      <motion.button
                        onClick={(e) => handleRepoClick(project, e)}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        className={`px-4 py-2 rounded-lg transition-colors duration-300 ${
                          theme === "light"
                            ? "bg-slate-200 text-slate-700 hover:bg-slate-300"
                            : "bg-slate-700 text-white hover:bg-slate-600"
                        }`}
                      >
                        {currentLanguage.projects.repo}
                      </motion.button>
                    </div>
                  </div>
                </div>

                {/* Highlight effect on click */}
                <motion.div 
                  className={`absolute inset-0 rounded-xl opacity-0 z-0 ${
                    theme === "light" 
                      ? "bg-indigo-100/50" 
                      : "bg-indigo-900/20"
                  }`}
                  animate={{
                    opacity: clickedIndex === index ? 1 : 0
                  }}
                  transition={{ duration: 0.3 }}
                />
              </ProjectCard>
            </motion.div>
          ))}
        </motion.div>

        {/* View all projects button */}
        <motion.div 
          className="flex justify-center mt-12"
          variants={itemVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
        >
          <motion.a
            href="#"
            className={`inline-flex items-center px-6 py-3 rounded-full transition-all duration-300 ${
              theme === "light"
                ? "bg-indigo-600 text-white hover:bg-indigo-700 shadow-md hover:shadow-lg" 
                : "bg-indigo-500 text-white hover:bg-indigo-600 shadow-md hover:shadow-lg"
            }`}
            whileHover={{ y: -5 }}
            whileTap={{ scale: 0.95 }}
          >
            View All Projects
            <svg className="ml-2 w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3" />
            </svg>
          </motion.a>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Projects;
