import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import ResumeButton from "./ResumeButton";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

// Define the navigation items with subtle hover animation
const NavItem = ({ name, href, onClick, isActive, isMobileMenuOpen }) => {
  const { theme } = useTheme();

  return (
    <motion.li
      className="relative"
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.97 }}
      transition={{ duration: 0.2 }}
    >
      <a 
        href={href} 
        onClick={onClick}
        className={`relative block py-2 px-3 text-base font-medium transition-colors duration-200
          ${isActive 
            ? theme === "light" 
              ? "text-rose-600" 
              : "text-indigo-400" 
            : theme === "light" 
              ? "text-gray-800 hover:text-rose-600" 
              : "text-gray-200 hover:text-indigo-400"
          }
          ${isMobileMenuOpen ? "text-lg" : ""}
        `}
      >
        {name}
        {isActive && (
          <motion.span
            className={`absolute inset-x-0 bottom-0 h-0.5 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
            layoutId="navbar-indicator-desktop"
            transition={{ type: "spring", duration: 0.4, bounce: 0.2 }}
          />
        )}
      </a>
    </motion.li>
  );
};

const Header = () => {
  const { theme, setTheme } = useTheme();
  const { language, setLanguage } = useLanguage();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLangDropdownOpen, setIsLangDropdownOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);
  const [visible, setVisible] = useState(true);

  // Store previous scroll position
  const prevScrollY = useRef(0);

  // Get current language data
  const currentLanguage = languages[language];

  const navItems = [
    { id: 'home', name: currentLanguage.header.home, href: '#home' },
    { id: 'skills', name: currentLanguage.header.skills, href: '#skills' },
    { id: 'services', name: currentLanguage.header.services, href: '#services' },
    { id: 'projects', name: currentLanguage.header.projects, href: '#projects' },
    { id: 'contact', name: currentLanguage.header.contact, href: '#contact' },
  ];

  // Completely rewritten scroll handling
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // Only start hiding after scrolling down a bit
      if (currentScrollY < 50) {
        setVisible(true);
        setScrolled(currentScrollY > 10);
      } else {
        // Hide when scrolling down, show when scrolling up
        if (currentScrollY > prevScrollY.current) {
          setVisible(false); // Scrolling down - hide header
        } else {
          setVisible(true); // Scrolling up - show header
        }
        setScrolled(true);
      }

      prevScrollY.current = currentScrollY;
    };

    // Add scroll event listener with passive option for better performance
    window.addEventListener('scroll', handleScroll, { passive: true });

    // Clean up
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Improved scroll to section function
  const handleNavClick = (e, sectionId) => {
    e.preventDefault();
    setActiveSection(sectionId);

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    // Find the section element
    const section = document.getElementById(sectionId);

    if (section) {
      // Get the height of the header to offset the scroll
      const headerHeight = 80;

      // Calculate position to scroll to
      const sectionTop = section.getBoundingClientRect().top;
      const offsetPosition = sectionTop + window.pageYOffset - headerHeight;

      // Scroll to the section
      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <motion.header 
      className={`fixed w-full z-30 transition-all duration-300
        ${scrolled 
          ? theme === "light" 
            ? "bg-white shadow-sm" 
            : "bg-gray-900 border-b border-gray-800"
          : theme === "light"
            ? "bg-white shadow-sm" 
            : "bg-gray-900 border-b border-gray-800"
        }`}
      initial={{ y: 0 }}
      animate={{ y: visible ? 0 : -100 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          {/* Name */}
          <a 
            href="#home" 
            className="flex items-center space-x-2"
            onClick={(e) => handleNavClick(e, 'home')}
          >
            <motion.span 
              className={`font-bold text-xl ${
                theme === "light" ? "text-gray-900" : "text-white"
              }`}
              whileHover={{ y: -1 }}
              transition={{ duration: 0.2 }}
            >
              {currentLanguage.header.name}
            </motion.span>
          </a>

          {/* Desktop Navigation */}
          <nav className="hidden md:block">
            <ul className="flex space-x-1">
              {navItems.map((item) => (
                <NavItem
                  key={item.name}
                  name={item.name}
                  href={item.href}
                  onClick={(e) => handleNavClick(e, item.id)}
                  isActive={activeSection === item.id}
                  isMobileMenuOpen={false}
                />
              ))}
            </ul>
          </nav>

          {/* Actions */}
          <div className="flex items-center space-x-3">
            {/* Theme Toggle */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.2 }}
              onClick={toggleTheme}
              className={`p-2 rounded-full ${
                theme === "light" 
                  ? "bg-gray-100 hover:bg-gray-200" 
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              aria-label="Toggle Theme"
            >
              {theme === "light" ? (
                <svg className="w-5 h-5 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              ) : (
                <svg className="w-5 h-5 text-gray-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              )}
            </motion.button>

            {/* Language Dropdown */}
            <div className="relative">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                transition={{ duration: 0.2 }}
                onClick={() => setIsLangDropdownOpen(!isLangDropdownOpen)}
                className={`p-2 rounded-full ${
                  theme === "light" 
                    ? "bg-gray-100 hover:bg-gray-200" 
                    : "bg-gray-800 hover:bg-gray-700"
                }`}
                aria-label="Language Menu"
              >
                <span className="font-medium text-sm">
                  {language === "en" ? "EN" : language === "jp" ? "JP" : "KO"}
                </span>
              </motion.button>

              {/* Dropdown Menu */}
              <AnimatePresence>
                {isLangDropdownOpen && (
                  <motion.div
                    initial={{ opacity: 0, scale: 0.95, y: 8 }}
                    animate={{ opacity: 1, scale: 1, y: 0 }}
                    exit={{ opacity: 0, scale: 0.95, y: 8 }}
                    transition={{ duration: 0.2 }}
                    className={`absolute right-0 mt-2 w-36 rounded-lg shadow-lg overflow-hidden z-50 
                      ${theme === "light" 
                        ? "bg-white ring-1 ring-gray-200" 
                        : "bg-gray-800 ring-1 ring-gray-700"
                      }`}
                  >
                    <div className="py-1">
                      <button
                        onClick={() => {
                          setLanguage("en");
                          setIsLangDropdownOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm ${
                          language === "en"
                            ? theme === "light" 
                              ? "bg-gray-100 text-gray-900 font-medium" 
                              : "bg-gray-700 text-white font-medium"
                            : theme === "light" 
                              ? "text-gray-700 hover:bg-gray-100" 
                              : "text-gray-300 hover:bg-gray-700"
                        }`}
                      >
                        <div className="flex items-center">
                          <img src="https://img.icons8.com/color/24/usa.png" alt="USA" className="w-5 h-5 mr-2" />
                          English
                        </div>
                      </button>
                      <button
                        onClick={() => {
                          setLanguage("jp");
                          setIsLangDropdownOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm ${
                          language === "jp"
                            ? theme === "light" 
                              ? "bg-gray-100 text-gray-900 font-medium" 
                              : "bg-gray-700 text-white font-medium"
                            : theme === "light" 
                              ? "text-gray-700 hover:bg-gray-100" 
                              : "text-gray-300 hover:bg-gray-700"
                        }`}
                      >
                        <div className="flex items-center">
                          <img src="https://img.icons8.com/color/24/japan.png" alt="Japan" className="w-5 h-5 mr-2" />
                          日本語
                        </div>
                      </button>
                      <button
                        onClick={() => {
                          setLanguage("ko");
                          setIsLangDropdownOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm ${
                          language === "ko"
                            ? theme === "light" 
                              ? "bg-gray-100 text-gray-900 font-medium" 
                              : "bg-gray-700 text-white font-medium"
                            : theme === "light" 
                              ? "text-gray-700 hover:bg-gray-100" 
                              : "text-gray-300 hover:bg-gray-700"
                        }`}
                      >
                        <div className="flex items-center">
                          <img src="https://img.icons8.com/color/24/south-korea.png" alt="South Korea" className="w-5 h-5 mr-2" />
                          한국어
                        </div>
                      </button>
                    </div>
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Mobile Menu Toggle */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.2 }}
              onClick={toggleMobileMenu}
              className={`md:hidden p-2 rounded-full ${
                theme === "light" 
                  ? "bg-gray-100 hover:bg-gray-200" 
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              aria-label="Toggle Mobile Menu"
            >
              {isMobileMenuOpen ? (
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              )}
            </motion.button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.nav
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: "auto", opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="md:hidden overflow-hidden"
            >
              <motion.ul 
                className="py-3 space-y-1"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.1 }}
              >
                {navItems.map((item) => (
                  <NavItem
                    key={item.name}
                    name={item.name}
                    href={item.href}
                    onClick={(e) => {
                      handleNavClick(e, item.id);
                      setIsMobileMenuOpen(false);
                    }}
                    isActive={activeSection === item.id}
                    isMobileMenuOpen={true}
                  />
                ))}
              </motion.ul>
            </motion.nav>
          )}
        </AnimatePresence>
      </div>
    </motion.header>
  );
};

export default Header;
