import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";

const Contact = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0, transition: { staggerChildren: 0.3, duration: 0.8 } },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6 } },
  };

  return (
    <motion.section
      id="contact"
      className={`min-h-screen flex flex-col justify-center py-16 px-8 relative overflow-hidden ${
        theme === "light"
          ? "bg-gradient-to-br from-light-background via-white to-orange-50"
          : "bg-gradient-to-br from-dark-background via-gray-900 to-blue-900"
      }`}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className={`absolute w-[500px] h-[500px] rounded-full blur-3xl opacity-20
          ${theme === "light" ? "bg-orange-400" : "bg-blue-500"}
          -top-32 -right-32 animate-pulse`}
        />
        <div className={`absolute w-[500px] h-[500px] rounded-full blur-3xl opacity-20
          ${theme === "light" ? "bg-yellow-400" : "bg-purple-500"}
          -bottom-32 -left-32 animate-pulse delay-1000`}
        />
      </div>
      <div className="container mx-auto max-w-4xl">
        {/* Title */}
        <motion.h2
          className={`text-3xl font-bold text-center mb-8 ${
            theme === "light" ? "text-light-accent" : "text-dark-accent"
          }`}
          variants={itemVariants}
        >
          {text.contact.title}
        </motion.h2>

        {/* Description */}
        <motion.p className="text-center mb-8" variants={itemVariants}>
          {text.contact.description}
        </motion.p>

        {/* Form */}
        <motion.form className="space-y-6" variants={containerVariants}>
          <motion.input
            type="text"
            placeholder="Your Name"
            className={`w-full p-4 rounded-lg border ${
              theme === "light" ? "bg-white border-gray-300" : "bg-gray-800 border-gray-600 text-gray-100"
            }`}
            variants={itemVariants}
          />
          <motion.input
            type="email"
            placeholder="Your Email"
            className={`w-full p-4 rounded-lg border ${
              theme === "light" ? "bg-white border-gray-300" : "bg-gray-800 border-gray-600 text-gray-100"
            }`}
            variants={itemVariants}
          />
          <motion.textarea
            placeholder="Your Message"
            rows="5"
            className={`w-full p-4 rounded-lg border ${
              theme === "light" ? "bg-white border-gray-300" : "bg-gray-800 border-gray-600 text-gray-100"
            }`}
            variants={itemVariants}
          ></motion.textarea>
          <motion.button
            type="submit"
            className={`w-full p-4 rounded-lg font-bold ${
              theme === "light"
                ? "bg-light-accent text-white hover:bg-orange-600"
                : "bg-dark-accent text-black hover:bg-green-700"
            } transition`}
            variants={itemVariants}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Send
          </motion.button>
        </motion.form>
      </div>
    </motion.section>
  );
};

export default Contact;
