import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages";
import { motion } from "framer-motion";

const Services = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [hoveredCard, setHoveredCard] = useState(null);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3,
      },
    },
  };

  // Header animation
  const headerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6,
        ease: "easeOut"
      } 
    }
  };

  // Service card animations
  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.5,
        ease: "easeOut" 
      }
    },
    hover: {
      y: -10,
      scale: 1.02,
      transition: { 
        duration: 0.3,
        ease: "easeOut" 
      }
    }
  };

  // Icons for services
  const serviceIcons = {
    "Tutoring": (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12 8V16M8 12H16M22 12C22 17.5228 17.5228 22 12 22C6.47715 22 2 17.5228 2 12C2 6.47715 6.47715 2 12 2C17.5228 2 22 6.47715 22 12Z" 
          stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      </svg>
    ),
    "Translation": (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M3 5H15M9 5V19M14 13L21 13M17.5 9.5L14 13L17.5 16.5" 
          stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      </svg>
    ),
    "Trumpet Lessons": (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M9 17.5V18.5C9 19.3284 8.32843 20 7.5 20H6.5C5.67157 20 5 19.3284 5 18.5V17.5M9 17.5V7.5C9 6.67157 9.67157 6 10.5 6H19.5C20.3284 6 21 6.67157 21 7.5V17.5C21 18.3284 20.3284 19 19.5 19H5M9 17.5H5M5 17.5V4.5C5 3.67157 4.32843 3 3.5 3V3"
          stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
      </svg>
    )
  };

  const services = currentLanguage.services.items;

  return (
    <motion.section
      id="services"
      className="min-h-screen py-20 px-8 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.1 }}
      variants={containerVariants}
    >
      {/* Background element */}
      <div className="absolute inset-0 pointer-events-none">
        <motion.div 
          className={`absolute top-1/4 right-1/4 w-64 h-64 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-rose-400" : "bg-indigo-500"}`}
          animate={{ 
            scale: [1, 1.2, 1],
            x: [0, 30, 0],
            y: [0, -20, 0],
          }}
          transition={{ 
            duration: 15, 
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <motion.div 
          className={`absolute bottom-1/4 left-1/4 w-64 h-64 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-amber-400" : "bg-purple-500"}`}
          animate={{ 
            scale: [1, 1.1, 1],
            x: [0, -20, 0],
            y: [0, 30, 0],
          }}
          transition={{ 
            duration: 18, 
            repeat: Infinity,
            ease: "easeInOut",
            delay: 1
          }}
        />
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section Header */}
        <motion.div className="text-center mb-16" variants={headerVariants}>
          <motion.h2 
            className={`text-4xl font-bold mb-4 inline-block
              ${theme === "light" ? "text-rose-600" : "text-indigo-400"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.title}
            <motion.div 
              className={`h-1 mt-1 ${theme === "light" ? "bg-rose-500" : "bg-indigo-500"}`}
              initial={{ width: 0 }}
              whileInView={{ width: "100%" }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.5 }}
            />
          </motion.h2>

          <motion.p 
            className={`text-lg max-w-2xl mx-auto
              ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
            variants={headerVariants}
          >
            {currentLanguage.services.description}
          </motion.p>
        </motion.div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div 
              key={index}
              className={`rounded-xl p-8 ${theme === "light" 
                ? hoveredCard === index
                  ? "bg-gradient-to-b from-gray-100 to-white shadow-lg border border-gray-200" 
                  : "bg-white/80 border border-gray-200 shadow-md"
                : hoveredCard === index
                  ? "bg-gradient-to-b from-gray-800 to-gray-900 shadow-lg border border-gray-700" 
                  : "bg-gray-800/60 border border-gray-700 shadow-md"
              } backdrop-blur-sm transition-all duration-300 relative overflow-hidden`}
              variants={cardVariants}
              whileHover="hover"
              onHoverStart={() => setHoveredCard(index)}
              onHoverEnd={() => setHoveredCard(null)}
            >
              {/* Animated background element on hover */}
              {hoveredCard === index && (
                <motion.div 
                  className={`absolute -bottom-6 -right-6 w-32 h-32 rounded-full opacity-20
                    ${theme === "light" ? "bg-rose-400" : "bg-indigo-500"}`}
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  exit={{ scale: 0 }}
                  transition={{ duration: 0.3 }}
                />
              )}

              {/* Service icon */}
              <div className={`mb-5 ${theme === "light" 
                ? "text-rose-500" 
                : "text-indigo-400"}`}
              >
                {serviceIcons[service.name] || (
                  <svg className="w-8 h-8" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M12 8V16M8 12H16M22 12C22 17.5228 17.5228 22 12 22C6.47715 22 2 17.5228 2 12C2 6.47715 6.47715 2 12 2C17.5228 2 22 6.47715 22 12Z" 
                      stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                )}
              </div>

              {/* Service title */}
              <h3 className={`text-xl font-bold mb-3 ${theme === "light" ? "text-gray-800" : "text-white"}`}>
                {service.name}
              </h3>

              {/* Service description */}
              <p className={`${theme === "light" ? "text-gray-600" : "text-gray-300"} relative z-10`}>
                {service.description}
              </p>

              {/* Animated learn more button */}
              <motion.div 
                className={`mt-6 inline-flex items-center text-sm font-medium 
                  ${theme === "light" ? "text-rose-600" : "text-indigo-400"}`}
                initial={{ opacity: 0.7 }}
                animate={{ opacity: hoveredCard === index ? 1 : 0.7 }}
                whileHover={{ x: 5 }}
              >
                Learn more 
                <svg className="w-4 h-4 ml-1" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10.293 5.293a1 1 0 011.414 0l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414-1.414L12.586 11H5a1 1 0 110-2h7.586l-2.293-2.293a1 1 0 010-1.414z" clipRule="evenodd" />
                </svg>
              </motion.div>
            </motion.div>
          ))}
        </div>

        {/* Call to action */}
        <motion.div 
          className="text-center mt-16"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <motion.a
            href="#contact"
            className={`inline-flex items-center px-6 py-3 rounded-full text-lg font-medium 
              ${theme === "light" 
                ? "bg-rose-500 text-white hover:bg-rose-600" 
                : "bg-indigo-500 text-white hover:bg-indigo-600"
              } transition-colors duration-300 shadow-lg`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.98 }}
          >
            {language === "en" ? "Get in Touch" : language === "jp" ? "お問い合わせ" : "문의하기"}
            <svg className="w-5 h-5 ml-2" viewBox="0 0 20 20" fill="currentColor">
