import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";

const ResumeButton = () => {
  const { theme } = useTheme();
  const { text, language } = useLanguage();

  // Use the resume text from the text object or fallback to a default value
  const resumeText = text.header.resume || (language === "en" ? "Resume" : "履歴書");

  return (
    <a
      href="https://docs.google.com/document/d/11GSYyV8riF_J5v3ewUiMieyqCJpkYWa6/edit?usp=sharing&ouid=100239401016507998095&rtpof=true&sd=true" 
      target="_blank"
      rel="noopener noreferrer"
      className={`px-4 py-2 rounded-lg text-sm font-medium transition ${
        theme === "dark"
          ? "bg-indigo-500 text-white hover:bg-indigo-600"
          : "bg-rose-500 text-white hover:bg-rose-600"
      }`}
    >
      {resumeText}    
    </a>
  );
};

export default ResumeButton;
