import { motion, AnimatePresence } from "framer-motion";
import { useInView } from "framer-motion";
import React, { useRef, useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";

const Skills = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const [clickedIndex, setClickedIndex] = useState(null);

  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0, transition: { duration: 1, staggerChildren: 0.3 } },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
    hover: {
      scale: 1.05,
      rotateY: 5,
      transition: { duration: 0.2 }
    },
    tap: {
      scale: 0.95,
      transition: { duration: 0.1 }
    }
  };

  const spokenLanguages = [
    { name: text.skills.languages.japanese, level: text.skills.proficiency.native, icon: "https://img.icons8.com/color/48/japan.png" },
    { name: text.skills.languages.english, level: text.skills.proficiency.native, icon: "https://img.icons8.com/color/48/usa.png" },
    { name: text.skills.languages.korean, level: text.skills.proficiency.fluent, icon: "https://img.icons8.com/color/48/south-korea.png" },
  ];

  const programmingLanguages = [
    { name: "Java", icon: "https://img.icons8.com/color/48/java-coffee-cup-logo.png", tools: ["SpringBoot", "Maven"] },
    { name: "Python", icon: "https://img.icons8.com/color/48/python.png", tools: ["Pandas", "NumPy", "MongoDB", "TensorFlow", "PyCharm"] },
    { name: "Kotlin", icon: "https://img.icons8.com/color/48/kotlin.png", tools: ["Ktor"] },
    { name: "SQL", icon: "https://img.icons8.com/color/48/sql.png", tools: ["MySQL", "NoSQL"] },
    { name: "JavaScript", icon: "https://img.icons8.com/color/48/javascript.png", tools: ["React.js", "Node.js"] },
    { name: "TypeScript", icon: "https://img.icons8.com/color/48/typescript.png", tools: ["Angular"] },
  ];

  const handleTileClick = (index) => {
    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 300);
  };

  const SkillTile = ({ item, index, isLanguage = false }) => (
    <motion.div
      className={`relative p-6 rounded-xl shadow-lg text-center cursor-pointer
        ${theme === "light" 
          ? "bg-gradient-to-br from-white to-gray-50 shadow-gray-200" 
          : "bg-gradient-to-br from-dark-card to-dark-background shadow-dark-border"
        }`}
      variants={itemVariants}
      whileHover="hover"
      whileTap="tap"
      onClick={() => handleTileClick(index)}
      style={{
        transformStyle: "preserve-3d",
        perspective: "1000px",
      }}
    >
      <div className="relative z-10">
        <img 
          src={item.icon} 
          alt={`${item.name} icon`} 
          className={`w-12 h-12 mx-auto mb-3 transition-transform duration-300
            ${clickedIndex === index ? "scale-110" : ""}`}
        />
        <h4 className={`text-xl font-semibold mb-2 ${theme === "dark" ? "text-dark-text" : "text-light-text"}`}>
          {item.name}
        </h4>
        {isLanguage ? (
          <p className={`text-sm ${theme === "dark" ? "text-dark-muted" : "text-gray-600"}`}>
            {item.level}
          </p>
        ) : (
          item.tools && (
            <div className="flex flex-wrap justify-center gap-2 mt-3">
              {item.tools.map((tool, idx) => (
                <span
                  key={idx}
                  className={`px-2 py-1 text-xs rounded-full
                    ${theme === "dark" 
                      ? "bg-dark-border text-dark-text" 
                      : "bg-gray-100 text-gray-700"
                    }`}
                >
                  {tool}
                </span>
              ))}
            </div>
          )
        )}
      </div>
      <div 
        className={`absolute inset-0 rounded-xl opacity-0 transition-opacity duration-300
          ${theme === "dark" 
            ? "bg-gradient-to-br from-dark-accent/20 to-dark-hover/20" 
            : "bg-gradient-to-br from-light-accent/20 to-orange-400/20"
          }`}
        style={{
          opacity: clickedIndex === index ? 1 : 0
        }}
      />
    </motion.div>
  );

  return (
    <motion.section
      id="skills"
      ref={ref}
      className={`scroll-offset py-16 px-8 ${
        theme === "light" ? "bg-light-background text-light-text" : "bg-dark-background text-dark-text dark-mode"
      } relative`}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }} 
      animate={isInView ? "visible" : "hidden"}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        <motion.div
          className="text-center mb-6"
          variants={itemVariants}
        >
          <h2 className={`text-4xl font-bold inline ${theme === "dark" ? "neon-text" : "text-light-accent"}`}>
            {text.skills.title}
          </h2>
        </motion.div>
        <motion.p
          className="text-lg text-center mb-12"
          variants={itemVariants}
        >
          {text.skills.description}
        </motion.p>

        {/* Spoken Languages */}
        <motion.div
          className="mb-12"
          variants={containerVariants}
        >
          <motion.h3
            className={`text-3xl font-semibold mb-6 ${theme === "dark" ? "neon-text" : "text-light-accent"}`}
            variants={itemVariants}
          >
            Spoken Languages
          </motion.h3>
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6"
            variants={containerVariants}
          >
            {spokenLanguages.map((lang, index) => (
              <SkillTile key={index} item={lang} index={index} isLanguage={true} />
            ))}
          </motion.div>
        </motion.div>

        {/* Programming Stack */}
        <motion.div variants={containerVariants}>
          <motion.h3
            className={`text-3xl font-semibold mb-6 ${theme === "dark" ? "neon-text" : "text-light-accent"}`}
            variants={itemVariants}
          >
            Programming Stack
          </motion.h3>
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-3 md:grid-cols-4 gap-6"
            variants={containerVariants}
          >
            {programmingLanguages.map((lang, index) => (
              <SkillTile key={index} item={lang} index={index} />
            ))}
          </motion.div>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Skills;
