import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import ThemeToggle from "./ThemeToggle";
import ResumeButton from "./ResumeButton";

// Define the navigation items with subtle hover animation
const NavItem = ({ name, href, isActive, onClick }) => {
  const { theme } = useTheme();

  return (
    <li className="relative">
      <a 
        href={href} 
        onClick={onClick}
        className={`relative block py-2 px-4 text-sm font-medium transition-all duration-300
          ${isActive 
            ? theme === "light" 
              ? "text-indigo-600" 
              : "text-indigo-400" 
            : theme === "light" 
              ? "text-slate-600 hover:text-indigo-600" 
              : "text-slate-300 hover:text-indigo-400"
          }
        `}
      >
        {name}
        <span 
          className={`absolute inset-x-0 bottom-0 h-0.5 transform origin-left transition-transform duration-300 ease-out ${
            isActive
              ? "scale-x-100 " + (theme === "light" ? "bg-indigo-600" : "bg-indigo-400")
              : "scale-x-0 " + (theme === "light" ? "bg-indigo-600/0" : "bg-indigo-400/0")
          }`}
        />
      </a>
    </li>
  );
};

// Language switcher component
const LanguageSwitcher = ({ language, setLanguage, theme }) => {
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Define available languages
  const languages = [
    { code: "en", name: "English", flag: "ðŸ‡ºðŸ‡¸" },
    { code: "ja", name: "æ—¥æœ¬èªž", flag: "ðŸ‡¯ðŸ‡µ" },
    { code: "ko", name: "í•œêµ­ì–´", flag: "ðŸ‡°ðŸ‡·" }
  ];

  // Handle clicks outside to close dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Get current language data
  const currentLang = languages.find(lang => lang.code === language);

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={`flex items-center space-x-1 px-3 py-1.5 rounded-lg text-sm font-medium transition-all duration-200 ${
          theme === "light"
            ? "bg-slate-100 hover:bg-slate-200 text-slate-700"
            : "bg-slate-800 hover:bg-slate-700 text-slate-200"
        }`}
      >
        <span>{currentLang?.flag}</span>
        <span className="hidden sm:inline">{currentLang?.code.toUpperCase()}</span>
        <svg
          className={`w-4 h-4 transition-transform duration-200 ${isOpen ? "rotate-180" : ""}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {/* Dropdown */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.2 }}
            className={`absolute right-0 mt-2 w-36 rounded-lg shadow-lg overflow-hidden z-50 ${
              theme === "light" ? "bg-white" : "bg-slate-800 border border-slate-700"
            }`}
          >
            <ul>
              {languages.map(lang => (
                <li key={lang.code}>
                  <button
                    onClick={() => {
                      setLanguage(lang.code);
                      setIsOpen(false);
                    }}
                    className={`w-full text-left flex items-center px-4 py-2.5 text-sm transition-colors ${
                      language === lang.code
                        ? theme === "light"
                          ? "bg-indigo-50 text-indigo-600"
                          : "bg-indigo-900/30 text-indigo-300"
                        : theme === "light"
                          ? "text-slate-700 hover:bg-slate-50"
                          : "text-slate-300 hover:bg-slate-700"
                    }`}
                  >
                    <span className="mr-2">{lang.flag}</span>
                    <span>{lang.name}</span>
                  </button>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

const Header = ({ activeSection, onSectionChange }) => {
  const { theme } = useTheme();
  const { language, setLanguage, text } = useLanguage();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [visible, setVisible] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(0);

  // Set up navigation items
  const navItems = [
    { id: 'home', name: text?.header?.home || 'Home', href: '#home' },
    { id: 'skills', name: text?.header?.skills || 'Skills', href: '#skills' },
    { id: 'projects', name: text?.header?.projects || 'Projects', href: '#projects' },
    { id: 'services', name: text?.header?.services || 'Services', href: '#services' },
    { id: 'contact', name: text?.header?.contact || 'Contact', href: '#contact' },
  ];

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;

      // Set scrolled state for styling
      setScrolled(currentScrollPos > 20);

      // Hide/show header based on scroll direction (only when significantly scrolled)
      if (currentScrollPos > 100) {
        setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 100);
      } else {
        setVisible(true);
      }

      setPrevScrollPos(currentScrollPos);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [prevScrollPos]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleNavClick = (e, section) => {
    e.preventDefault();

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    // Use the provided callback to change section
    if (onSectionChange) {
      onSectionChange(section);
    }
  };

  return (
    <motion.header 
      className={`fixed w-full z-40 transition-all duration-300
        ${scrolled 
          ? theme === "light" 
            ? "bg-white/80 backdrop-blur-md shadow-sm" 
            : "bg-slate-950/80 backdrop-blur-md border-b border-slate-800/50"
          : "bg-transparent"
        }
        ${visible ? "translate-y-0" : "-translate-y-full"}
      `}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16 md:h-20">
          {/* Logo */}
          <a 
            href="#home" 
            onClick={(e) => handleNavClick(e, 'home')}
            className="flex items-center gap-2"
          >
            <motion.div 
              initial={{ scale: 1 }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`relative flex items-center justify-center w-9 h-9 rounded-lg bg-gradient-to-br ${
                theme === "light"
                  ? "from-indigo-500 to-violet-500" 
                  : "from-indigo-600 to-violet-600"
              }`}
            >
              <span className="text-white font-bold text-lg">Y</span>
              <div className="absolute inset-0 rounded-lg bg-white/20 opacity-0 hover:opacity-20 transition-opacity duration-300"></div>
            </motion.div>

            <span className={`text-lg font-bold ${
              theme === "light" ? "text-slate-800" : "text-white"
            }`}>
              {text?.header?.name || 'Yutaka Yamaguchi'}
            </span>
          </a>

          {/* Desktop Navigation */}
          <nav className="hidden md:block">
            <ul className="flex space-x-1">
              {navItems.map((item) => (
                <NavItem
                  key={item.id}
                  name={item.name}
                  href={item.href}
                  onClick={(e) => handleNavClick(e, item.id)}
                  isActive={activeSection === item.id}
                />
              ))}
            </ul>
          </nav>

          {/* Actions */}
          <div className="flex items-center space-x-3">
            {/* Language Switcher */}
            <LanguageSwitcher 
              language={language} 
              setLanguage={setLanguage} 
              theme={theme} 
            />

            {/* Theme Toggle */}
            <ThemeToggle />

            {/* Resume Button - Desktop */}
            <div className="hidden md:block">
              <ResumeButton />
            </div>

            {/* Mobile menu button */}
            <button
              onClick={toggleMobileMenu}
              className={`md:hidden p-2 rounded-lg ${
                theme === "light" 
                  ? "bg-slate-100 hover:bg-slate-200 text-slate-700" 
                  : "bg-slate-800 hover:bg-slate-700 text-slate-200"
              }`}
              aria-label="Toggle Menu"
            >
              {isMobileMenuOpen ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
            className={`md:hidden overflow-hidden ${
              theme === "light" 
                ? "bg-white/90 backdrop-blur-md"
                : "bg-slate-900/90 backdrop-blur-md"
            }`}
          >
            <nav className="container mx-auto px-4 py-4">
              <ul className="flex flex-col space-y-1">
                {navItems.map((item) => (
                  <motion.li 
                    key={item.id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    <a
                      href={item.href}
                      onClick={(e) => handleNavClick(e, item.id)}
                      className={`block py-3 px-4 text-base font-medium rounded-lg transition-colors ${
                        activeSection === item.id
                          ? theme === "light" 
                            ? "bg-indigo-50 text-indigo-600" 
                            : "bg-slate-800 text-indigo-400"
                          : theme === "light"
                            ? "text-slate-700 hover:bg-slate-50"
                            : "text-slate-300 hover:bg-slate-800"
                      }`}
                    >
                      {item.name}
                    </a>
                  </motion.li>
                ))}

                {/* Resume Button - Mobile */}
                <motion.li
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.2, delay: 0.3 }}
                >
                  <a
                    href="https://docs.google.com/document/d/11GSYyV8riF_J5v3ewUiMieyqCJpkYWa6/edit?usp=sharing"
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`block py-3 px-4 mt-2 text-base font-medium rounded-lg transition-colors ${
                      theme === "light"
                        ? "bg-indigo-600 text-white hover:bg-indigo-700"
                        : "bg-indigo-500 text-white hover:bg-indigo-600"
                    }`}
                  >
                    <div className="flex items-center">
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                      {text?.header?.resume || 'Resume'}
                    </div>
                  </a>
                </motion.li>
              </ul>
            </nav>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.header>
  );
};

export default Header;
