import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

// Simpler card component with optimized hover effects
const ProjectCard = ({ children, onClick, isLoading }) => {
  const [isHovered, setIsHovered] = useState(false);
  const { theme } = useTheme();

  if (isLoading) {
    return (
      <motion.div
        className={`relative h-full w-full rounded-2xl overflow-hidden ${
          theme === "light" 
            ? "bg-white/80 border border-gray-200 shadow-md" 
            : "bg-gray-800/60 border border-gray-700 shadow-md"
        }`}
        initial={{ opacity: 0.6 }}
        animate={{ 
          opacity: [0.6, 0.8, 0.6],
          y: [0, -3, 0]
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          repeatType: "loop",
          ease: "easeInOut"
        }}
      >
        {/* Skeleton Content */}
        <div className="p-5 h-full flex flex-col">
          {/* Skeleton image */}
          <div className={`w-full h-40 rounded-lg mb-4 ${
            theme === "light" ? "bg-gray-200" : "bg-gray-700"
          }`} />

          {/* Skeleton title */}
          <div className={`h-6 w-2/3 rounded-md mb-3 ${
            theme === "light" ? "bg-gray-200" : "bg-gray-700"
          }`} />

          {/* Skeleton description */}
          <div className="space-y-2 mb-4">
            <div className={`h-4 w-full rounded-md ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
            <div className={`h-4 w-5/6 rounded-md ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
            <div className={`h-4 w-4/6 rounded-md ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
          </div>

          {/* Skeleton tech tags */}
          <div className="flex space-x-2 mt-auto">
            <div className={`h-6 w-16 rounded-full ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
            <div className={`h-6 w-20 rounded-full ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
            <div className={`h-6 w-14 rounded-full ${
              theme === "light" ? "bg-gray-200" : "bg-gray-700"
            }`} />
          </div>
        </div>

        {/* Loading shimmer effect */}
        <motion.div 
          className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent"
          initial={{ x: '-100%' }}
          animate={{ x: '100%' }}
          transition={{ 
            repeat: Infinity, 
            repeatType: 'loop', 
            duration: 1.5,
            ease: 'linear',
            repeatDelay: 0.5
          }}
        />
      </motion.div>
    );
  }

  return (
    <motion.div
      className="relative h-full w-full rounded-2xl overflow-hidden"
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      onClick={onClick}
      whileHover={{ 
        y: -6,
        transition: { duration: 0.5, ease: "easeOut" }
      }}
    >
      {children}

      {/* Shimmer effect only visible on hover */}
      {isHovered && (
        <motion.div 
          className="absolute inset-0 pointer-events-none"
          initial={{ opacity: 0 }}
          animate={{ 
            opacity: 1,
            backgroundPosition: ["200% 0%", "-200% 0%"]
          }}
          style={{
            background: "linear-gradient(to right, transparent, rgba(255, 255, 255, 0.1), transparent)",
            backgroundSize: "400% 100%",
          }}
          transition={{
            backgroundPosition: {
              duration: 3.5,
              repeat: Infinity,
              ease: "linear",
              repeatType: "loop"
            }
          }}
        />
      )}
    </motion.div>
  );
};

const Projects = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [clickedIndex, setClickedIndex] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");
  const popupTimeoutRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [loadingProgress, setLoadingProgress] = useState(0);

  // Simulate loading with progress
  useEffect(() => {
    if (isLoading) {
      const totalDuration = 1200; // Total loading time in ms
      const interval = 40; // Update interval in ms
      const steps = totalDuration / interval;
      let currentStep = 0;

      const timer = setInterval(() => {
        currentStep += 1;
        const progress = Math.min(currentStep / steps, 1);
        setLoadingProgress(progress);

        if (progress >= 1) {
          clearInterval(timer);
          setTimeout(() => setIsLoading(false), 200);
        }
      }, interval);

      return () => clearInterval(timer);
    }
  }, [isLoading]);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15, 
        delayChildren: 0.1,
        when: "beforeChildren",
        duration: 0.6,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.98 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { 
        duration: 0.5,
        type: "spring",
        stiffness: 100,
        damping: 15,
        delay: i * 0.08
      },
    }),
    tap: {
      scale: 0.97,
      transition: { duration: 0.1 }
    }
  };

  // Loading progress line animation
  const progressVariants = {
    initial: { width: 0 },
    animate: { width: `${loadingProgress * 100}%` }
  };

  // Header animation with improved timing and effects
  const headerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6,
        ease: "easeOut",
        delay: 0.1
      } 
    }
  };

  // Background animation for the section
  const backgroundVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 1.2 }
    }
  };

  const handleDemoClick = (e) => {
    e.preventDefault();
    e.stopPropagation(); // Stop event propagation to prevent card click

    // Clear any existing timeout and prevent showing popup if already visible
    if (popupTimeoutRef.current) {
      clearTimeout(popupTimeoutRef.current);
    }

    // Only show the popup if it's not already visible
    if (!showPopup) {
      setPopupMessage("Live Demo Coming Soon!");
      setShowPopup(true);
      popupTimeoutRef.current = setTimeout(() => {
        setShowPopup(false);
        popupTimeoutRef.current = null;
      }, 2000);
    }
  };

  const handleRepoClick = (project, e) => {
    e.preventDefault();
    e.stopPropagation(); // Stop event propagation to prevent card click

    // Check if the project has a repo URL defined
    if (!project.repoUrl || project.repoUrl === "#" || project.repoUrl === "") {
      // Clear any existing timeout and prevent showing popup if already visible
      if (popupTimeoutRef.current) {
        clearTimeout(popupTimeoutRef.current);
      }

      // Only show the popup if it's not already visible
      if (!showPopup) {
        setPopupMessage("Repository Coming Soon!");
        setShowPopup(true);
        popupTimeoutRef.current = setTimeout(() => {
          setShowPopup(false);
          popupTimeoutRef.current = null;
        }, 2000);
      }
      return;
    }

    // Make sure URL has proper protocol
    let url = project.repoUrl;
    if (!url.startsWith('http://') && !url.startsWith('https://')) {
      url = 'https://' + url;
    }

    window.open(url, '_blank');
  };

  const handleCardClick = (index) => {
    if (clickedIndex === index) {
      setClickedIndex(null);
      return;
    }

    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  // Improved Popup component
  const Popup = () => (
    <motion.div 
      className="fixed top-0 left-0 right-0 flex justify-center items-start z-50 mt-24 px-4"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ type: "spring", stiffness: 300, damping: 25 }}
    >
      <motion.div 
        className={`px-6 py-3 rounded-lg shadow-xl backdrop-blur-sm ${
          theme === "light" 
            ? "bg-gradient-to-r from-rose-500 to-pink-600 text-white" 
            : "bg-gradient-to-r from-indigo-500 to-violet-600 text-white"
        }`}
        initial={{ scale: 0.9 }}
        animate={{ scale: 1 }}
        exit={{ scale: 0.9 }}
      >
        <div className="flex items-center space-x-2">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
          </svg>
          <p className="font-medium">{popupMessage}</p>
        </div>
      </motion.div>
    </motion.div>
  );

  return (
    <motion.section
      className="min-h-screen py-20 px-8 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.1 }}
      variants={containerVariants}
    >
      {/* Loading progress indicator */}
      {isLoading && (
        <div className="fixed top-0 left-0 right-0 z-50">
          <motion.div 
            className={`h-1 ${theme === "light" ? "bg-rose-500" : "bg-indigo-500"}`}
            variants={progressVariants}
            initial="initial"
            animate="animate"
            transition={{ duration: 0.4 }}
          />
        </div>
      )}

      {/* Background element */}
      <motion.div 
        className="absolute inset-0 pointer-events-none"
        variants={backgroundVariants}
      >
        <motion.div 
          className={`absolute top-1/4 right-1/4 w-64 h-64 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-rose-400" : "bg-indigo-500"}`}
          animate={{ 
            scale: [1, 1.2, 1],
            x: [0, 30, 0],
            y: [0, -20, 0],
          }}
          transition={{ 
            duration: 15, 
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <motion.div 
          className={`absolute bottom-1/4 left-1/4 w-64 h-64 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-amber-400" : "bg-purple-500"}`}
          animate={{ 
            scale: [1, 1.1, 1],
            x: [0, -20, 0],
            y: [0, 30, 0],
          }}
          transition={{ 
            duration: 18, 
            repeat: Infinity,
            ease: "easeInOut",
            delay: 1
          }}
        />
      </motion.div>

      <div className="max-w-7xl mx-auto">
        {/* Section Header */}
        <motion.div className="text-center mb-16" variants={headerVariants}>
          <motion.h2 
            className={`text-4xl font-bold mb-4 inline-block
              ${theme === "light" ? "text-rose-600" : "text-indigo-400"}`}
          >
            {currentLanguage?.projects?.title || "Projects"}
            <motion.div 
              className={`h-1 mt-1 ${theme === "light" ? "bg-rose-500" : "bg-indigo-500"}`}
              initial={{ width: 0 }}
              whileInView={{ width: "100%" }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.5 }}
            />
          </motion.h2>
          <motion.p 
            className={`text-lg max-w-2xl mx-auto
              ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
          >
            {currentLanguage?.projects?.description || "Explore my featured projects showcasing my skills and experience."}
          </motion.p>
        </motion.div>

        {/* Project Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {(currentLanguage?.projects?.items || []).map((project, index) => (
            <motion.div 
              key={index}
              className={`h-full ${
                theme === "light" 
                  ? "bg-white/80 rounded-2xl shadow-lg" 
                  : "bg-gray-800/60 rounded-2xl shadow-lg"}
                  backdrop-blur-sm overflow-hidden border
                  ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
              variants={itemVariants}
              custom={index}
              whileTap="tap"
            >
              <ProjectCard onClick={() => handleCardClick(index)} isLoading={isLoading}>
                {/* Card Content */}
                <div className="p-6 relative z-10">
                  {/* Project image with loading placeholder */}
                  <div className="w-full h-40 rounded-lg mb-4 overflow-hidden">
                    <img 
                      src={project.image} 
                      alt={project.title || "Project"}
                      className={`object-cover h-full w-full transition-opacity duration-300 ${isLoading ? 'opacity-0' : 'opacity-100'}`}
                    />
                  </div>
                  <h3 className={`text-xl font-bold mb-3 ${
                    theme === "light" ? "text-gray-800" : "text-white"
                  }`}>
                    {project.title || "Project Title"}
                  </h3>
                  <p className={`mb-4 ${
                    theme === "light" ? "text-gray-600" : "text-gray-300"
                  }`}>
                    {project.description || "Project description"}
                  </p>

                  {/* Tech stack tags */}
                  <div className="flex flex-wrap gap-2 mb-4">
                    {(project.technologies || []).map((tech, techIndex) => (
                      <span 
                        key={techIndex}
                        className={`text-xs px-3 py-1 rounded-full ${
                          theme === "light"
                            ? "bg-rose-100 text-rose-800"
                            : "bg-indigo-900/40 text-indigo-300"
                        }`}
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  {/* Links */}
                  <div className="flex space-x-4 mt-auto">
                    {project.demoLink && (
                      <motion.a
                        href={project.demoLink}
                        target="_blank"
                        rel="noopener noreferrer"
                        className={`text-sm font-medium ${
                          theme === "light" ? "text-rose-600" : "text-indigo-400"
                        } hover:underline inline-flex items-center`}
                        onClick={handleDemoClick}
                        whileHover={{ x: 3 }}
                      >
                        {currentLanguage?.projects?.viewDemo || "View Demo"}
                        <svg className="w-4 h-4 ml-1" viewBox="0 0 20 20" fill="currentColor">
                          <path d="M11 3a1 1 0 100 2h2.586l-6.293 6.293a1 1 0 101.414 1.414L15 6.414V9a1 1 0 102 0V4a1 1 0 00-1-1h-5z" />
                          <path d="M5 5a2 2 0 00-2 2v8a2 2 0 002 2h8a2 2 0 002-2v-3a1 1 0 10-2 0v3H5V7h3a1 1 0 000-2H5z" />
                        </svg>
                      </motion.a>
                    )}
                    {project.codeLink && (
                      <motion.a
                        href={project.codeLink}
                        target="_blank"
                        rel="noopener noreferrer"
                        className={`text-sm font-medium ${
                          theme === "light" ? "text-rose-600" : "text-indigo-400"
                        } hover:underline inline-flex items-center`}
                        whileHover={{ x: 3 }}
                      >
                        {currentLanguage?.projects?.viewCode || "View Code"}
                        <svg className="w-4 h-4 ml-1" fill="currentColor" viewBox="0 0 24 24">
                          <path fillRule="evenodd" clipRule="evenodd" d="M12 2C6.477 2 2 6.477 2 12c0 4.42 2.865 8.17 6.839 9.49.5.092.682-.217.682-.48 0-.237-.008-.866-.013-1.7-2.782.603-3.369-1.34-3.369-1.34-.454-1.156-1.11-1.464-1.11-1.464-.908-.62.069-.608.069-.608 1.003.07 1.531 1.03 1.531 1.03.892 1.529 2.341 1.087 2.91.832.092-.647.35-1.088.636-1.338-2.22-.253-4.555-1.11-4.555-4.943 0-1.091.39-1.984 1.029-2.683-.103-.253-.446-1.272.098-2.65 0 0 .84-.27 2.75 1.026A9.564 9.564 0 0112 6.844c.85.004 1.705.115 2.504.337 1.909-1.296 2.747-1.027 2.747-1.027.546 1.379.202 2.398.1 2.651.64.7 1.028 1.592 1.028 2.683 0 3.841-2.337 4.687-4.565 4.935.359.309.678.92.678 1.855 0 1.338-.012 2.419-.012 2.747 0 .268.18.58.688.482A10.019 10.019 0 0022 12c0-5.523-4.477-10-10-10z" />
                        </svg>
                      </motion.a>
                    )}
                  </div>
                </div>
              </ProjectCard>
            </motion.div>
          ))}
        </div>

        {/* Alert Notification */}
        <AnimatePresence>
          {showAlert && (
            <motion.div 
              className={`fixed z-50 top-24 left-1/2 -translate-x-1/2 py-3 px-6 rounded-xl shadow-lg
                ${theme === "light" 
                  ? "bg-white/90 text-gray-800 border border-rose-500/30" 
                  : "bg-gray-800/90 text-white border border-indigo-500/30"
                } backdrop-blur-md flex items-center space-x-3 min-w-[220px] justify-center`}
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5, type: 'spring', stiffness: 300 }}
            >
              <svg className={`w-5 h-5 ${theme === "light" ? "text-rose-500" : "text-indigo-400"}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span className="font-medium">{alertMessage}</span>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Popup for coming soon features */}
        <AnimatePresence>
          {showPopup && <Popup />}
        </AnimatePresence>
      </div>
    </motion.section>
  );
};

export default Projects;
