import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages.js";

const ProjectCard = ({ project, onClick, theme }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <motion.div
      className={`relative overflow-hidden border-2 ${
        theme === "light" 
          ? "border-gray-800 bg-white" 
          : "border-gray-100 bg-gray-900"
      } cursor-pointer`}
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, margin: "-100px" }}
      transition={{ duration: 0.5 }}
      whileHover={{ y: -8 }}
      onHoverStart={() => setIsHovered(true)}
      onHoverEnd={() => setIsHovered(false)}
      onClick={onClick}
    >
      {/* Top border accent */}
      <div 
        className={`h-2 w-full ${
          theme === "light" ? "bg-rose-500" : "bg-indigo-500"
        }`}
      />

      {/* Project Image */}
      <div className="relative overflow-hidden">
        <div 
          className="aspect-video bg-cover bg-center transition-transform duration-500"
          style={{ 
            backgroundImage: `url(${project.image})`,
            transform: isHovered ? "scale(1.05)" : "scale(1)"
          }}
        />

        {/* Overlay on hover */}
        <motion.div 
          className={`absolute inset-0 ${
            theme === "light" 
              ? "bg-rose-500/10" 
              : "bg-indigo-500/10"
          }`}
          initial={{ opacity: 0 }}
          animate={{ opacity: isHovered ? 1 : 0 }}
          transition={{ duration: 0.3 }}
        />
      </div>

      {/* Content */}
      <div className="p-6">
        <h3 className={`text-xl font-bold mb-2 ${
          theme === "light" ? "text-gray-900" : "text-white"
        }`}>
          {project.title}
        </h3>

        <p className={`mb-4 line-clamp-2 ${
          theme === "light" ? "text-gray-700" : "text-gray-300"
        }`}>
          {project.description}
        </p>

        {/* Tags */}
        <div className="flex flex-wrap gap-2 mb-4">
          {project.tags.map((tag, index) => (
            <div 
              key={index}
              className={`text-xs px-2 py-1 ${
                theme === "light" 
                  ? "bg-gray-100 text-gray-800 border border-gray-200" 
                  : "bg-gray-800 text-gray-200 border border-gray-700"
              }`}
            >
              {tag}
            </div>
          ))}
        </div>

        {/* View More Button */}
        <div className="flex justify-end">
          <motion.div
            className={`inline-flex items-center space-x-1 font-medium ${
              theme === "light" ? "text-rose-600" : "text-indigo-400"
            }`}
            animate={{ x: isHovered ? 5 : 0 }}
            transition={{ duration: 0.2 }}
          >
            <span>View Project</span>
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M5 12h14M12 5l7 7-7 7"/>
            </svg>
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

const Projects = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];
  const [selectedProject, setSelectedProject] = useState(null);

  const handleViewProject = (project) => {
    setSelectedProject(project);
  };

  const closeProjectDetails = () => {
    setSelectedProject(null);
  };

  return (
    <section id="projects" className="py-24 relative overflow-hidden">
      {/* Background Grid */}
      <div 
        className={`absolute inset-0 ${
          theme === "light" ? "opacity-[0.015]" : "opacity-[0.03]"
        }`}
        style={{
          backgroundImage: `
            linear-gradient(to right, ${theme === "light" ? "black" : "white"} 1px, transparent 1px),
            linear-gradient(to bottom, ${theme === "light" ? "black" : "white"} 1px, transparent 1px)
          `,
          backgroundSize: '5rem 5rem'
        }}
      />

      {/* Background Shapes */}
      {[1, 2, 3].map((i) => (
        <motion.div
          key={i}
          className={`absolute pointer-events-none ${
            theme === "light" 
              ? i % 2 === 0 ? "bg-rose-500/10" : "bg-gray-800/5" 
              : i % 2 === 0 ? "bg-indigo-500/10" : "bg-gray-200/5"
          } ${i % 2 === 0 ? 'rounded-full' : ''}`}
          style={{
            width: 200 + i * 100,
            height: 200 + i * 100,
            top: `${20 + i * 25}%`,
            left: i % 2 === 0 ? `-${i * 5}%` : `${80 + i * 5}%`,
            zIndex: 0
          }}
          animate={{
            x: [0, i * 20, -i * 30, i * 10, 0],
            y: [0, -i * 20, i * 30, -i * 10, 0],
            rotate: [0, i * 10, i * 20, i * 10, 0]
          }}
          transition={{
            duration: 20 + i * 5,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}

      <div className="container mx-auto px-4 relative z-10">
        {/* Section Header */}
        <div className="mb-16 relative">
          <motion.div
            initial={{ x: -30, opacity: 0 }}
            whileInView={{ x: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
            className={`absolute -left-3 top-0 h-16 w-3 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
          />
          <motion.h2 
            className={`text-5xl md:text-6xl font-bold mb-6 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}
            initial={{ y: 30, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
          >
            {currentLanguage.projects.title}
          </motion.h2>
          <motion.p
            className={`text-xl max-w-3xl ${
              theme === "light" ? "text-gray-700" : "text-gray-300"
            }`}
            initial={{ y: 20, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            {currentLanguage.projects.description}
          </motion.p>
        </div>

        {/* Project Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {currentLanguage.projects.items.map((project, index) => (
            <ProjectCard
              key={index}
              project={project}
              onClick={() => handleViewProject(project)}
              theme={theme}
            />
          ))}
        </div>
      </div>

      {/* Project Detail Modal */}
      <AnimatePresence>
        {selectedProject && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 flex items-center justify-center z-50 p-4"
            onClick={closeProjectDetails}
          >
            <div 
              className={`absolute inset-0 ${
                theme === "light" ? "bg-black/70" : "bg-black/80"
              }`}
            />

            <motion.div
              initial={{ scale: 0.9, opacity: 0, y: 20 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.9, opacity: 0, y: 20 }}
              transition={{ type: "spring", damping: 25 }}
              className={`max-w-4xl w-full overflow-hidden border-2 ${
                theme === "light" 
                  ? "border-gray-800 bg-white" 
                  : "border-gray-100 bg-gray-900"
              }`}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Header Bar */}
              <div className={`p-4 flex justify-between items-center ${
                theme === "light" 
                  ? "bg-gray-900 text-white" 
                  : "bg-gray-100 text-gray-900"
              }`}>
                <h3 className="text-xl font-bold">{selectedProject.title}</h3>
                <button 
                  onClick={closeProjectDetails}
                  className="w-8 h-8 flex items-center justify-center rounded-full hover:bg-black/10"
                >
                  âœ•
                </button>
              </div>

              {/* Project Image */}
              <div 
                className="w-full aspect-video bg-cover bg-center"
                style={{ backgroundImage: `url(${selectedProject.image})` }}
              />

              {/* Project Details */}
              <div className="p-6">
                <div className="mb-6">
                  <h4 className={`text-lg font-bold mb-2 ${
                    theme === "light" ? "text-gray-900" : "text-white"
                  }`}>
                    {currentLanguage.projects.details.description}
                  </h4>
                  <p className={`${
                    theme === "light" ? "text-gray-700" : "text-gray-300"
                  }`}>
                    {selectedProject.description}
                  </p>
                </div>

                <div className="mb-6">
                  <h4 className={`text-lg font-bold mb-2 ${
                    theme === "light" ? "text-gray-900" : "text-white"
                  }`}>
                    {currentLanguage.projects.details.technologies}
                  </h4>
                  <div className="flex flex-wrap gap-2">
                    {selectedProject.tags.map((tag, index) => (
                      <div 
                        key={index}
                        className={`px-3 py-1 ${
                          theme === "light" 
                            ? "bg-gray-100 text-gray-800 border border-gray-200" 
                            : "bg-gray-800 text-gray-200 border border-gray-700"
                        }`}
                      >
                        {tag}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex flex-wrap gap-4">
                  {selectedProject.live && (
                    <a 
                      href={selectedProject.live} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className={`px-5 py-3 font-bold ${
                        theme === "light"
                          ? "bg-rose-500 text-white hover:bg-rose-600"
                          : "bg-indigo-500 text-white hover:bg-indigo-600"
                      } transition-colors`}
                    >
                      {currentLanguage.projects.details.liveDemo}
                    </a>
                  )}

                  {selectedProject.github && (
                    <a 
                      href={selectedProject.github} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className={`px-5 py-3 font-bold ${
                        theme === "light"
                          ? "bg-gray-900 text-white hover:bg-black"
                          : "bg-gray-800 text-white hover:bg-gray-900"
                      } transition-colors`}
                    >
                      {currentLanguage.projects.details.codeRepo}
                    </a>
                  )}
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </section>
  );
};

export default Projects;
