import React, { useState, useEffect, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import ThemeToggle from "./ThemeToggle";
import ResumeButton from "./ResumeButton";
import LanguageSwitcher from "./LanguageSwitcher";

// Define the navigation items with subtle hover animation
const NavItem = ({ name, href, isActive, onClick }) => {
  const { theme } = useTheme();

  return (
    <li className="relative">
      <a 
        href={href} 
        onClick={onClick}
        className={`relative block py-2 px-4 text-sm font-medium transition-all duration-300
          ${isActive 
            ? theme === "light" 
              ? "text-indigo-600" 
              : "text-indigo-400" 
            : theme === "light" 
              ? "text-slate-600 hover:text-indigo-600" 
              : "text-slate-300 hover:text-indigo-400"
          }
        `}
      >
        {name}
        <span 
          className={`absolute inset-x-0 bottom-0 h-0.5 transform origin-left transition-transform duration-300 ease-out ${
            isActive
              ? "scale-x-100 " + (theme === "light" ? "bg-indigo-600" : "bg-indigo-400")
              : "scale-x-0 " + (theme === "light" ? "bg-indigo-600/0" : "bg-indigo-400/0")
          }`}
        />
      </a>
    </li>
  );
};

const Header = ({ activeSection, onSectionChange }) => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [visible, setVisible] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(0);

  // Set up navigation items
  const navItems = [
    { id: 'home', name: text?.header?.home || 'Home', href: '#home' },
    { id: 'skills', name: text?.header?.skills || 'Skills', href: '#skills' },
    { id: 'projects', name: text?.header?.projects || 'Projects', href: '#projects' },
    { id: 'services', name: text?.header?.services || 'Services', href: '#services' },
    { id: 'contact', name: text?.header?.contact || 'Contact', href: '#contact' },
  ];

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;

      // Set scrolled state for styling
      setScrolled(currentScrollPos > 20);

      // Hide/show header based on scroll direction (only when significantly scrolled)
      if (currentScrollPos > 100) {
        setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 100);
      } else {
        setVisible(true);
      }

      setPrevScrollPos(currentScrollPos);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [prevScrollPos]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleNavClick = (e, section) => {
    e.preventDefault();

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    // Use the provided callback to change section
    if (onSectionChange) {
      onSectionChange(section);
    }
  };

  return (
    <motion.header 
      className={`fixed w-full z-40 transition-all duration-300
        ${scrolled 
          ? theme === "light" 
            ? "bg-white/80 backdrop-blur-md shadow-sm" 
            : "bg-slate-950/80 backdrop-blur-md border-b border-slate-800/50"
          : "bg-transparent"
        }
        ${visible ? "translate-y-0" : "-translate-y-full"}
      `}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16 md:h-20">
          {/* Logo */}
          <a 
            href="#home" 
            onClick={(e) => handleNavClick(e, 'home')}
            className="flex items-center gap-2"
          >
            <motion.div 
              initial={{ scale: 1 }}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className={`relative flex items-center justify-center w-9 h-9 rounded-lg bg-gradient-to-br ${
                theme === "light"
                  ? "from-indigo-500 to-violet-500" 
                  : "from-indigo-600 to-violet-600"
              }`}
            >
              <span className="text-white font-bold text-lg">Y</span>
              <div className="absolute inset-0 rounded-lg bg-white/20 opacity-0 hover:opacity-20 transition-opacity duration-300"></div>
            </motion.div>

            <span className={`text-lg font-bold ${
              theme === "light" ? "text-slate-800" : "text-white"
            }`}>
              {text?.header?.name || 'Yutaka Yamaguchi'}
            </span>
          </a>

          {/* Desktop Navigation */}
          <nav className="hidden md:block">
            <ul className="flex space-x-1">
              {navItems.map((item) => (
                <NavItem
                  key={item.id}
                  name={item.name}
                  href={item.href}
                  onClick={(e) => handleNavClick(e, item.id)}
                  isActive={activeSection === item.id}
                />
              ))}
            </ul>
          </nav>

          {/* Actions */}
          <div className="flex items-center space-x-3">
            {/* Language Switcher */}
            <LanguageSwitcher />

            {/* Theme Toggle */}
            <ThemeToggle />

            {/* Resume Button - Desktop */}
            <div className="hidden md:block">
              <ResumeButton />
            </div>

            {/* Mobile menu button */}
            <button
              onClick={toggleMobileMenu}
              className={`md:hidden p-2 rounded-lg ${
                theme === "light" 
                  ? "text-slate-700 hover:bg-slate-100" 
                  : "text-slate-300 hover:bg-slate-800"
              }`}
            >
              {isMobileMenuOpen ? (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: 'auto' }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
            className={`md:hidden overflow-hidden ${
              theme === "light" 
                ? "bg-white/90 backdrop-blur-md"
                : "bg-slate-900/90 backdrop-blur-md"
            }`}
          >
            <div className="container mx-auto px-4 py-4">
              <nav>
                <ul className="flex flex-col space-y-2">
                  {navItems.map((item) => (
                    <motion.li
                      key={item.id}
                      initial={{ opacity: 0, x: -10 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: -10 }}
                      transition={{ duration: 0.2 }}
                    >
                      <a 
                        href={item.href}
                        onClick={(e) => handleNavClick(e, item.id)}
                        className={`block py-2 px-4 rounded-lg ${
                          activeSection === item.id
                            ? theme === "light"
                              ? "bg-indigo-100 text-indigo-600"
                              : "bg-indigo-900/30 text-indigo-400"
                            : theme === "light"
                              ? "text-slate-700 hover:bg-slate-100"
                              : "text-slate-300 hover:bg-slate-800"
                        }`}
                      >
                        {item.name}
                      </a>
                    </motion.li>
                  ))}

                  {/* Resume button in mobile menu */}
                  <motion.li
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -10 }}
                    transition={{ duration: 0.2, delay: 0.2 }}
                  >
                    <div className="px-4 py-2">
                      <ResumeButton />
                    </div>
                  </motion.li>
                </ul>
              </nav>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.header>
  );
};

export default Header;
