import React, { useState, useRef } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";

const TiltCard = ({ children, speed = 50 }) => {
  const cardRef = useRef(null);
  const [tiltStyle, setTiltStyle] = useState({
    transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
    transition: 'all 0.4s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
  });
  const [glarePosition, setGlarePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const card = cardRef.current;
    const cardRect = card.getBoundingClientRect();
    const cardCenterX = cardRect.left + cardRect.width / 2;
    const cardCenterY = cardRect.top + cardRect.height / 2;

    // Calculate cursor position relative to card center
    const cursorX = e.clientX;
    const cursorY = e.clientY;

    // Calculate rotation values
    const rotateY = ((cursorX - cardCenterX) / (cardRect.width / 2)) * (speed / 10);
    const rotateX = -((cursorY - cardCenterY) / (cardRect.height / 2)) * (speed / 10);

    // Update glare position based on cursor
    const glareX = ((cursorX - cardRect.left) / cardRect.width) * 100;
    const glareY = ((cursorY - cardRect.top) / cardRect.height) * 100;

    setGlarePosition({ x: glareX, y: glareY });

    // Apply transformation
    setTiltStyle({
      transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale3d(1.02, 1.02, 1.02)`,
      transition: 'all 0.4s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setTiltStyle({
      transform: 'perspective(1000px) rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)',
      transition: 'all 0.4s cubic-bezier(0.03, 0.98, 0.52, 0.99) 0s',
    });
  };

  return (
    <div
      ref={cardRef}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={tiltStyle}
      className="relative h-full w-full"
    >
      {children}
      {isHovered && (
        <div
          className="absolute inset-0 pointer-events-none overflow-hidden rounded-xl opacity-30"
          style={{
            background: `radial-gradient(circle at ${glarePosition.x}% ${glarePosition.y}%, rgba(255, 255, 255, 0.8) 0%, transparent 50%)`,
          }}
        />
      )}
    </div>
  );
};

const Services = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const [clickedIndex, setClickedIndex] = useState(null);

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0, transition: { staggerChildren: 0.2 } },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.8 } },
    hover: {
      scale: 1.02,
      y: -5,
      transition: { duration: 0.2 }
    },
    tap: {
      scale: 0.98,
      transition: { duration: 0.1 }
    }
  };

  const handleServiceClick = (index) => {
    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  const services = text.services.items;

  return (
    <motion.section
      id="services"
      className="min-h-screen py-24 px-8 flex flex-col justify-center relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        {/* Section Title */}
        <motion.h2
          className={`text-3xl font-bold text-center mb-5
            ${theme === "dark" ? "neon-text" : "text-light-accent"}`}
          variants={itemVariants}
        >
          {text.services.title}
        </motion.h2>
        <motion.p
          className={`text-base text-center mb-10 max-w-3xl mx-auto
            ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
          variants={itemVariants}
        >
          {text.services.description}
        </motion.p>

        {/* Service Cards */}
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
          variants={containerVariants}
        >
          {services.map((service, index) => (
            <motion.div
              key={index}
              className={`relative rounded-2xl overflow-hidden h-full
                ${theme === "light" 
                  ? "bg-white/80 shadow-[0_8px_32px_rgba(0,0,0,0.1)]" 
                  : "bg-dark-card/80 shadow-[0_8px_32px_rgba(0,0,0,0.3)]"
                } backdrop-blur-sm border border-opacity-20
                ${theme === "light" ? "border-gray-200" : "border-gray-700"}`}
              variants={itemVariants}
              whileHover="hover"
              whileTap="tap"
              onClick={() => handleServiceClick(index)}
            >
              <TiltCard>
                <div className="p-6 relative z-10">
                  <div className="relative mb-4">
                    <div className={`absolute -left-3 top-0 h-full w-1 rounded-full 
                      ${theme === "light" 
                        ? "bg-gradient-to-b from-light-accent to-orange-400" 
                        : "bg-gradient-to-b from-dark-accent to-blue-500"
                      }`} 
                    />
                    <h3
                      className={`text-xl font-semibold ml-1
                        ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}
                    >
                      {service.name}
                    </h3>
                  </div>
                  <p
                    className={`text-sm leading-relaxed
                      ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}
                  >
                    {service.description}
                  </p>
                </div>
              </TiltCard>
              <motion.div 
                className={`absolute inset-0 rounded-xl opacity-0 transition-all duration-300
                  ${theme === "dark" 
                    ? "bg-gradient-to-br from-dark-accent/10 to-dark-hover/10" 
                    : "bg-gradient-to-br from-light-accent/10 to-orange-400/10"
                  }`}
                animate={{
                  opacity: clickedIndex === index ? 1 : 0
                }}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Services;
