import React, { useState } from "react";
import { motion } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages.js";

// Tilt card component for a subtle 3D effect
const TiltCard = ({ children, onClick }) => {
  return (
    <motion.div
      className="relative overflow-hidden rounded-xl bg-gradient-to-b from-transparent to-transparent"
      whileHover={{ scale: 1.02 }}
      transition={{ duration: 0.3 }}
      onClick={onClick}
    >
      {children}
    </motion.div>
  );
};

// Main project component
const Projects = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [clickedIndex, setClickedIndex] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState("");

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.6 },
    },
  };

  // Project Card component for reuse
  const ProjectCard = ({ children, onClick }) => (
    <TiltCard onClick={onClick}>
      <div
        className={`h-full rounded-xl ${
          theme === "light"
            ? "bg-white shadow-xl"
            : "bg-gray-800/80 shadow-lg shadow-gray-900/30"
        }`}
      >
        {children}
      </div>
    </TiltCard>
  );

  const handleDemoClick = (e) => {
    e.preventDefault();
    setPopupMessage("Live Demo Coming Soon!");
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 1500);
  };

  const handleRepoClick = (project, e) => {
    e.preventDefault();

    // Check if the project has a repo URL defined
    if (!project.repoUrl || project.repoUrl === "#" || project.repoUrl === "") {
      setPopupMessage("Repository Coming Soon!");
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 1500);
      return;
    }

    // Make sure URL has proper protocol
    let url = project.repoUrl;
    if (!url.startsWith('http://') && !url.startsWith('https://')) {
      url = 'https://' + url;
    }

    window.open(url, '_blank');
  };

  const handleCardClick = (index) => {
    if (clickedIndex === index) {
      setClickedIndex(null);
      return;
    }

    setClickedIndex(index);
    setTimeout(() => setClickedIndex(null), 600);
  };

  // Popup component
  const Popup = () => (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className={`px-6 py-4 rounded-lg shadow-lg ${theme === "light" ? "bg-white text-gray-800" : "bg-gray-800 text-white"}`}>
        <p className="text-center font-medium">{popupMessage}</p>
      </div>
    </div>
  );

  return (
    <motion.section
      id="projects"
      className={`min-h-screen py-20 px-8 ${
        theme === "light" ? "bg-gray-50" : "bg-gray-900"
      }`}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      <div className="container mx-auto max-w-5xl">
        {/* Section title */}
        <motion.h2
          className={`text-3xl font-bold text-center mb-4 ${
            theme === "light" ? "text-light-accent" : "text-dark-accent"
          }`}
          variants={itemVariants}
        >
          {currentLanguage.projects.title}
        </motion.h2>

        {/* Section description */}
        <motion.p className="text-center mb-12 max-w-3xl mx-auto" variants={itemVariants}>
          {currentLanguage.projects.description}
        </motion.p>

        {/* Projects grid */}
        <motion.div 
          className="grid grid-cols-1 md:grid-cols-2 gap-8"
          variants={containerVariants}
        >
          {currentLanguage.projects.items.map((project, index) => (
            <motion.div
              key={index}
              variants={itemVariants}
              className={`rounded-xl transition-all duration-300 
                  ${theme === "light" ? "border-gray-200/50" : "border-gray-700/50"}`}
            >
              <ProjectCard onClick={() => handleCardClick(index)}>
                {/* Card Content */}
                <div className="p-6 relative z-10">
                  <h3 className={`text-xl font-bold mb-3 ${
                    theme === "light" ? "text-gray-800" : "text-white"
                  }`}>
                    {project.name}
                  </h3>

                  <p className={`mb-6 ${
                    theme === "light" ? "text-gray-600" : "text-gray-300"
                  }`}>
                    {project.description}
                  </p>

                  {/* Technologies list */}
                  <div className="mb-6">
                    <p className={`text-sm mb-2 ${
                      theme === "light" ? "text-gray-700" : "text-gray-300"
                    }`}>
                      {currentLanguage.projects.technologiesUsed}
                    </p>
                    <div className="flex flex-wrap gap-2">
                      {project.tech.map((tech, techIndex) => (
                        <span
                          key={techIndex}
                          className={`text-xs px-3 py-1 rounded-full ${
                            theme === "light"
                              ? "bg-gray-100 text-gray-800"
                              : "bg-gray-700 text-gray-200"
                          }`}
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>

                  {/* Action buttons */}
                  <div className="flex justify-between mt-8 pt-4 border-t border-gray-200/30">
                    <motion.button
                      onClick={handleDemoClick}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className={`px-4 py-2 rounded-lg mr-2 ${
                        theme === "light"
                          ? "bg-light-accent text-white hover:bg-orange-600"
                          : "bg-dark-accent text-black hover:bg-green-700"
                      }`}
                    >
                      {currentLanguage.projects.demo}
                    </motion.button>
                    <motion.button
                      onClick={(e) => handleRepoClick(project, e)}
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      className="px-4 py-2 rounded-lg bg-gray-600 text-white hover:bg-gray-700"
                    >
                      {currentLanguage.projects.repo}
                    </motion.button>
                  </div>
                </div>
              </ProjectCard>
            </motion.div>
          ))}
        </motion.div>
      </div>

      {showPopup && <Popup />}
    </motion.section>
  );
};

export default Projects;
