import React, { useState, useEffect } from "react";
import { ThemeProvider } from "./contexts/ThemeContext";
import { LanguageProvider } from "./contexts/LanguageContext";
import { useTheme } from "./contexts/ThemeContext";
import Header from "./components/Header";
import Home from "./components/Home";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import LoadingScreen from "./components/LoadingScreen";
import Services from "./components/Services";
import PageTransition from "./components/PageTransition";
import ScrollToTop from "./components/ScrollToTop";
import Footer from "./components/Footer";
import { motion, useScroll, useSpring } from "framer-motion";

// Scroll progress indicator component
const ScrollProgress = () => {
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });
  const { theme } = useTheme();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsVisible(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <motion.div
      className="fixed top-0 left-0 right-0 h-1.5 z-50 origin-left"
      style={{ 
        scaleX,
        opacity: isVisible ? 1 : 0,
        background: theme === "light" 
          ? "linear-gradient(to right, #f43f5e, #ec4899)" 
          : "linear-gradient(to right, #6366f1, #8b5cf6)",
        boxShadow: theme === "light"
          ? "0 0 10px rgba(244, 63, 94, 0.5)"
          : "0 0 10px rgba(99, 102, 241, 0.5)"
      }}
      transition={{ opacity: { duration: 0.3 } }}
    />
  );
};

// Particle Background component
const ParticleBackground = ({ theme }) => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const createParticles = () => {
      const newParticles = [];
      const count = window.innerWidth < 768 ? 20 : 40;

      for (let i = 0; i < count; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * 100,
          y: Math.random() * 100,
          size: Math.random() * 3 + 1,
          opacity: Math.random() * 0.3 + 0.1,
          speedX: (Math.random() - 0.5) * 0.3,
          speedY: (Math.random() - 0.5) * 0.3,
          duration: Math.random() * 15 + 15
        });
      }

      setParticles(newParticles);
    };

    createParticles();

    window.addEventListener('resize', createParticles);
    return () => window.removeEventListener('resize', createParticles);
  }, []);

  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none z-0">
      {particles.map((particle) => (
        <motion.div
          key={particle.id}
          className="absolute rounded-full"
          style={{
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            opacity: particle.opacity,
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            backgroundColor: theme === "light" ? "rgba(230, 73, 128, 0.7)" : "rgba(99, 102, 241, 0.7)",
            filter: `blur(${particle.size > 2 ? '1px' : '0'})`,
          }}
          animate={{
            x: [0, particle.speedX * 50, 0],
            y: [0, particle.speedY * 50, 0],
            scale: [1, particle.size > 2 ? 1.1 : 1, 1],
          }}
          transition={{
            duration: particle.duration,
            repeat: Infinity,
            repeatType: "reverse",
            ease: "easeInOut",
          }}
        />
      ))}
    </div>
  );
};

// Animated background gradients
const AnimatedBackground = ({ theme }) => {
  return (
    <div className="fixed inset-0 overflow-hidden pointer-events-none">
      <motion.div 
        className="absolute w-[700px] h-[700px] rounded-full blur-3xl -top-64 -right-64"
        style={{
          backgroundColor: theme === "light" ? "rgba(230, 73, 128, 0.15)" : "rgba(99, 102, 241, 0.2)",
        }}
        animate={{ 
          y: [0, 30, -20, 0],
          x: [0, -20, 10, 0],
          scale: [1, 1.05, 0.95, 1],
          rotate: [0, 3, -3, 0],
        }}
        transition={{ 
          duration: 20, 
          repeat: Infinity,
          repeatType: "loop",
          ease: "easeInOut"
        }}
      />
      <motion.div 
        className="absolute w-[700px] h-[700px] rounded-full blur-3xl -bottom-64 -left-64"
        style={{
          backgroundColor: theme === "light" ? "rgba(251, 191, 36, 0.15)" : "rgba(139, 92, 246, 0.2)",
        }}
        animate={{ 
          y: [0, -30, 20, 0],
          x: [0, 20, -10, 0],
          scale: [1, 0.95, 1.05, 1],
          rotate: [0, -3, 3, 0],
        }}
        transition={{ 
          duration: 25, 
          repeat: Infinity,
          repeatType: "loop",
          ease: "easeInOut",
          delay: 1
        }}
      />
      <motion.div 
        className="absolute w-[500px] h-[500px] rounded-full blur-3xl top-1/4 left-1/3"
        style={{
          backgroundColor: theme === "light" ? "rgba(56, 189, 248, 0.1)" : "rgba(139, 92, 246, 0.1)",
        }}
        animate={{ 
          y: [0, 25, -15, 0],
          x: [0, -15, 20, 0],
          scale: [1, 1.02, 0.98, 1],
          rotate: [0, 5, -5, 0],
        }}
        transition={{ 
          duration: 30, 
