// Mouse-following beam effect component
const MouseFollowingBeam = ({ theme }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      // Get mouse position relative to the viewport
      const { clientX, clientY } = e;
      setMousePosition({ x: clientX, y: clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <motion.div
      className="fixed top-0 left-0 w-full h-full pointer-events-none z-10"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      {/* Outer subtle glow */}
      <motion.div
        className={`absolute rounded-full ${
          theme === "light"
            ? "bg-gradient-to-r from-rose-400/20 via-pink-500/20 to-rose-400/20"
            : "bg-gradient-to-r from-indigo-500/20 via-violet-600/20 to-indigo-500/20"
        }`}
        style={{
          width: '350px',  
          height: '350px',
          filter: 'blur(80px)',
        }}
        animate={{ 
          left: mousePosition.x - 175,
          top: mousePosition.y - 175,
        }}
        transition={{
          type: "spring",
          stiffness: 500,
          damping: 30,
          mass: 0.5
        }}
      />

      {/* Core of the beam - softer center */}
      <motion.div
        className={`absolute rounded-full ${
          theme === "light"
            ? "bg-gradient-to-r from-rose-300/20 via-pink-400/20 to-rose-300/20"
            : "bg-gradient-to-r from-indigo-400/20 via-violet-500/20 to-indigo-400/20"
        }`}
        style={{
          width: '120px',
          height: '120px',
          filter: 'blur(30px)',
          opacity: 0.6,
        }}
        animate={{ 
          left: mousePosition.x - 60,
          top: mousePosition.y - 60,
        }}
        transition={{
          type: "spring",
          stiffness: 800,
          damping: 30,
          mass: 0.2
        }}
      />
    </motion.div>
  );
};

// Wrapper component to apply theme
const AppContent = () => {
  const { theme } = useTheme();
  const [isLoading, setIsLoading] = useState(true);
  const [currentSection, setCurrentSection] = useState("home");

  // Track current section based on scroll position
  useEffect(() => {
    const sections = ["home", "skills", "projects", "services", "contact"];

    const handleScroll = () => {
      const scrollY = window.scrollY;
      const windowHeight = window.innerHeight;

      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          // If section is in view (or close to)
          if (rect.top <= windowHeight * 0.5 && rect.bottom > windowHeight * 0.2) {
            setCurrentSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Simulate loading
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  // Add light/dark theme class to the body
  useEffect(() => {
    const root = document.documentElement;

    // Update body class for theme-specific styling
    if (theme === "light") {
      document.body.classList.add("light-theme");
      document.body.classList.remove("dark-theme");
    } else {
      document.body.classList.add("dark-theme");
      document.body.classList.remove("light-theme");
    }
  }, [theme]);

  return (
    <div className={`app-container ${theme === "light" ? "light-theme" : "dark-theme"}`}>
      {isLoading ? (
        <LoadingScreen theme={theme} />
      ) : (
        <>
          <ScrollProgress />

          {/* Backgrounds */}
          <ParticleBackground theme={theme} />
          <AnimatedBackground theme={theme} />
          <MouseFollowingBeam theme={theme} />

          {/* Apply the noise overlay */}
          {theme === "light" ? (
            <div className="light-noise-overlay"></div>
          ) : (
            <div className="dark-noise-overlay"></div>
          )}

          <Header currentSection={currentSection} />

          <main className="scroll-container">
            <div id="home">
              <PageTransition>
                <Home />
              </PageTransition>
            </div>

            <div id="skills">
              <PageTransition>
                <Skills />
              </PageTransition>
            </div>

            <div id="projects">
              <PageTransition>
                <Projects />
              </PageTransition>
            </div>

            <div id="services">
              <PageTransition>
                <Services />
              </PageTransition>
            </div>

            <div id="contact">
              <PageTransition>
                <Contact />
              </PageTransition>
            </div>
          </main>

          <Footer />
          <ScrollToTop theme={theme} />
        </>
      )}
    </div>
  );
};

const App = () => {
  return (
    <ThemeProvider>
      <LanguageProvider>
        <AppContent />
      </LanguageProvider>
    </ThemeProvider>
  );
};

export default App;
