import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';

const PageTransition = ({ children }) => {
  const { theme } = useTheme();
  const [loading, setLoading] = useState(true);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);

    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  if (!mounted) return null;

  return (
    <>
      <AnimatePresence mode="wait">
        {loading ? (
          <motion.div
            key="loader"
            className="fixed inset-0 flex items-center justify-center z-50"
            initial={{ opacity: 1 }}
            exit={{ 
              opacity: 0,
              transition: { 
                duration: 0.8, 
                ease: [0.43, 0.13, 0.23, 0.96]
              }
            }}
          >
            <motion.div 
              className={`absolute inset-0 ${
                theme === "light" 
                  ? "bg-gradient-to-br from-rose-100 to-white" 
                  : "bg-gradient-to-br from-slate-900 to-gray-900"
              }`}
              exit={{ 
                y: "-100%",
                transition: { 
                  duration: 0.8,
                  ease: [0.43, 0.13, 0.23, 0.96] 
                }
              }}
            />

            <motion.div 
              className="relative z-10 flex flex-col items-center"
              exit={{ 
                y: -50,
                opacity: 0,
                transition: { duration: 0.4 } 
              }}
            >
              <motion.div 
                className={`h-16 w-16 mb-8 rounded-full ${
                  theme === "light" 
                    ? "bg-rose-500" 
                    : "bg-indigo-500"
                }`}
                animate={{
                  scale: [1, 1.2, 1],
                  rotate: [0, 180, 0],
                  borderRadius: ["50%", "20%", "50%"]
                }}
                transition={{
                  duration: 2,
                  repeat: Infinity,
                  repeatType: "loop",
                  ease: "easeInOut"
                }}
              />
              <motion.div 
                className="flex space-x-3"
                animate={{
                  opacity: [0.5, 1, 0.5]
                }}
                transition={{
                  duration: 1.5,
                  repeat: Infinity,
                  repeatType: "loop"
                }}
              >
                <motion.div 
                  className={`h-3 w-3 rounded-full ${
                    theme === "light" 
                      ? "bg-rose-500" 
                      : "bg-indigo-500"
                  }`}
                  animate={{ y: [0, -10, 0] }}
                  transition={{ 
                    duration: 0.6, 
                    repeat: Infinity, 
                    repeatType: "loop",
                    ease: "easeInOut",
                    delay: 0 
                  }}
                />
                <motion.div 
                  className={`h-3 w-3 rounded-full ${
                    theme === "light" 
                      ? "bg-rose-500" 
                      : "bg-indigo-500"
                  }`}
                  animate={{ y: [0, -10, 0] }}
                  transition={{ 
                    duration: 0.6, 
                    repeat: Infinity, 
                    repeatType: "loop",
                    ease: "easeInOut",
                    delay: 0.15 
                  }}
                />
                <motion.div 
                  className={`h-3 w-3 rounded-full ${
                    theme === "light" 
                      ? "bg-rose-500" 
                      : "bg-indigo-500"
                  }`}
                  animate={{ y: [0, -10, 0] }}
                  transition={{ 
                    duration: 0.6, 
                    repeat: Infinity, 
                    repeatType: "loop",
                    ease: "easeInOut",
                    delay: 0.3 
                  }}
                />
              </motion.div>
            </motion.div>
          </motion.div>
        ) : (
          <motion.div
            key="content"
            initial={{ opacity: 0, y: 20 }}
            animate={{ 
              opacity: 1, 
              y: 0,
              transition: { 
                duration: 0.8, 
                ease: "easeOut", 
                delay: 0.2 
              }
            }}
            className="w-full"
          >
            {children}
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
};

export default PageTransition;
