import React from "react";
import { motion } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages.js";

const SkillBar = ({ skill, index, theme }) => {
  return (
    <motion.div 
      className="mb-6"
      initial={{ opacity: 0, x: -50 }}
      whileInView={{ opacity: 1, x: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5, delay: index * 0.05 }}
    >
      <div className="flex justify-between mb-2">
        <h3 className={`font-bold ${
          theme === "light" ? "text-gray-900" : "text-white"
        }`}>
          {skill.name}
        </h3>
        <span className={`font-mono ${
          theme === "light" ? "text-gray-700" : "text-gray-300"
        }`}>
          {skill.level}%
        </span>
      </div>

      <div className={`w-full h-4 relative ${
        theme === "light" ? "bg-gray-100" : "bg-gray-800"
      }`}>
        <motion.div 
          className={`absolute top-0 left-0 h-full ${
            theme === "light" ? "bg-rose-500" : "bg-indigo-500"
          }`}
          initial={{ width: 0 }}
          whileInView={{ width: `${skill.level}%` }}
          viewport={{ once: true }}
          transition={{ duration: 1, delay: 0.5 + index * 0.05 }}
        />
      </div>
    </motion.div>
  );
};

const SkillIcon = ({ skill, index, theme }) => {
  return (
    <motion.div 
      className={`flex flex-col items-center p-4 border-2 ${
        theme === "light" 
          ? "border-gray-800 bg-white" 
          : "border-gray-100 bg-gray-900"
      }`}
      initial={{ opacity: 0, y: 30 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true }}
      transition={{ duration: 0.5, delay: index * 0.05 }}
      whileHover={{ y: -5, transition: { duration: 0.2 } }}
    >
      {/* Top accent */}
      <div className={`h-1 w-16 mb-4 ${
        theme === "light" ? "bg-rose-500" : "bg-indigo-500"
      }`} />

      <div className="text-4xl mb-3">{skill.icon}</div>
      <h3 className={`font-bold text-center ${
        theme === "light" ? "text-gray-900" : "text-white"
      }`}>
        {skill.name}
      </h3>
    </motion.div>
  );
};

const Skills = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  return (
    <section id="skills" className="py-24 relative overflow-hidden">
      {/* Background Grid */}
      <div 
        className={`absolute inset-0 ${
          theme === "light" ? "opacity-[0.015]" : "opacity-[0.03]"
        }`}
        style={{
          backgroundImage: `
            linear-gradient(to right, ${theme === "light" ? "black" : "white"} 1px, transparent 1px),
            linear-gradient(to bottom, ${theme === "light" ? "black" : "white"} 1px, transparent 1px)
          `,
          backgroundSize: '5rem 5rem'
        }}
      />

      {/* Background Shapes */}
      {[1, 2, 3].map((i) => (
        <motion.div
          key={i}
          className={`absolute pointer-events-none ${
            theme === "light" 
              ? i % 2 === 0 ? "bg-rose-500/10" : "bg-gray-800/5" 
              : i % 2 === 0 ? "bg-indigo-500/10" : "bg-gray-200/5"
          } ${i === 1 ? 'rounded-none' : 'rounded-full'}`}
          style={{
            width: 200 + i * 100,
            height: 200 + i * 100,
            top: `${40 + i * 10}%`,
            left: i % 2 === 0 ? `${80 + i * 5}%` : `-${i * 5}%`,
            zIndex: 0
          }}
          animate={{
            x: [0, i * 20, -i * 30, i * 10, 0],
            y: [0, -i * 20, i * 30, -i * 10, 0],
            rotate: [0, i * 10, i * 20, i * 10, 0]
          }}
          transition={{
            duration: 20 + i * 5,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}

      <div className="container mx-auto px-4 relative z-10">
        {/* Section Header */}
        <div className="mb-16 relative">
          <motion.div
            initial={{ x: -30, opacity: 0 }}
            whileInView={{ x: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
            className={`absolute -left-3 top-0 h-16 w-3 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
          />
          <motion.h2 
            className={`text-5xl md:text-6xl font-bold mb-6 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}
            initial={{ y: 30, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
          >
            {currentLanguage.skills.title}
          </motion.h2>
          <motion.p
            className={`text-xl max-w-3xl ${
              theme === "light" ? "text-gray-700" : "text-gray-300"
            }`}
            initial={{ y: 20, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            {currentLanguage.skills.description}
          </motion.p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
          {/* Skill Bars */}
          <div>
            <motion.h3 
              className={`text-2xl font-bold mb-8 ${
                theme === "light" ? "text-gray-900" : "text-white"
              }`}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
            >
              {currentLanguage.skills.proficiency}
            </motion.h3>

            {currentLanguage.skills.skillBars.map((skill, index) => (
              <SkillBar 
                key={index} 
                skill={skill} 
                index={index}
                theme={theme}
              />
            ))}
          </div>

          {/* Technologies & Tools */}
          <div>
            <motion.h3 
              className={`text-2xl font-bold mb-8 ${
                theme === "light" ? "text-gray-900" : "text-white"
              }`}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
            >
              {currentLanguage.skills.technologies}
            </motion.h3>

            <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
              {currentLanguage.skills.techIcons.map((skill, index) => (
                <SkillIcon 
                  key={index} 
                  skill={skill} 
                  index={index}
                  theme={theme}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
