import React, { useState } from "react";
import { ThemeProvider } from "./contexts/ThemeContext";
import { LanguageProvider } from "./contexts/LanguageContext";
import { useTheme } from "./contexts/ThemeContext";
import Header from "./components/Header";
import Home from "./components/Home";
import Skills from "./components/Skills";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import LoadingScreen from "./components/LoadingScreen";
import Services from "./components/Services";

// Wrapper component to apply theme
const AppContent = () => {
  const { theme } = useTheme();
  const [isLoading, setIsLoading] = useState(true);

  return (
    <div className="font-sans relative">
      {isLoading ? (
        <LoadingScreen onComplete={() => setIsLoading(false)} />
      ) : (
        <div className={`min-h-screen relative overflow-hidden transition-colors duration-500
          ${theme === "light"
            ? "bg-gradient-to-br from-light-background via-white to-orange-50 text-gray-900"
            : "bg-gradient-to-br from-dark-background via-gray-900 to-blue-900 text-white"
          }`}
        >
          {/* Animated Background Elements - Now only in the main layout */}
          <div className="fixed inset-0 overflow-hidden pointer-events-none">
            <div className={`absolute w-[600px] h-[600px] rounded-full blur-3xl opacity-20
              ${theme === "light" ? "bg-orange-400" : "bg-blue-500"}
              -top-64 -right-64 animate-pulse`}
              style={{ animationDuration: '8s' }}
            />
            <div className={`absolute w-[600px] h-[600px] rounded-full blur-3xl opacity-20
              ${theme === "light" ? "bg-yellow-400" : "bg-purple-500"}
              -bottom-64 -left-64 animate-pulse delay-1000`}
              style={{ animationDuration: '10s' }}
            />
            {/* Additional ambient elements for more dynamic background */}
            <div className={`absolute w-[400px] h-[400px] rounded-full blur-3xl opacity-10
              ${theme === "light" ? "bg-blue-300" : "bg-purple-300"}
              top-1/4 left-1/3 animate-pulse`}
              style={{ animationDuration: '12s', animationDelay: '2s' }}
            />
          </div>

          <Header />
          <div className="scroll-container relative z-10">
            <div className="scroll-section">
              <Home />
            </div>
            <div className="scroll-section">
              <Skills />
            </div>
            <div className="scroll-section">
              <Services />
            </div>
            <div className="scroll-section">
              <Projects />
            </div>
            <div className="scroll-section">
              <Contact />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <ThemeProvider>
      <LanguageProvider>
        <AppContent />
      </LanguageProvider>
    </ThemeProvider>
  );
};

export default App;
