import React, { useState } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";

const Contact = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();
  const [formStatus, setFormStatus] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Use text from language context with fallbacks
  const contactText = {
    title: text?.contact?.title || "Contact Me",
    description: text?.contact?.description || "Feel free to reach out for collaboration or inquiries.",
    namePlaceholder: text?.contact?.namePlaceholder || "Your Name",
    emailPlaceholder: text?.contact?.emailPlaceholder || "Your Email",
    messagePlaceholder: text?.contact?.messagePlaceholder || "Your Message",
    sendButton: text?.contact?.sendButton || "Send Message",
    successMessage: text?.contact?.successMessage || "Message sent successfully!",
    errorMessage: text?.contact?.errorMessage || "Something went wrong. Please try again."
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        staggerChildren: 0.2, 
        delayChildren: 0.1,
        duration: 0.8 
      } 
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        duration: 0.6,
        type: "spring",
        stiffness: 100,
        damping: 15
      } 
    },
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setFormStatus('success');

      // Reset form
      e.target.reset();

      // Reset status after 5 seconds
      setTimeout(() => {
        setFormStatus(null);
      }, 5000);
    }, 1500);
  };

  // Notification component
  const Notification = ({ status }) => (
    <motion.div
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.3 }}
      className={`fixed top-24 left-1/2 transform -translate-x-1/2 z-50 rounded-lg shadow-lg px-6 py-4 flex items-center ${
        status === 'success'
          ? theme === 'light' 
            ? 'bg-white text-green-800 border border-green-200' 
            : 'bg-slate-800 text-green-400 border border-green-900'
          : theme === 'light'
            ? 'bg-white text-red-800 border border-red-200'
            : 'bg-slate-800 text-red-400 border border-red-900'
      }`}
    >
      {status === 'success' ? (
        <svg className="w-5 h-5 mr-2 text-green-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
        </svg>
      ) : (
        <svg className="w-5 h-5 mr-2 text-red-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
        </svg>
      )}
      <span className="font-medium">
        {status === 'success' ? contactText.successMessage : contactText.errorMessage}
      </span>
    </motion.div>
  );

  // Input with icon component
  const InputWithIcon = ({ icon, type, placeholder, name, variants }) => (
    <motion.div 
      className="relative" 
      variants={variants}
    >
      <div className={`absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none ${
        theme === "light" ? "text-slate-500" : "text-slate-400"
      }`}>
        {icon}
      </div>
      <input
        type={type}
        name={name}
        placeholder={placeholder}
        className={`w-full p-4 pl-12 rounded-lg border transition-colors duration-200 ${
          theme === "light" 
            ? "bg-white border-slate-200 text-slate-800 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500" 
            : "bg-slate-800 border-slate-700 text-slate-200 focus:border-indigo-400 focus:ring-1 focus:ring-indigo-500"
        }`}
        required
      />
    </motion.div>
  );

  return (
    <motion.section
      id="contact"
      className="py-24 px-4 sm:px-6 lg:px-8 relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      {/* Background decorations */}
      <div className="absolute top-0 left-0 -ml-40 -mt-40 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-br from-indigo-600 to-violet-600" />
      <div className="absolute bottom-0 right-0 -mr-40 -mb-40 w-80 h-80 rounded-full blur-3xl opacity-10 pointer-events-none
        bg-gradient-to-tr from-violet-600 to-indigo-600" />

      {/* Notification for form submission */}
      <AnimatePresence>
        {formStatus && <Notification status={formStatus} />}
      </AnimatePresence>

      <div className="container mx-auto max-w-5xl relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Left column - Contact info */}
          <motion.div variants={containerVariants}>
            <motion.div className="mb-12" variants={itemVariants}>
              <span className={`inline-block px-3 py-1 text-sm font-medium rounded-full mb-4 
                ${theme === "light" 
                  ? "bg-indigo-100 text-indigo-800" 
                  : "bg-indigo-900/50 text-indigo-300 border border-indigo-700/50"
                }`}>
                {contactText.title}
              </span>
              <h2 className={`text-3xl sm:text-4xl font-bold mb-4 ${
                theme === "light" ? "text-slate-900" : "text-white"
              }`}>
                Let's <span className={theme === "light" ? "text-indigo-600" : "text-indigo-400"}>
                  Connect
                </span>
              </h2>
              <p className={`text-lg max-w-md ${
                theme === "light" ? "text-slate-600" : "text-slate-300"
              }`}>
                {contactText.description}
              </p>
            </motion.div>

            {/* Contact details */}
            <motion.div className="space-y-6" variants={containerVariants}>
              <motion.div 
                className={`flex items-start space-x-4 p-4 rounded-lg ${
                  theme === "light" 
                    ? "bg-white shadow-sm border border-slate-100" 
                    : "bg-slate-800/50 shadow-sm border border-slate-700/50"
                }`}
                variants={itemVariants}
              >
                <div className={`rounded-full p-3 ${
                  theme === "light" 
                    ? "bg-indigo-100 text-indigo-600" 
                    : "bg-indigo-900/50 text-indigo-400 border border-indigo-700/50"
                }`}>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                      d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                  </svg>
                </div>
                <div>
                  <h3 className={`text-sm font-semibold ${theme === "light" ? "text-slate-500" : "text-slate-400"}`}>
                    Email
                  </h3>
                  <p className={`mt-1 text-base font-medium ${theme === "light" ? "text-slate-800" : "text-white"}`}>
                    contact@yutaka-yamaguchi.com
                  </p>
                </div>
              </motion.div>

              <motion.div 
                className={`flex items-start space-x-4 p-4 rounded-lg ${
                  theme === "light" 
                    ? "bg-white shadow-sm border border-slate-100" 
                    : "bg-slate-800/50 shadow-sm border border-slate-700/50"
                }`}
                variants={itemVariants}
              >
                <div className={`rounded-full p-3 ${
                  theme === "light" 
                    ? "bg-indigo-100 text-indigo-600" 
                    : "bg-indigo-900/50 text-indigo-400 border border-indigo-700/50"
                }`}>
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                      d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                  </svg>
                </div>
                <div>
                  <h3 className={`text-sm font-semibold ${theme === "light" ? "text-slate-500" : "text-slate-400"}`}>
                    Location
                  </h3>
                  <p className={`mt-1 text-base font-medium ${theme === "light" ? "text-slate-800" : "text-white"}`}>
                    Tokyo, Japan
                  </p>
                </div>
              </motion.div>

              {/* Social links */}
              <motion.div 
                className="mt-8"
                variants={itemVariants}
              >
                <h3 className={`text-sm font-semibold mb-4 ${theme === "light" ? "text-slate-700" : "text-slate-300"}`}>
                  Connect with me
                </h3>
                <div className="flex space-x-3">
                  <motion.a
                    href="https://www.linkedin.com/in/yutaka-yamaguchi"
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`p-3 rounded-full transition-all duration-300 ${
                      theme === "light"
                        ? "bg-slate-100 text-slate-700 hover:bg-slate-200 hover:text-indigo-600"
                        : "bg-slate-800 text-slate-300 hover:bg-slate-700 hover:text-indigo-400"
                    }`}
                    whileHover={{ y: -3 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M19 0h-14c-2.761 0-5 2.239-5 5v14c0 2.761 2.239 5 5 5h14c2.762 0 5-2.239 5-5v-14c0-2.761-2.238-5-5-5zm-11 19h-3v-11h3v11zm-1.5-12.268c-.966 0-1.75-.79-1.75-1.764s.784-1.764 1.75-1.764 1.75.79 1.75 1.764-.783 1.764-1.75 1.764zm13.5 12.268h-3v-5.604c0-.88-.018-2.013-1.227-2.013-1.227 0-1.415.957-1.415 1.947v5.67h-3v-11h2.85v1.634h.042c.494-.936 1.699-1.924 3.5-1.924 3.742 0 4.4 2.464 4.4 5.667v6.623z"/>
                    </svg>
                  </motion.a>
                  <motion.a
                    href="https://github.com/yyamaguchi0102?tab=repositories"
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`p-3 rounded-full transition-all duration-300 ${
                      theme === "light"
                        ? "bg-slate-100 text-slate-700 hover:bg-slate-200 hover:text-indigo-600"
                        : "bg-slate-800 text-slate-300 hover:bg-slate-700 hover:text-indigo-400"
                    }`}
                    whileHover={{ y: -3 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.239 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                    </svg>
                  </motion.a>
                  <motion.a
                    href="https://twitter.com"
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`p-3 rounded-full transition-all duration-300 ${
                      theme === "light"
                        ? "bg-slate-100 text-slate-700 hover:bg-slate-200 hover:text-indigo-600"
                        : "bg-slate-800 text-slate-300 hover:bg-slate-700 hover:text-indigo-400"
                    }`}
                    whileHover={{ y: -3 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M23.953 4.57a10 10 0 01-2.825.775 4.958 4.958 0 002.163-2.723c-.951.555-2.005.959-3.127 1.184a4.92 4.92 0 00-8.384 4.482C7.69 8.095 4.067 6.13 1.64 3.162a4.822 4.822 0 00-.666 2.475c0 1.71.87 3.213 2.188 4.096a4.904 4.904 0 01-2.228-.616v.06a4.923 4.923 0 003.946 4.827 4.996 4.996 0 01-2.212.085 4.936 4.936 0 004.604 3.417 9.867 9.867 0 01-6.102 2.105c-.39 0-.779-.023-1.17-.067a13.995 13.995 0 007.557 2.209c9.053 0 13.998-7.496 13.998-13.985 0-.21 0-.42-.015-.63A9.935 9.935 0 0024 4.59z"/>
                    </svg>
                  </motion.a>
                </div>
              </motion.div>
            </motion.div>
          </motion.div>

          {/* Right column - Contact form */}
          <motion.div variants={containerVariants}>
            <motion.div 
              className={`p-8 rounded-2xl ${
                theme === "light" 
                  ? "bg-white shadow-lg border border-slate-100" 
                  : "bg-slate-800 shadow-lg border border-slate-700/50"
              }`}
              variants={itemVariants}
            >
              <h3 className={`text-xl font-bold mb-6 ${
                theme === "light" ? "text-slate-900" : "text-white"
              }`}>
                Send me a message
              </h3>

              <motion.form 
                className="space-y-6" 
                variants={containerVariants}
                onSubmit={handleSubmit}
              >
                <InputWithIcon 
                  icon={
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                    </svg>
                  }
                  type="text"
                  name="name"
                  placeholder={contactText.namePlaceholder}
                  variants={itemVariants}
                />

                <InputWithIcon 
                  icon={
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                    </svg>
                  }
                  type="email"
                  name="email"
                  placeholder={contactText.emailPlaceholder}
                  variants={itemVariants}
                />

                <motion.div className="relative" variants={itemVariants}>
                  <div className={`absolute top-4 left-0 pl-4 flex items-start pointer-events-none ${
                    theme === "light" ? "text-slate-500" : "text-slate-400"
                  }`}>
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 10h.01M12 10h.01M16 10h.01M9 16H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-5l-5 5v-5z" />
                    </svg>
                  </div>
                  <textarea
                    name="message"
                    placeholder={contactText.messagePlaceholder}
                    rows="5"
                    className={`w-full p-4 pl-12 rounded-lg border transition-colors duration-200 ${
                      theme === "light" 
                        ? "bg-white border-slate-200 text-slate-800 focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500" 
                        : "bg-slate-800 border-slate-700 text-slate-200 focus:border-indigo-400 focus:ring-1 focus:ring-indigo-500"
                    }`}
                    required
                  ></textarea>
                </motion.div>

                <motion.button
                  type="submit"
                  disabled={isSubmitting}
                  className={`w-full p-4 rounded-lg font-medium flex items-center justify-center transition-all duration-300 ${
                    theme === "light"
                      ? "bg-indigo-600 text-white hover:bg-indigo-700 shadow-md hover:shadow-lg"
                      : "bg-indigo-500 text-white hover:bg-indigo-600 shadow-md hover:shadow-lg"
                  } ${isSubmitting ? 'opacity-70 cursor-not-allowed' : ''}`}
                  variants={itemVariants}
                  whileHover={!isSubmitting ? { scale: 1.02, y: -2 } : {}}
                  whileTap={!isSubmitting ? { scale: 0.98 } : {}}
                >
                  {isSubmitting ? (
                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                  ) : null}
                  {isSubmitting ? 'Sending...' : contactText.sendButton}
                </motion.button>
              </motion.form>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </motion.section>
  );
};

export default Contact;
