import React from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";

const Projects = () => {
  const { theme } = useTheme();
  const { text } = useLanguage();

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        staggerChildren: 0.3,
        duration: 0.8,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20, scale: 0.95 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { duration: 1 },
    },
    hover: {
      scale: 1.02,
      y: -5,
      transition: { duration: 0.2 }
    },
    tap: {
      scale: 0.98,
      transition: { duration: 0.1 }
    }
  };

  return (
    <motion.section
      id="projects"
      className="min-h-screen py-16 px-8 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true }}
      variants={containerVariants}
    >
      <div className="max-w-7xl mx-auto">
        <motion.h2
          className="text-4xl font-bold mb-8 text-center"
          variants={itemVariants}
        >
          {text.projects.title}
        </motion.h2>
        <motion.p className="text-center mb-12" variants={itemVariants}>
          {text.projects.description}
        </motion.p>
        <motion.div
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8"
          variants={containerVariants}
        >
          {text.projects.items.map((project, index) => (
            <motion.div
              key={index}
              className={`relative p-6 rounded-2xl overflow-hidden
                ${theme === "light" 
                  ? "bg-white/80 shadow-[0_8px_32px_rgba(0,0,0,0.1)]" 
                  : "bg-dark-card/80 shadow-[0_8px_32px_rgba(0,0,0,0.3)]"
                } backdrop-blur-sm border border-opacity-20
                ${theme === "light" ? "border-gray-200" : "border-gray-700"}`}
              variants={itemVariants}
              whileHover="hover"
              whileTap="tap"
            >
              <h3 className={`text-2xl font-semibold mb-4 
                ${theme === "light" ? "text-gray-800" : "text-gray-100"}`}>
                {project.name}
              </h3>
              <p className={`mb-4 leading-relaxed
                ${theme === "light" ? "text-gray-600" : "text-gray-300"}`}>
                {project.description}
              </p>
              <div className="mt-6">
                <h4 className={`text-lg font-medium mb-3
                  ${theme === "light" ? "text-gray-700" : "text-gray-200"}`}>
                  {text.projects.technologiesUsed}
                </h4>
                <div className="flex flex-wrap gap-2">
                  {(project.tech || []).map((tech, idx) => (
                    <span
                      key={idx}
                      className={`px-3 py-1 text-sm rounded-full font-medium
                        ${theme === "light" 
                          ? "bg-gray-100 text-gray-700" 
                          : "bg-dark-border/50 text-gray-200"
                        }`}
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
              <div className="flex justify-between mt-8">
                <a
                  href={project.demoUrl || "#"}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-300
                    ${theme === "light"
                      ? "bg-gradient-to-r from-light-accent to-orange-500 text-white hover:shadow-lg hover:shadow-orange-500/20"
                      : "bg-gradient-to-r from-dark-accent to-blue-500 text-white hover:shadow-lg hover:shadow-blue-500/20"
                    }`}
                >
                  {project.demo}
                </a>
                <a
                  href={project.repoUrl || "#"}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`px-4 py-2 rounded-xl font-medium transition-all duration-300
                    ${theme === "light"
                      ? "bg-gradient-to-r from-gray-100 to-white text-gray-800 hover:shadow-lg hover:shadow-gray-200"
                      : "bg-gradient-to-r from-dark-border/50 to-dark-card text-gray-200 hover:shadow-lg hover:shadow-gray-800/20"
                    }`}
                >
                  {project.repo}
                </a>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
};

export default Projects;
