import React, { useState, useEffect } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';

const PageTransition = ({ children }) => {
  const [isLoading, setIsLoading] = useState(true);
  const { theme } = useTheme();

  useEffect(() => {
    // Even shorter loading time for better UX
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 400);

    return () => clearTimeout(timer);
  }, []);

  // Simplified reveal animation - fewer masks for better performance
  const masks = Array.from({ length: 3 }, (_, i) => i);

  return (
    <AnimatePresence mode="wait">
      {isLoading ? (
        <motion.div
          key="loader"
          className={`fixed inset-0 z-50 overflow-hidden
            ${theme === 'light' 
              ? 'bg-gradient-to-br from-rose-50 via-white to-amber-50' 
              : 'bg-gradient-to-br from-slate-900 via-gray-900 to-indigo-900'}`}
          exit={{ 
            opacity: 1,
            transition: { duration: 0.4 }
          }}
        >
          {/* Masked Transition */}
          <div className="absolute inset-0 flex items-center justify-center">
            {masks.map((_, i) => (
              <motion.div
                key={i}
                className={`absolute inset-0 ${theme === 'light' 
                  ? 'bg-rose-500' 
                  : 'bg-indigo-500'}`}
                initial={{ clipPath: 'inset(100% 0% 0% 0%)' }}
                exit={{
                  clipPath: 'inset(0% 0% 0% 0%)',
                  transition: {
                    duration: 0.4,
                    delay: i * 0.06, // Even faster delay
                    ease: [0.645, 0.045, 0.355, 1.000]
                  }
                }}
                style={{
                  clipPath: 'inset(100% 0% 0% 0%)',
                  opacity: 0.9 - (i * 0.2),
                  zIndex: 50 - i
                }}
              />
            ))}
          </div>

          {/* Simplified animated elements - fewer elements for better performance */}
          <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
            <motion.div 
              className={`text-6xl font-bold ${
                theme === 'light' 
                  ? 'text-rose-600' 
                  : 'text-white'
              } text-opacity-90`}
              initial={{ filter: "blur(4px)", opacity: 0, scale: 1.2 }}
              animate={{ 
                filter: "blur(0px)", 
                opacity: 1, 
                scale: 1,
                transition: { duration: 0.3, ease: "easeOut" } 
              }}
              exit={{ 
                filter: "blur(4px)", 
                opacity: 0, 
                scale: 0.8,
                transition: { duration: 0.2 } 
              }}
            >
              Welcome
            </motion.div>
          </div>
        </motion.div>
      ) : (
        <motion.div
          key="content"
          className="w-full"
          initial={{ opacity: 0 }}
          animate={{ 
            opacity: 1,
            transition: {
              duration: 0.3,
              ease: "easeOut"
            }
          }}
        >
          {children}
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default PageTransition;
