import React, { useState, useEffect } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';

const PageTransition = ({ children }) => {
  const [isLoading, setIsLoading] = useState(true);
  const { theme } = useTheme();

  useEffect(() => {
    // Use a shorter loading time for better UX
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 600);

    return () => clearTimeout(timer);
  }, []);

  return (
    <AnimatePresence mode="wait">
      {isLoading ? (
        <motion.div
          key="loader"
          className={`fixed inset-0 flex flex-col items-center justify-center z-50 
            ${theme === 'light' 
              ? 'bg-gradient-to-br from-rose-50 via-white to-amber-50' 
              : 'bg-gradient-to-br from-slate-900 via-gray-900 to-indigo-900'}`}
          initial={{ opacity: 1 }}
          exit={{ 
            opacity: 0,
            y: -20,
            transition: { duration: 0.4, ease: "easeInOut" }
          }}
        >
          <motion.div
            className={`w-16 h-16 rounded-full border-4 border-t-transparent
              ${theme === 'light' 
                ? 'border-rose-500' 
                : 'border-indigo-500'}`}
            animate={{ rotate: 360 }}
            transition={{ 
              duration: 1, 
              repeat: Infinity,
              ease: "linear" 
            }}
          />
        </motion.div>
      ) : (
        <motion.div
          key="content"
          initial={{ opacity: 0, y: 10 }}
          animate={{ 
            opacity: 1, 
            y: 0,
            transition: {
              duration: 0.4,
              ease: "easeOut"
            }
          }}
          className="w-full"
        >
          {children}
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default PageTransition;
