import React from "react";
import { motion } from "framer-motion";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { languages } from "../languages.js";

const ServiceCard = ({ service, index, theme }) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, margin: "-100px" }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
      whileHover={{ y: -8 }}
      className={`relative border-2 ${
        theme === "light" 
          ? "border-gray-800 bg-white" 
          : "border-gray-100 bg-gray-900"
      }`}
    >
      {/* Side accent bar */}
      <div 
        className={`absolute -left-3 top-6 bottom-6 w-3 ${
          theme === "light" ? "bg-rose-500" : "bg-indigo-500"
        }`} 
      />

      <div className="p-8">
        <div 
          className={`inline-flex items-center justify-center w-16 h-16 mb-6 ${
            theme === "light" 
              ? "bg-gray-100 text-gray-800" 
              : "bg-gray-800 text-gray-200"
          }`}
        >
          <span className="text-3xl">{service.icon}</span>
        </div>

        <h3 className={`text-2xl font-bold mb-4 ${
          theme === "light" ? "text-gray-900" : "text-white"
        }`}>
          {service.title}
        </h3>

        <p className={`mb-6 ${
          theme === "light" ? "text-gray-700" : "text-gray-300"
        }`}>
          {service.description}
        </p>

        <ul className="space-y-2">
          {service.features.map((feature, idx) => (
            <li key={idx} className="flex items-start">
              <span className={`mr-2 mt-1 text-lg ${
                theme === "light" ? "text-rose-500" : "text-indigo-400"
              }`}>
                &#x2013;
              </span>
              <span className={theme === "light" ? "text-gray-700" : "text-gray-300"}>
                {feature}
              </span>
            </li>
          ))}
        </ul>

        <motion.button
          whileHover={{ x: 5 }}
          transition={{ duration: 0.2 }}
          className={`mt-8 py-3 px-6 font-bold inline-flex items-center space-x-2 ${
            theme === "light"
              ? "bg-gray-900 text-white hover:bg-black"
              : "bg-gray-100 text-gray-900 hover:bg-white"
          } transition-colors`}
        >
          <span>{service.buttonText}</span>
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M5 12h14M12 5l7 7-7 7"/>
          </svg>
        </motion.button>
      </div>
    </motion.div>
  );
};

const Services = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  return (
    <section id="services" className="py-24 relative overflow-hidden">
      {/* Background Grid */}
      <div 
        className={`absolute inset-0 ${
          theme === "light" ? "opacity-[0.015]" : "opacity-[0.03]"
        }`}
        style={{
          backgroundImage: `
            linear-gradient(to right, ${theme === "light" ? "black" : "white"} 1px, transparent 1px),
            linear-gradient(to bottom, ${theme === "light" ? "black" : "white"} 1px, transparent 1px)
          `,
          backgroundSize: '5rem 5rem'
        }}
      />

      {/* Background Shapes */}
      {[1, 2, 3].map((i) => (
        <motion.div
          key={i}
          className={`absolute pointer-events-none ${
            theme === "light" 
              ? i % 2 === 0 ? "bg-rose-500/10" : "bg-gray-800/5" 
              : i % 2 === 0 ? "bg-indigo-500/10" : "bg-gray-200/5"
          } ${i === 2 ? 'rounded-none' : 'rounded-full'}`}
          style={{
            width: 200 + i * 100,
            height: 200 + i * 100,
            bottom: `${10 + i * 15}%`,
            right: i % 2 === 0 ? `-${i * 5}%` : `${80 + i * 5}%`,
            zIndex: 0
          }}
          animate={{
            x: [0, i * 20, -i * 30, i * 10, 0],
            y: [0, -i * 20, i * 30, -i * 10, 0],
            rotate: [0, i * 10, i * 20, i * 10, 0]
          }}
          transition={{
            duration: 20 + i * 5,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}

      <div className="container mx-auto px-4 relative z-10">
        {/* Section Header */}
        <div className="mb-16 relative">
          <motion.div
            initial={{ x: -30, opacity: 0 }}
            whileInView={{ x: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
            className={`absolute -left-3 top-0 h-16 w-3 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
          />
          <motion.h2 
            className={`text-5xl md:text-6xl font-bold mb-6 ${
              theme === "light" ? "text-gray-900" : "text-white"
            }`}
            initial={{ y: 30, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7 }}
          >
            {currentLanguage.services.title}
          </motion.h2>
          <motion.p
            className={`text-xl max-w-3xl ${
              theme === "light" ? "text-gray-700" : "text-gray-300"
            }`}
            initial={{ y: 20, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            {currentLanguage.services.description}
          </motion.p>
        </div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-8 gap-y-12">
          {currentLanguage.services.items.map((service, index) => (
            <ServiceCard 
              key={index} 
              service={service} 
              index={index}
              theme={theme}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
