import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '../contexts/ThemeContext';

const CustomCursor = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const [isPointing, setIsPointing] = useState(false);
  const [isClicking, setIsClicking] = useState(false);
  const { theme } = useTheme();

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    const handleMouseDown = () => {
      setIsClicking(true);
    };

    const handleMouseUp = () => {
      setIsClicking(false);
    };

    const handleMouseOver = (e) => {
      if (e.target.tagName === 'A' || 
          e.target.tagName === 'BUTTON' || 
          e.target.classList.contains('cursor-pointer') ||
          e.target.parentElement.classList.contains('cursor-pointer')) {
        setIsHovering(true);

        // Check if it's a link or button that changes cursor to pointer
        const computedStyle = window.getComputedStyle(e.target);
        if (computedStyle.cursor === 'pointer') {
          setIsPointing(true);
        }
      }
    };

    const handleMouseOut = () => {
      setIsHovering(false);
      setIsPointing(false);
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    document.addEventListener('mouseover', handleMouseOver);
    document.addEventListener('mouseout', handleMouseOut);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('mouseover', handleMouseOver);
      document.removeEventListener('mouseout', handleMouseOut);
    };
  }, []);

  // Hide on mobile devices
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  if (isMobile) return null;

  return (
    <>
      {/* Main large cursor */}
      <motion.div
        className={`fixed top-0 left-0 w-10 h-10 rounded-full pointer-events-none z-[999] mix-blend-difference`}
        animate={{
          x: mousePosition.x - 20,
          y: mousePosition.y - 20,
          scale: isClicking ? 0.8 : isHovering ? 1.5 : 1,
        }}
        transition={{
          type: "spring",
          damping: 20,
          stiffness: 400,
          mass: 0.2
        }}
        style={{
          backgroundColor: 'white',
        }}
      />

      {/* Small dot cursor */}
      <motion.div
        className={`fixed top-0 left-0 w-2 h-2 rounded-full pointer-events-none z-[999] bg-white mix-blend-difference`}
        animate={{
          x: mousePosition.x - 4,
          y: mousePosition.y - 4,
          opacity: isPointing ? 0 : 1,
          scale: isClicking ? 0.5 : 1,
        }}
        transition={{
          type: "spring",
          damping: 30,
          stiffness: 800,
          mass: 0.1
        }}
      />

      {/* Trail effect */}
      {Array.from({ length: 5 }).map((_, index) => (
        <motion.div
          key={index}
          className={`fixed top-0 left-0 rounded-full pointer-events-none z-[998] mix-blend-difference`}
          style={{
            width: `${8 - index}px`,
            height: `${8 - index}px`,
            backgroundColor: 'white',
          }}
          animate={{
            x: mousePosition.x - (8 - index) / 2,
            y: mousePosition.y - (8 - index) / 2,
          }}
          transition={{
            type: "tween",
            ease: "linear",
            duration: 0.1,
            delay: index * 0.03,
          }}
        />
      ))}
    </>
  );
};

export default CustomCursor;
