import React, { useState, useEffect } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

const Contact = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const currentLanguage = languages[language];

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [loadingProgress, setLoadingProgress] = useState(0);
  const [focusedField, setFocusedField] = useState(null);

  // Simulate loading with progress
  useEffect(() => {
    if (isLoading) {
      const totalDuration = 1000; // Total loading time in ms
      const interval = 40; // Update interval in ms
      const steps = totalDuration / interval;
      let currentStep = 0;

      const timer = setInterval(() => {
        currentStep += 1;
        const progress = Math.min(currentStep / steps, 1);
        setLoadingProgress(progress);

        if (progress >= 1) {
          clearInterval(timer);
          setTimeout(() => setIsLoading(false), 200);
        }
      }, interval);

      return () => clearInterval(timer);
    }
  }, [isLoading]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (name && email && message) {
      // Simulate form submission
      setSubmitted(true);

      // Reset form after 3 seconds
      setTimeout(() => {
        setName("");
        setEmail("");
        setMessage("");
        setSubmitted(false);
      }, 3000);
    }
  };

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.1,
        when: "beforeChildren",
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { 
        type: "spring",
        stiffness: 100,
        damping: 15,
        duration: 0.5,
      },
    },
  };

  // Form field animation variants
  const fieldVariants = {
    hidden: { opacity: 0, y: 10, scale: 0.98 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      scale: 1,
      transition: { 
        type: "spring",
        stiffness: 80,
        damping: 15,
        delay: i * 0.1 + 0.2
      },
    }),
    focus: {
      scale: 1.02,
      transition: { duration: 0.3 }
    }
  };

  // Loading progress line animation
  const progressVariants = {
    initial: { width: 0 },
    animate: { width: `${loadingProgress * 100}%` }
  };

  // Background animation for the section
  const backgroundVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { duration: 1.2 }
    }
  };

  // Loading Shimmer Effect Component
  const ShimmerEffect = () => (
    <motion.div 
      className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent"
      style={{ backgroundSize: "200% 100%" }}
      initial={{ x: '-100%' }}
      animate={{ x: '100%' }}
      transition={{ 
        repeat: Infinity, 
        repeatType: 'loop', 
        duration: 1.5,
        ease: 'linear',
        repeatDelay: 0.5
      }}
    />
  );

  return (
    <motion.section
      className="min-h-screen py-20 px-8 relative"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.1 }}
      variants={containerVariants}
    >
      {/* Loading progress indicator */}
      {isLoading && (
        <div className="fixed top-0 left-0 right-0 z-50">
          <motion.div 
            className={`h-1 ${theme === "light" ? "bg-rose-500" : "bg-indigo-500"}`}
            variants={progressVariants}
            initial="initial"
            animate="animate"
            transition={{ duration: 0.4 }}
          />
        </div>
      )}

      {/* Background element */}
      <motion.div 
        className="absolute inset-0 pointer-events-none overflow-hidden"
        variants={backgroundVariants}
      >
        <motion.div 
          className={`absolute top-1/3 right-1/3 w-72 h-72 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-rose-400" : "bg-indigo-500"}`}
          animate={{ 
            scale: [1, 1.2, 1],
            x: [0, 40, 0],
            y: [0, -30, 0],
          }}
          transition={{ 
            duration: 18, 
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <motion.div 
          className={`absolute bottom-1/3 left-1/3 w-72 h-72 rounded-full opacity-10 blur-3xl
            ${theme === "light" ? "bg-amber-400" : "bg-purple-500"}`}
          animate={{ 
            scale: [1, 1.1, 1],
            x: [0, -30, 0],
            y: [0, 40, 0],
          }}
          transition={{ 
            duration: 20, 
            repeat: Infinity,
            ease: "easeInOut",
            delay: 1
          }}
        />
      </motion.div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section Header */}
        <motion.div className="text-center mb-16" variants={itemVariants}>
          <motion.h2 
            className={`text-4xl font-bold mb-4 inline-block
              ${theme === "light" ? "text-rose-600" : "text-indigo-400"}`}
            variants={itemVariants}
          >
            {currentLanguage?.contact?.title || "Contact Me"}
            <motion.div 
              className={`h-1 mt-1 ${theme === "light" ? "bg-rose-500" : "bg-indigo-500"}`}
              initial={{ width: 0 }}
              whileInView={{ width: "100%" }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.5 }}
            />
          </motion.h2>

          <motion.p 
            className={`text-lg max-w-2xl mx-auto
              ${theme === "light" ? "text-gray-700" : "text-gray-300"}`}
            variants={itemVariants}
          >
            {currentLanguage?.contact?.description || "Get in touch with me. I'd love to hear from you."}
          </motion.p>
        </motion.div>

        {/* Contact Form */}
        <div className="max-w-3xl mx-auto">
          <motion.div 
            className={`p-8 rounded-2xl ${
              theme === "light" 
                ? "bg-white/80 shadow-lg border border-gray-200/50" 
                : "bg-gray-800/60 shadow-lg border border-gray-700/50"
            } backdrop-blur-sm relative overflow-hidden`}
            variants={itemVariants}
          >
            {isLoading ? (
              // Skeleton form when loading
              <div className="space-y-6">
                {/* Skeleton header */}
                <div className={`h-7 w-1/3 mb-6 rounded-md ${
                  theme === "light" ? "bg-gray-200" : "bg-gray-700"
                }`} />

                {/* Skeleton name field */}
                <div className="space-y-1">
                  <div className={`h-5 w-20 rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                  <div className={`h-10 w-full rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                </div>

                {/* Skeleton email field */}
                <div className="space-y-1">
                  <div className={`h-5 w-24 rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                  <div className={`h-10 w-full rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                </div>

                {/* Skeleton message field */}
                <div className="space-y-1">
                  <div className={`h-5 w-28 rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                  <div className={`h-32 w-full rounded-md ${
                    theme === "light" ? "bg-gray-200" : "bg-gray-700"
                  }`} />
                </div>

                {/* Skeleton button */}
                <div className={`h-12 w-32 rounded-md ${
                  theme === "light" ? "bg-gray-200" : "bg-gray-700"
                }`} />

                {/* Loading shimmer effect */}
                <ShimmerEffect />
              </div>
            ) : (
              // Actual form when loaded
              <form onSubmit={handleSubmit}>
                <motion.h3 
                  className={`text-xl font-bold mb-6 ${
                    theme === "light" ? "text-gray-800" : "text-white"
                  }`}
                  variants={itemVariants}
                >
                  {currentLanguage?.contact?.formTitle || "Send a Message"}
                </motion.h3>

                <div className="space-y-6">
                  {/* Name field */}
                  <motion.div 
                    variants={fieldVariants}
                    custom={0}
                    animate={focusedField === 'name' ? 'focus' : 'visible'}
                  >
                    <label 
                      htmlFor="name" 
                      className={`block text-sm font-medium mb-1 ${
                        theme === "light" ? "text-gray-700" : "text-gray-300"
                      }`}
                    >
                      {currentLanguage?.contact?.nameLabel || "Name"}
                    </label>
                    <input
                      type="text"
                      id="name"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      onFocus={() => setFocusedField('name')}
                      onBlur={() => setFocusedField(null)}
                      className={`w-full px-4 py-2 rounded-lg focus:outline-none ${
                        theme === "light" 
                          ? "bg-gray-50 border border-gray-200 focus:border-rose-400 text-gray-800" 
                          : "bg-gray-700 border border-gray-600 focus:border-indigo-400 text-white"
                      }`}
                      placeholder={currentLanguage?.contact?.namePlaceholder || "Your name"}
                      required
                    />
                  </motion.div>

                  {/* Email field */}
                  <motion.div 
                    variants={fieldVariants}
                    custom={1}
                    animate={focusedField === 'email' ? 'focus' : 'visible'}
                  >
                    <label 
                      htmlFor="email" 
                      className={`block text-sm font-medium mb-1 ${
                        theme === "light" ? "text-gray-700" : "text-gray-300"
                      }`}
                    >
                      {currentLanguage?.contact?.emailLabel || "Email"}
                    </label>
                    <input
                      type="email"
                      id="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      onFocus={() => setFocusedField('email')}
                      onBlur={() => setFocusedField(null)}
                      className={`w-full px-4 py-2 rounded-lg focus:outline-none ${
                        theme === "light" 
                          ? "bg-gray-50 border border-gray-200 focus:border-rose-400 text-gray-800" 
                          : "bg-gray-700 border border-gray-600 focus:border-indigo-400 text-white"
                      }`}
                      placeholder={currentLanguage?.contact?.emailPlaceholder || "Your email address"}
                      required
                    />
                  </motion.div>

                  {/* Message field */}
                  <motion.div 
                    variants={fieldVariants}
                    custom={2}
                    animate={focusedField === 'message' ? 'focus' : 'visible'}
                  >
                    <label 
                      htmlFor="message" 
                      className={`block text-sm font-medium mb-1 ${
                        theme === "light" ? "text-gray-700" : "text-gray-300"
                      }`}
                    >
                      {currentLanguage?.contact?.messageLabel || "Message"}
                    </label>
                    <textarea
                      id="message"
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                      onFocus={() => setFocusedField('message')}
                      onBlur={() => setFocusedField(null)}
                      className={`w-full px-4 py-2 rounded-lg focus:outline-none min-h-[150px] ${
                        theme === "light" 
                          ? "bg-gray-50 border border-gray-200 focus:border-rose-400 text-gray-800" 
                          : "bg-gray-700 border border-gray-600 focus:border-indigo-400 text-white"
                      }`}
                      placeholder={currentLanguage?.contact?.messagePlaceholder || "Your message"}
                      required
                    />
                  </motion.div>

                  {/* Submit button */}
                  <motion.div
                    variants={fieldVariants}
                    custom={3}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <button
                      type="submit"
                      className={`px-6 py-3 rounded-lg font-medium transition-colors ${
                        theme === "light" 
                          ? "bg-rose-500 text-white hover:bg-rose-600" 
                          : "bg-indigo-500 text-white hover:bg-indigo-600"
                      }`}
                    >
                      {currentLanguage?.contact?.submitButton || "Send Message"}
                    </button>
                  </motion.div>
                </div>
              </form>
            )}
          </motion.div>

          {/* Success message */}
          <AnimatePresence>
            {submitted && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className={`mt-6 p-4 rounded-lg ${
                  theme === "light" 
                    ? "bg-green-100 border border-green-200 text-green-800" 
                    : "bg-green-900/30 border border-green-800/30 text-green-300"
                }`}
              >
                <div className="flex items-center">
                  <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                  </svg>
                  <p>{currentLanguage?.contact?.successMessage || "Message sent successfully! I'll get back to you soon."}</p>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </motion.section>
  );
};

export default Contact;
