import React, { useState, useEffect } from "react";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import ResumeButton from "./ResumeButton";
import { motion, AnimatePresence } from "framer-motion";
import logo from "../assets/logo.svg";

// Define the navigation items with subtle hover animation
const NavItem = ({ name, href, onClick, isActive, isMobileMenuOpen }) => {
  const { theme } = useTheme();

  return (
    <motion.li
      className="relative"
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.97 }}
      transition={{ duration: 0.2 }}
    >
      <a 
        href={href} 
        onClick={onClick}
        className={`relative block py-2 px-3 text-base font-medium transition-colors duration-200
          ${isActive 
            ? theme === "light" 
              ? "text-rose-600" 
              : "text-indigo-400" 
            : theme === "light" 
              ? "text-gray-800 hover:text-rose-600" 
              : "text-gray-200 hover:text-indigo-400"
          }
          ${isMobileMenuOpen ? "text-lg" : ""}
        `}
      >
        {name}
        {isActive && (
          <motion.span
            className={`absolute inset-x-0 bottom-0 h-0.5 ${
              theme === "light" ? "bg-rose-500" : "bg-indigo-500"
            }`}
            layoutId="navbar-indicator-desktop"
            transition={{ type: "spring", duration: 0.4, bounce: 0.2 }}
          />
        )}
      </a>
    </motion.li>
  );
};

const Header = () => {
  const { theme, setTheme } = useTheme();
  const { language, text, toggleLanguage } = useLanguage();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  const navItems = [
    { id: 'home', name: text.header.home, href: '#home' },
    { id: 'skills', name: text.header.skills, href: '#skills' },
    { id: 'services', name: text.header.services, href: '#services' },
    { id: 'projects', name: text.header.projects, href: '#projects' },
    { id: 'contact', name: text.header.contact, href: '#contact' },
  ];

  // Add scroll event listener
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleNavClick = (e, section) => {
    e.preventDefault();
    setActiveSection(section);

    // Close mobile menu if open
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }

    // Scroll to the section
    const element = document.getElementById(section);
    if (element) {
      // Use smooth scrolling with a slight offset for the header
      const headerOffset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: "smooth"
      });
    }
  };

  return (
    <header 
      className={`sticky top-0 w-full z-30 transition-all duration-300 
        ${isMobileMenuOpen ? "bg-gray-900/90 backdrop-blur-md border-b border-gray-800" : ""}
      `}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          {/* Logo */}
          <a 
            href="#home" 
            className="flex items-center space-x-2"
            onClick={(e) => handleNavClick(e, 'home')}
          >
            <motion.div 
              whileHover={{ rotate: 5, scale: 1.05 }}
              whileTap={{ rotate: -5, scale: 0.95 }}
              transition={{ duration: 0.2 }}
            >
              <img 
                src={logo} 
                alt="Your Name" 
                className="w-8 h-8 rounded-lg"
              />
            </motion.div>
            <motion.span 
              className={`font-bold text-xl ${
                theme === "light" ? "text-gray-900" : "text-white"
              }`}
              whileHover={{ y: -1 }}
              transition={{ duration: 0.2 }}
            >
              {text.header.name}
            </motion.span>
          </a>

          {/* Desktop Navigation */}
          <nav className="hidden md:block">
            <ul className="flex space-x-1">
              {navItems.map((item) => (
                <NavItem
                  key={item.name}
                  name={item.name}
                  href={item.href}
                  onClick={(e) => handleNavClick(e, item.id)}
                  isActive={activeSection === item.id}
                  isMobileMenuOpen={false}
                />
              ))}
            </ul>
          </nav>

          {/* Actions */}
          <div className="flex items-center space-x-3">
            {/* Theme Toggle */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.2 }}
              onClick={toggleTheme}
              className={`p-2 rounded-full ${
                theme === "light" 
                  ? "bg-gray-100 hover:bg-gray-200" 
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              aria-label="Toggle Theme"
            >
              {theme === "light" ? (
                <svg className="w-5 h-5 text-gray-700" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              ) : (
                <svg className="w-5 h-5 text-gray-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              )}
            </motion.button>

            {/* Language Toggle */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.2 }}
              onClick={toggleLanguage}
              className={`p-2 rounded-full ${
                theme === "light" 
                  ? "bg-gray-100 hover:bg-gray-200" 
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              aria-label="Toggle Language"
            >
              <span className="font-medium text-sm">
                {language === "en" ? "JP" : "EN"}
              </span>
            </motion.button>

            {/* Mobile Menu Toggle */}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.2 }}
              onClick={toggleMobileMenu}
              className={`md:hidden p-2 rounded-full ${
                theme === "light" 
                  ? "bg-gray-100 hover:bg-gray-200" 
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              aria-label="Toggle Mobile Menu"
            >
              {isMobileMenuOpen ? (
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              ) : (
                <svg className="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                </svg>
              )}
            </motion.button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.nav
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: "auto", opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="md:hidden overflow-hidden"
            >
              <motion.ul 
                className="py-3 space-y-1"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.1 }}
              >
                {navItems.map((item) => (
                  <NavItem
                    key={item.name}
                    name={item.name}
                    href={item.href}
                    onClick={(e) => {
                      handleNavClick(e, item.id);
                      setIsMobileMenuOpen(false);
                    }}
                    isActive={activeSection === item.id}
                    isMobileMenuOpen={true}
                  />
                ))}
              </motion.ul>
            </motion.nav>
          )}
        </AnimatePresence>
      </div>
    </header>
  );
};

export default Header;
