import React, { createContext, useState, useContext, useEffect } from "react";

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState("light");

  // Apply theme-specific CSS variables
  useEffect(() => {
    const root = document.documentElement;

    if (theme === "light") {
      // Softer light theme
      root.style.setProperty('--bg-primary', '#f8f9fa');
      root.style.setProperty('--bg-secondary', '#f1f3f5');
      root.style.setProperty('--text-primary', '#343a40');
      root.style.setProperty('--text-secondary', '#495057');
      root.style.setProperty('--accent-color', '#e64980');
      root.style.setProperty('--card-bg', '#ffffff');
      root.style.setProperty('--border-color', 'rgba(0,0,0,0.07)');
      document.body.classList.remove('dark-theme');
      document.body.classList.add('light-theme');
    } else {
      // Dark theme (unchanged)
      root.style.setProperty('--bg-primary', '#121212');
      root.style.setProperty('--bg-secondary', '#1e1e1e');
      root.style.setProperty('--text-primary', '#e9ecef');
      root.style.setProperty('--text-secondary', '#adb5bd');
      root.style.setProperty('--accent-color', '#6366f1');
      root.style.setProperty('--card-bg', '#242424');
      root.style.setProperty('--border-color', 'rgba(255,255,255,0.07)');
      document.body.classList.remove('light-theme');
      document.body.classList.add('dark-theme');
    }
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
