import React, { useState } from "react";
import { Typewriter } from "react-simple-typewriter";
import { useTheme } from "../contexts/ThemeContext";
import { useLanguage } from "../contexts/LanguageContext";
import { motion } from "framer-motion";
import { languages } from "../languages";

const LoadingScreen = ({ onComplete }) => {
  const { setTheme } = useTheme();
  const { setLanguage } = useLanguage();
  const [selectedLanguage, setSelectedLanguage] = useState("en");
  const [showLanguagePrompt, setShowLanguagePrompt] = useState(true);
  const [showMoodPrompt, setShowMoodPrompt] = useState(false);

  // Get text for the current selected language
  const currentLanguageText = languages[selectedLanguage];

  const languagePrompts = [
    "Select A Language",
    "言語を選択してください",
    "언어를 선택하세요",
  ];

  const handleLanguageSelection = (lang) => {
    setSelectedLanguage(lang);
    setLanguage(lang);
    setShowLanguagePrompt(false);
    setShowMoodPrompt(true);
  };

  const handleMoodSelection = (mood) => {
    setTheme(mood);
    setTimeout(onComplete, 1000);
  };

  return (
    <div className="w-full h-screen flex flex-col justify-center items-center bg-gradient-to-br from-slate-900 to-gray-900 text-white relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/10 to-violet-500/10 animate-pulse"></div>
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-indigo-500/5 via-transparent to-transparent"></div>
      </div>

      {showLanguagePrompt ? (
        <motion.div 
          className="text-center z-10"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* Language Selection */}
          <div className="text-4xl font-bold mb-12 h-16 flex items-center justify-center">
            <Typewriter
              words={languagePrompts}
              loop={true}
              cursor
              cursorStyle="_"
              typeSpeed={70}
              deleteSpeed={50}
              delaySpeed={1500}
            />
          </div>
          <div className="flex space-x-6 mt-8">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleLanguageSelection("en")}
              className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/0 to-violet-500/0 group-hover:from-indigo-500/10 group-hover:to-violet-500/10 rounded-2xl transition-all duration-300"></div>
              <div className="relative flex items-center space-x-3">
                <img
                  src="https://flagsapi.com/US/flat/32.png"
                  alt="US Flag"
                  className="w-8 h-8"
                />
                <span className="text-lg font-medium">English</span>
              </div>
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleLanguageSelection("jp")}
              className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/0 to-violet-500/0 group-hover:from-indigo-500/10 group-hover:to-violet-500/10 rounded-2xl transition-all duration-300"></div>
              <div className="relative flex items-center space-x-3">
                <img
                  src="https://flagsapi.com/JP/flat/32.png"
                  alt="Japanese Flag"
                  className="w-8 h-8"
                />
                <span className="text-lg font-medium">日本語</span>
              </div>
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleLanguageSelection("ko")}
              className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/0 to-violet-500/0 group-hover:from-indigo-500/10 group-hover:to-violet-500/10 rounded-2xl transition-all duration-300"></div>
              <div className="relative flex items-center space-x-3">
                <img
                  src="https://flagsapi.com/KR/flat/32.png"
                  alt="Korean Flag"
                  className="w-8 h-8"
                />
                <span className="text-lg font-medium">한국어</span>
              </div>
            </motion.button>
          </div>
        </motion.div>
      ) : (
        <motion.div 
          className="text-center z-10"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* Theme Selection */}
          <h1 className="text-4xl font-bold mb-12">{currentLanguageText.loadingScreen.moodPrompt}</h1>
          <div className="flex space-x-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleMoodSelection("light")}
              className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-rose-500/0 to-pink-500/0 group-hover:from-rose-500/10 group-hover:to-pink-500/10 rounded-2xl transition-all duration-300"></div>
              <div className="relative flex items-center space-x-3">
                <span className="text-2xl">☀️</span>
                <span className="text-lg font-medium">{currentLanguageText.loadingScreen.moodBright}</span>
              </div>
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleMoodSelection("dark")}
              className="group relative px-8 py-4 bg-white/5 backdrop-blur-sm rounded-2xl border border-white/10 hover:border-white/20 transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-indigo-500/0 to-violet-500/0 group-hover:from-indigo-500/10 group-hover:to-violet-500/10 rounded-2xl transition-all duration-300"></div>
              <div className="relative flex items-center space-x-3">
                <span className="text-2xl">🌙</span>
                <span className="text-lg font-medium">{currentLanguageText.loadingScreen.moodDark}</span>
              </div>
            </motion.button>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default LoadingScreen;
