import React, { useState, useEffect, useRef } from "react";
import { useLanguage } from "../contexts/LanguageContext";
import { useTheme } from "../contexts/ThemeContext";
import { Typewriter } from "react-simple-typewriter";
import { motion, useAnimation, AnimatePresence } from "framer-motion";

const FloatingElement = ({ children, delay = 0, duration = 4, y = 15 }) => {
  return (
    <motion.div
      animate={{
        y: [0, -y, 0],
      }}
      transition={{
        duration: duration,
        repeat: Infinity,
        repeatType: "reverse",
        ease: "easeInOut",
        delay: delay,
      }}
    >
      {children}
    </motion.div>
  );
};

// 3D Text perspective effect
const Perspective3DText = ({ children, intensity = 30 }) => {
  const textRef = useRef(null);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (!textRef.current) return;

      const container = textRef.current.getBoundingClientRect();
      const containerCenterX = container.left + container.width / 2;
      const containerCenterY = container.top + container.height / 2;

      // Calculate mouse position relative to the center of container
      const mouseX = e.clientX - containerCenterX;
      const mouseY = e.clientY - containerCenterY;

      // Calculate rotation based on mouse position
      // Divide by larger values to reduce the intensity of the effect
      const rotateY = mouseX / (container.width / 2) * (intensity / 3);
      const rotateX = -mouseY / (container.height / 2) * (intensity / 6);

      setRotation({ x: rotateX, y: rotateY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [intensity]);

  return (
    <div 
      ref={textRef}
      className="perspective-text py-2 inline-block"
      style={{ perspective: '1000px', transformStyle: 'preserve-3d' }}
    >
      <div 
        style={{ 
          transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg)`,
          transition: 'transform 0.1s ease'
        }}
      >
        {children}
      </div>
    </div>
  );
};

const Home = () => {
