import React, { useState, useEffect } from "react";
import { useLanguage } from "../contexts/LanguageContext";
import { useTheme } from "../contexts/ThemeContext";
import { Typewriter } from "react-simple-typewriter";
import { motion, AnimatePresence } from "framer-motion";
import { languages } from "../languages.js";

const Home = () => {
  const { language } = useLanguage();
  const { theme } = useTheme();
  const currentLanguage = languages[language];
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Random shape generator for background decorations
  const randomShapes = Array(5).fill().map((_, i) => ({
    id: i,
    shape: Math.random() > 0.5 ? 'circle' : 'square',
    size: 40 + Math.random() * 120,
    x: Math.random() * 100,
    y: Math.random() * 100,
    rotation: Math.random() * 360,
    delay: i * 0.2,
    duration: 20 + Math.random() * 30
  }));

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <section
      id="home"
      className="relative min-h-screen flex items-center justify-center overflow-hidden py-16 px-4"
    >
      {/* Background Shapes - Brutalist decorative elements */}
      {randomShapes.map((shape) => (
        <motion.div
          key={shape.id}
          className={`absolute pointer-events-none opacity-20 ${
            theme === "light" 
              ? shape.id % 2 === 0 ? "bg-rose-500" : "bg-gray-800" 
              : shape.id % 2 === 0 ? "bg-indigo-500" : "bg-gray-200"
          } ${shape.shape === 'circle' ? 'rounded-full' : 'rounded-none'}`}
          style={{
            width: shape.size,
            height: shape.size,
            left: `${shape.x}%`,
            top: `${shape.y}%`,
          }}
          animate={{
            x: [0, 20, -30, 10, 0],
            y: [0, -20, 30, -10, 0],
            rotate: [0, shape.rotation / 2, shape.rotation, shape.rotation / 2, 0]
          }}
          transition={{
            duration: shape.duration,
            delay: shape.delay,
            repeat: Infinity,
            repeatType: "mirror"
          }}
        />
      ))}

      {/* Grid Overlay - Brutalist grid structure */}
      <div 
        className={`absolute inset-0 ${
          theme === "light" ? "opacity-[0.015]" : "opacity-[0.03]"
        }`}
        style={{
          backgroundImage: `
            linear-gradient(to right, ${theme === "light" ? "black" : "white"} 1px, transparent 1px),
            linear-gradient(to bottom, ${theme === "light" ? "black" : "white"} 1px, transparent 1px)
          `,
          backgroundSize: '5rem 5rem'
        }}
      />

      <div className="container mx-auto relative z-10">
        <div className="flex flex-col items-center text-center">

          {/* Primary Heading - Large, Brutalist Style */}
          <div className="mb-8 relative">
            <motion.div
              initial={{ x: -30, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.7 }}
              className={`absolute -left-3 -top-3 h-20 w-3 ${
                theme === "light" ? "bg-rose-500" : "bg-indigo-500"
              }`}
            />
            <motion.h1
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.7, delay: 0.3 }}
              className={`text-7xl md:text-9xl font-black tracking-tight`}
            >
              <span className={`${theme === "light" ? "text-gray-900" : "text-white"}`}>
                {currentLanguage.home.name}
              </span>
            </motion.h1>
          </div>

          {/* Typewriter Text */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.8 }}
            className="mb-8 relative"
          >
            <div className={`inline-block relative mb-2 py-4 px-3 border-2 ${
              theme === "light" 
                ? "border-gray-800 bg-white" 
                : "border-gray-100 bg-gray-900"
            }`}>
              <h2 className={`text-xl md:text-3xl font-mono ${
                theme === "light" ? "text-gray-800" : "text-gray-100"
              }`}>
                {currentLanguage.home.staticPhrase} {" "}
                <span className={`${
                  theme === "light" ? "text-rose-500" : "text-indigo-400"
                } font-bold`}>
                  <Typewriter
                    words={currentLanguage.home.typewriter}
                    loop={true}
                    cursor
                    cursorStyle="|"
                    typeSpeed={70}
                    deleteSpeed={50}
                    delaySpeed={1500}
                  />
                </span>
              </h2>
            </div>
          </motion.div>

          {/* Introduction Text */}
          <motion.p
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 1.0 }}
            className={`text-lg md:text-xl mb-12 max-w-2xl leading-relaxed ${
              theme === "light" ? "text-gray-700" : "text-gray-300"
            }`}
          >
            {currentLanguage.home.intro}
          </motion.p>

          {/* Brutalist Button Group */}
